INSERT INTO `items_localized` VALUES ('43680', 'deDE', 'König Varian Wrynns Silbermünze', 'Ich wünschte, der Aufstand zuhause würde sich bald wieder legen. Würde nicht wollen, dass irgendjemand verletzt wird.');
INSERT INTO `items_localized` VALUES ('43681', 'deDE', 'König Terenas Menethils Silbermünze', 'Möge mein Sohn Arthas immer im besten Interesse seines Volkes handeln.');
INSERT INTO `items_localized` VALUES ('43682', 'deDE', 'König Anasterian Sonnenwanderers Silbermünze', 'Ich hoffe, der junge Kael\'thas ist sich bewusst, dass er eines Tages unser Volk führen muss.');
