INSERT INTO `items_localized` VALUES ('43697', 'deDE', 'Artefakt aus der Heimatwelt der Nathrezim', 'Was für eine seltsame Konstruktion. Es muss einer befremdlichsten Gegenstände sein, den Ihr je zu Gesicht bekommen habt.');
INSERT INTO `items_localized` VALUES ('43698', 'deDE', 'Riesige Kanalratte', 'Lehrt Euch, wie man dieses Haustier beschwört.');
INSERT INTO `items_localized` VALUES ('43699', 'deDE', 'Datenscheibe: Fluch des Fleisches', 'Auf diesem Ding befinden sich Informationen? Wie? Da steht gar nichts geschrieben!');
INSERT INTO `items_localized` VALUES ('43701', 'deDE', 'Kupfermünze eines Fußsoldaten', 'Wünschte, ich hätte die gleiche Aufgabe wie einer meiner Cousins: Nichts weiter tun, als unbekümmert Holz herumschleppen...');
INSERT INTO `items_localized` VALUES ('43702', 'deDE', 'Alonsus Faols Kupfermünze', 'Hoffe, dass ich eines Tages den Weg des Lichts unter dem einfachen Volke verbreiten und ihnen den Wert der Drei Tugenden näher bringen kann.');
INSERT INTO `items_localized` VALUES ('43703', 'deDE', 'Ansirems Kupfermünze', 'Für meine Tochter wünsche ich mir Glück und Anerkennung. Sie fühlt sich hier so fehl am Platz...');
INSERT INTO `items_localized` VALUES ('43704', 'deDE', 'Attumens Kupfermünze', 'Kann ich einen bequemeren Sattel haben? Mein Hintern tut weh.');
INSERT INTO `items_localized` VALUES ('43705', 'deDE', 'Danaths Kupfermünze', 'Hoffe, ein Troll stirbt genau... JETZT.');
INSERT INTO `items_localized` VALUES ('43706', 'deDE', 'Dornaas glänzende Kupfermünze', 'Wünschte, dieser Trottel Arthas würde in einen Haufen Magnataurenmist fallen!');
INSERT INTO `items_localized` VALUES ('43707', 'deDE', 'Etriggs Kupfermünze', 'Möge Tirion auf seiner Suche hier in Nordend Erfolg haben oder einen ehrenhaften Tod im Kampf finden.');
INSERT INTO `items_localized` VALUES ('43708', 'deDE', 'Elling Trias\' Kupfermünze', 'Ein Schurke zu sein ist harte Arbeit. Hoffe, dass ich eines Tages meiner einzigen wahren Leidenschaft nachgehen kann...');
INSERT INTO `items_localized` VALUES ('43709', 'deDE', 'Falstad Wildhammers Kupfermünze', 'Hoffe, dass sich die Dinge irgendwann einmal ändern werden.');
INSERT INTO `items_localized` VALUES ('43710', 'deDE', 'Genns Kupfermünze', 'Pfff, die Brunnen in Gilneas könnten zehn Mal so viele Wünsche erfüllen wie die in Dalaran!');
INSERT INTO `items_localized` VALUES ('43711', 'deDE', 'Inigos Kupfermünze', 'Ich wünschte, mein Vater wär\' wieder da...');
INSERT INTO `items_localized` VALUES ('43712', 'deDE', 'Krasus\' Kupfermünze', 'Meine Gebete gehen an meine Brüder und Schwestern, die noch immer im Herzen von Grim Batol Qualen erleiden müssen. Ich WERDE sie befreien.');
INSERT INTO `items_localized` VALUES ('43713', 'deDE', 'Krylls Kupfermünze', 'Bumm. Bumm? Ich wünsche mir einen Bumm. Na los, mach bumm! Die Münze muss kaputt sein. Frage mich, ob sie explodieren wird.');
INSERT INTO `items_localized` VALUES ('43714', 'deDE', 'Landro Fernblicks Kupfermünze', 'Ich wünschte, ich hätte die Münze behalten...');
INSERT INTO `items_localized` VALUES ('43715', 'deDE', 'Moloks Kupfermünze', 'Nur einmal - mehr will ich gar nicht - nur einmal möchte ich mit dem Mädchen reiten...');
INSERT INTO `items_localized` VALUES ('43716', 'deDE', 'Murkys Kupfermünze', 'Ich wünschte, das Wasser in Nordend wäre nicht so kalt - es macht mich müde.');
INSERT INTO `items_localized` VALUES ('43717', 'deDE', 'Prinzessin Calia Menethils Kupfermünze', 'Ich hoffe, dass dieser gutaussehende Edelmann Lord Prestor mich bemerkt! Was für ein Traummann!');
