INSERT INTO `npc_monstersay` VALUES ('16464', '0', '50', '0', '12', 'Apothecary Venustus', 'Drat!  This bloodthistle extract doesn\'t seem to want to combine properly with my poison.  I\'ll not be deterred - those trolls will be dealt with!  Perhaps a different mixture?', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16468', '5', '100', '0', '12', 'Spectral Patron', 'I\'ll miss the show!', 'Yes... I think I remember now...', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16470', '5', '100', '0', '12', 'Ghostly Philanthropist', 'It\'s all... coming back to me.', 'Is it really over?', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16481', '5', '100', '0', '12', 'Ghastly Haunt', 'Free... at last.', 'Time to move on...', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16482', '5', '100', '0', '12', 'Trapped Soul', 'Free... at last.', 'Time to move on...', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16483', '0', '50', '35', '12', 'Draenei Survivor', '$C, you saved me!  I owe you a debt that I can never repay.  I\'ll go see if I can help the others.', 'The last thing I remember is the ship falling and us getting into the pods.  I\'ll go see how I can help.  Thank you!', 'Where am I?  Who are you?  Oh no!  What happened to the ship?', 'Ugh... what is this place?  Is that all that\'s left of the ship over there?', null);
INSERT INTO `npc_monstersay` VALUES ('16485', '5', '100', '0', '12', 'Arcane Watchman', 'Others will take my place!', 'This... changes nothing. Eternal damnation awaits you!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16500', '0', '50', '7', '12', 'Valaatu', 'No...', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16501', '0', '50', '7', '12', 'Aurelon', 'As you wish.', 'As the Light wills.', 'Of course, Zalduun.', null, null);
INSERT INTO `npc_monstersay` VALUES ('16502', '0', '50', '7', '12', 'Zalduun', 'I could use some help over here if you please.', 'Paladin Aurelon, if you have the mana to spare, could you help with that one, please?  Thank you.', 'Oh no!  I\'m losing this one!', 'Paladin, perhaps you would lend a hand?  I just need another heal on that one.  Thank you.', null);
INSERT INTO `npc_monstersay` VALUES ('16514', '0', '50', '0', '12', 'Botanist Taerix', 'Tedon, go ahead and try the neutralizing agent on the mutated creature.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16521', '0', '15', '0', '12', 'Blood Elf Scout', 'What a pity you survived the crash just to die here.', 'We won\'t allow you to leave this valley!', 'I hope you\'ve made your peace.', null, null);
INSERT INTO `npc_monstersay` VALUES ('16522', '0', '100', '0', '12', 'Surveyor Candress', 'You\'ll not take me alive, scum!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16544', '5', '100', '0', '12', 'Ethereal Thief', 'You will succeed only in damning yourselves!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16550', '0', '50', '7', '12', 'Image of Technician Dyvuun', 'By the seven Ata\'mal crystals!  Others have survived the crash!  Zhanaa, is that you?  It is so good to see you again!', 'And who do we have here?  No doubt th... crzzzk ...ade this communication possible?  I give you greetings fro... psshzzzk... Azure Watch.', 'Judging by your signal, we put you... ust southeast of the larger part of the island we crashed onto.  Cross the Crystalbrook River to ge... kshhhhk zzzt. Sorry, there\'s a lot of stat...', null, null);
INSERT INTO `npc_monstersay` VALUES ('16551', '0', '50', '7', '12', 'Technician Dyvuun', 'Yes, Zhanaa, it\'s me!  Your signal is weak.', 'Zhanaa!  Gah, I have to get this thing fixed!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16576', '0', '50', '1', '12', 'Overlord Hun Maimfist', 'For the Warchief! For the Horde!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16577', '0', '50', '1', '12', 'Martik Tor\'seldori', 'Brothers and sisters, I have been to the promised land. I have tasted in the sublime energy. I have felt bliss - bliss so engrossing and all encompassing that I was left wondering if I had stumbled upon the dreams of gods.', 'In paradise, you merely reach out and take from the Nether.', 'From the very air! Tendrils of arcane energy light up the obsidian sky as plumes of raw magic rise up from fissures in the land. Kneel and drink from the fissure as you do from a stream or well. Pluck a tendril from the heavens as if it were an apple hang', null, null);
INSERT INTO `npc_monstersay` VALUES ('16578', '0', '50', '1', '12', 'Blood Elf Pilgrim', 'Tell us more, Martik. What is it? What does it feel like?', 'But how?', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16580', '0', '50', '1', '12', 'Thrallmar Grunt', 'Try it, and I\'ll fire you AND your ten silver over that wall.', 'You cut the rope. You have the knife.', 'Harumph! Coward.', 'You know, I think we can hit Honor Hold from here.', 'I say we fire it. Cut the rope.');
INSERT INTO `npc_monstersay` VALUES ('16584', '0', '50', '1', '12', 'Watch Commander Krunk', 'Don\'t say I didn\'t warn ya when I thrust my fist into the back of yer head. It\'ll feel just like home, back in the Valley of Trials.', 'Yer gonna learn why they call this place Hellfire, maggot.', 'This ain\'t daycare, peon. Double time!', 'I\'ve seen gnomes work harder than you, peon! Little girl gnomes - with pig tails! MOVE IT, MOVE IT, MOVE IT!', null);
INSERT INTO `npc_monstersay` VALUES ('16585', '0', '50', '1', '12', 'Cookie One-Eye', 'And if I hear that you palookas complained to Nazgrel again, it\'ll be one of ya\'s that I serve up next time...', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16587', '0', '100', '1', '12', 'Barley', 'Arrrhhh...Guards!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16589', '0', '50', '1', '12', 'Guard Captain Cragtar', 'Big red bastards! Bigger than any orc I\'d ever seen. Muscles out to here, tusks out to here... ', 'So there we were - me and the battalion, doing a routine recon down the bone road. We\'d sent the scout up ahead to look around in case anything dangerous come up.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16590', '0', '50', '1', '12', 'Injured Thrallmar Grunt', 'It tastes like I died and went to the Nether; where slop that tastes like rotting threshadon carcass is then shoved down my throat by an obese tauren that\'s never washed their hands - or bathed...', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16593', '0', '50', '0', '12', 'Shattered Hand Brawler', 'This world is OURS!', 'The blood is our power!', 'Lok\'tar Illadari!', null, null);
INSERT INTO `npc_monstersay` VALUES ('16594', '0', '50', '0', '12', 'Shadowmoon Acolyte', 'Lok\'tar Illadari!', 'The blood is our power!', 'This world is OURS!', null, null);
INSERT INTO `npc_monstersay` VALUES ('16700', '0', '50', '0', '12', 'Shattered Hand Legionnaire', 'We will destroy you.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16769', '0', '50', '0', '12', 'Firewing Warlock', 'Kill them before they get away!', 'You won\'t escape alive!', 'For Kael\'thas!', 'They mustn\'t escape!', 'You know too much!');
INSERT INTO `npc_monstersay` VALUES ('16790', '0', '50', '0', '12', 'Falconer Drenna Riverwind', 'Thank you, $N.  It looks like they\'re going to get along just fine.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16793', '0', '50', '1', '12', 'Magistrix Carinda', 'You\'re such a good friend, Twinkle.  You\'d never leave me, would you?', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16794', '0', '50', '0', '12', 'Apothecary Azethen', '...a potent concoction!', 'Hmmm... looks like I\'m going to need a new test subject.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16795', '0', '50', '35', '12', 'Draenei Prisoner', 'I pity you, Forsaken.  Your soul is devoid of light.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16799', '0', '50', '0', '12', 'Ikan', 'Welcome, my brethren!  You\'ve been freed from demons.  Embrace the teachings of Velen!  Embrace the light!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16819', '0', '50', '7', '12', 'Force Commander Danath Trollbane', 'As I live and breathe! \"Little\" Arator? Is that you?', 'I am afraid that I do not, Arator. I have not seen or heard from your father in 15 years. We had long since presumed him dead. But know this: I did all that was in my power to try and find him. From the bounds of Hellfire to the ravages of Nagrand, we sea', 'That is precisely why I have called for you old friend. We have much to discuss.', 'Welcome to Honor Hold, $N.  It\'s good to have you.', null);
INSERT INTO `npc_monstersay` VALUES ('16821', '0', '50', '7', '12', 'Magus Filinthus', 'And have you heard? Have you heard the preposterous claims? Alliance and Horde supporting peace? Why the very thought of such things makes my blood boil!', 'Twenty years lost! Twenty! I won\'t stand for this! No, no, no... ', 'I take my leave now, Commander. Good day!', 'A mailbox? Well is that not the salt on the wounds! Twenty years cut-off from the world and now a mailbox! WONDERFUL!', 'Sid! Ale, NOW!');
INSERT INTO `npc_monstersay` VALUES ('16822', '0', '100', '7', '12', 'Flightmaster Krill Bitterhue', 'Guards!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16825', '0', '50', '7', '12', 'Father Malgor Devidicus', 'Shay, what\'sh your deal anwaysh? Are you shome kind of elf or shomethin? You looksh shorta familiar.', 'By the Light! So this is why you look familiar. Is this fate? Destiny perhaps? Arator, your father and I were the best of friends. It is I who kept him in good health... Oh the battles we fought... And...', 'And... the battle we lost. I... I\'m sorry, Arator. It was my fault...', 'Quickly, Sid. Fill my tankard. Make the memories fade...', null);
INSERT INTO `npc_monstersay` VALUES ('16826', '0', '50', '7', '12', 'Sid Limbardi', 'Oh brother... Listen, he\'s gonna be here crying like a baby for another five hours. If you\'re really the son of Turalyon, go speak with Trollbane. He\'s in the keep - top floor.', 'Just leave \'em in the cellar.', 'Comin\' right up, yer highness! Is there anythin\' else you\'ll be needin\'? Perhaps yer hat cleaned or yer nails trimmed?', 'Bless yer heart, soldier! Just put \'em in the back room.', null);
INSERT INTO `npc_monstersay` VALUES ('16830', '0', '50', '7', '12', 'Field Commander Romus', 'At ease, Private. I have a mission for you. The last messenger I sent to Nethergarde didn\'t make it. We found his entrails scattered about the bone road a few hours ago. We suspect it may have been Legion or fel orc, maybe even Horde...', 'I need this message delivered to Watch Commander Netherwane at Nethergarde Keep. There\'s a commendation in it for you if you make it... Your horse is waiting at the stables. Dismissed!', 'Soldier! Front and center!', null, null);
INSERT INTO `npc_monstersay` VALUES ('16831', '0', '50', '7', '12', 'Nethergarde Infantry', 'SIR! YES, SIR! Reporting for duty, sir!', 'Got another shipment for you, Sid.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16833', '0', '50', '0', '12', 'Makuru', 'No!  Not... Sedai!  The orcs must pay!', 'The orcs hate us, Obadei!  They\'ve killed many of us before!  They deserve death and worse.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16834', '0', '50', '0', '12', 'Anchorite Obadei', 'What have you done, Makuru?!  These are not our ways!', 'I understand how you feel Makuru.  Sedai was my brother after all.  Yet we can\'t disgrace his memory by going against his very ideals.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16842', '0', '50', '7', '12', 'Honor Hold Defender', 'Do ya want me to call a medic? Maybe a priest? It\'s been over an hour, mate!', 'What\'s goin\' on in there?? For the love of the LIGHT, hurry it up!', 'OY! You in there, this is official Honor Hold business yer holdin\' up!', 'So help me, I\'m gonna count to ten and if yer not outta there by the time I reach 10, I\'m comin\' in!', null);
INSERT INTO `npc_monstersay` VALUES ('16851', '0', '50', '0', '12', 'Mirren Longbeard', 'You treat my hat well now, $N.  You won\'t find a finer one anywhere in this world or another.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16864', '0', '50', '7', '12', 'Stormwind Infantry', 'Got another shipment for you, Sid.', 'Sir, yes sir! Private will do their best to not die, sir!', 'Where do you want these bottles?', 'Craziest thing - No matter how many bottles I take from the stockpile, the quantity on hand never decreases. It\'s like I\'m stuck in some endless loop of actions...', 'SIR! YES, SIR! Reporting for duty, sir!');
INSERT INTO `npc_monstersay` VALUES ('16873', '0', '15', '0', '12', 'Bleeding Hollow Dark Shaman', 'The Brotherhood will not tolerate your actions.', 'The Brotherhood will not tolerate your actions.', 'Feel the power of the Brotherhood!', null, null);
INSERT INTO `npc_monstersay` VALUES ('16886', '0', '50', '7', '12', 'Arator the Redeemer', 'Aye, Commander; but I am little no more. I have come to find my father. Have you any news as to his whereabouts? I know that he still lives. I have seen it in my dreams...', 'As long as he remains unfound, I will search. I know that he lives. And I know that I must find him. I am thankful for what you have done, Commander. As my father called upon you, a brother in arms, know that you may call upon me. I am at your service, my', 'Farewell, Commander. I will be staying at the inn, should you require my services.', 'I am Arator, son of Turalyon and Alleria. So yes, Father, I am \"shome\" kind of elf: half-elf to be precise.', 'Take care of him, Sid. He may be the only link I have to my father and mother. He needs to sober up...');
INSERT INTO `npc_monstersay` VALUES ('16907', '0', '50', '0', '12', 'Bleeding Hollow Peon', 'For Spinebreaker!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16916', '0', '50', '1', '12', 'Wretched Captive', 'Please!  I must feed on something soon... A mana crystal.... a shard... anything!  The pain is unbearable!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16923', '0', '50', '1', '12', 'Novice Ranger', 'I might just have to go hunting for that Wretched beast now that there\'s a bounty on his head.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16924', '0', '50', '1', '12', 'Sergeant Kan\'ren', 'You\'re not getting anything until you speak, scum!', 'Tell us!  Where does your leader hide?', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16978', '0', '50', '0', '12', 'Lieutenant Commander Thalvos', 'Everybody settle down! We\'re doing our best to get the situation under control. Them bleedin\' orcs are unrelenting!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('16993', '0', '50', '0', '12', 'Wounded Blood Elf Pilgrim', 'Thank you for agreeing to help. Now, let\'s get out of here, $N!', 'You won\'t keep me from getting to Falcon Watch!', 'Over there! They\'re following us!', 'Allow me a moment to rest. The journey taxes what little strength I have.', 'Do you hear something?');
INSERT INTO `npc_monstersay` VALUES ('17000', '0', '50', '0', '12', 'Aggonis', 'Fools!  You seek to abjure my father\'s presence?  Death awaits you, mortals!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('17008', '0', '50', '0', '12', 'Gul\'dan', 'Be silent! The shattering is soon to come!', 'Bear witness to the undeniable power of our dark master!', 'With his gift, I shall raze this land and reform it!', 'Watch! See the ground shatter before us! Watch as the energy flows! It will feed our armies...', 'We will never be without power! I have secured our future! Bask in my glory!');
INSERT INTO `npc_monstersay` VALUES ('17058', '0', '50', '0', '12', 'Illidari Taskmaster', 'You dare interfere with the Master\'s endeavors?  You will pay, foolish mortal!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('17071', '0', '50', '7', '12', 'Technician Zhanaa', 'This is great news!  Your efforts have indeed saved us, $N!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('17083', '0', '50', '0', '12', 'Fel Orc Convert', 'Lok\'tar Illadari!', 'The blood is our power!', 'This world is OURS!', null, null);
INSERT INTO `npc_monstersay` VALUES ('17093', '0', '50', '0', '12', 'Magistrix Elosai', 'I\'ve endured much hardship to complete my research and with your help, $N, I\'ve finally done it. Here\'s to a brighter future!', 'I feel... lightheaded... What\'s... happening to me?', 'No, this can\'t be! This tonic... it was supposed to reverse my addiction, not hasten my descent into the ranks of the Wretched!', 'All those years of research... and for nothing! Perhaps they were right, perhaps this is something we must learn to live with.', null);
INSERT INTO `npc_monstersay` VALUES ('17109', '0', '50', '0', '12', 'Cersei Dusksinger', 'Oh, would you please stop checking your nails and spanking yourself, for one moment?  Really, no one cares!', 'I need some fresh air, not that there\'s any to be had in this swamp!  I\'m going out for a bit.  Tidy up a bit while I\'m gone, would you?', 'Must you follow me everywhere? <sigh>', 'Are you hungry?  I\'m hungry.  I just can\'t stand the thought of cured ham steak from Thultash again.  If only we could get some of that succulent roasted quail!', null);
INSERT INTO `npc_monstersay` VALUES ('17117', '0', '50', '7', '12', 'Injured Night Elf Priestess', 'Whe... Where am I? What... What is this place?', 'BY THE LIGHT OF ELUNE! Eredar! Cursed demons, what have you done to me? I have not come alone, we will fight you as we have in the past! I...', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('17123', '0', '50', '1', '12', 'Earthcaller Ryga', 'Welcome, kind spirit.  What has brought you to us?', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('17127', '0', '50', '0', '12', 'Anchorite Avuun', 'I must find a way to put an end to the evil that Cersei Dusksinger is committing against our lost brothers and sisters!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('17206', '0', '50', '0', '12', 'Hauteur', 'You dare summon me?!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('17209', '0', '100', '7', '12', 'William Kielar', 'Guards!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('17214', '0', '50', '7', '12', 'Anchorite Fateema', 'Now to apply the ointment to her wounds.', 'It appears that the ointment is ineffective. Such a pity...', 'Man\'ari... But how... We are not eredar! She must know! If there are others like her, we must find them and explain...', null, null);
INSERT INTO `npc_monstersay` VALUES ('17215', '0', '50', '7', '12', 'Daedal', 'There may still be a solution. Come here, $N. I will explain what I need.', 'This should do it...', 'Wake up little creature. Wake up from your slumber... You are safe now.', null, null);
INSERT INTO `npc_monstersay` VALUES ('17220', '0', '50', '0', '12', 'Hauteur Reborn', 'Thank you, shaman.  I am reborn and will burn brighter than ever before!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('17222', '0', '50', '7', '12', 'Artificer Daelo', 'This won\'t do at all! We have to secure the foundation or the entire structure will crumble!', 'This structure violates at least 8 different ordinances!', 'I won\'t be held responsible for the catastrophe that will surely result from the haphazard maintenance of this building.\r\n', 'This won\'t work at all! The buildings will surely collapse if we do not shore up the support beams.', 'Hrm, this looks much better. Let\'s have a look on the inside.');
INSERT INTO `npc_monstersay` VALUES ('17226', '0', '50', '0', '12', 'Viera Sunwhisper', 'Ok, let\'s go.  I know just the perfect spot!', 'What... is happening... to me?  Get this cat away from me!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('17228', '0', '50', '7', '12', 'Draenei Artificer', 'How can I be expected to get any work done with that thing spreading its heresy?', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('17240', '0', '50', '7', '12', 'Admiral Odesyus', 'Wow! Really?', 'I hate you so much, Cookie.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('17243', '0', '50', '0', '12', 'Engineer \"Spark\" Overgrind', 'What\'s the big idea? You nearly blew my cover, idiot! I told you to put the compass and navigation maps somewhere safe - not out in the open for any fool to discover.', 'The Master has gone to great lengths to secure information about the whereabouts of the Exodar. You could have blown the entire operation, including the cover of our spy on the inside.', 'Relax? Do you know what Kael\'thas does to those that fail him, Geezle? Eternal suffering and pain... Do NOT screw this up, fool.', 'Our Bloodmyst scouts have located our contact. The fool, Velen, will soon leave himself open and defenseless -- long enough for us to strike! Now get out of my sight before I vaporize you...', 'It\'s ironic, I hate the ocean but I love the beach.');
INSERT INTO `npc_monstersay` VALUES ('17246', '0', '50', '7', '12', '\"Cookie\" McWeaksauce', 'No. Not really...', 'It be chicken...', 'Yarr, Admiral. Let me think about it... Arrr... It be comin\' into focus.', null, null);
INSERT INTO `npc_monstersay` VALUES ('17259', '0', '10', '1', '12', 'Bonechewer Hungerer', 'The blood is our power!', 'We are the true Horde!', 'This world is OURS!', null, null);
INSERT INTO `npc_monstersay` VALUES ('17264', '0', '20', '1', '12', 'Bonechewer Ravener', 'The blood is our power!', 'We are the true Horde!', 'This world is OURS!', null, null);
INSERT INTO `npc_monstersay` VALUES ('17268', '0', '50', '7', '12', 'Image of Technician Zhanaa', 'Dyvuun, is that you?', 'I can barely hear you... <static> aking up!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('17271', '0', '20', '1', '12', 'Bonechewer Destroyer', 'The blood is our power!', 'We are the true Horde!', 'For Kargath!  For Victory!', null, null);
INSERT INTO `npc_monstersay` VALUES ('17281', '0', '50', '0', '12', 'Bonechewer Ripper', 'We are the true Horde!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('17282', '0', '50', '1', '12', 'Falcon Watch Ranger', 'What, exactly, are we ever going to fire these at?', 'We\'re never going to fire one of these, are we?', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('17318', '0', '50', '0', '12', 'Geezle', 'What\'s the big idea, Spark? Why\'d you call for this meeting?', 'Relax, Spark! I have it all under control. We\'ll strip mine the Exodar right out from under \'em - making both you and I very, very rich in the process.', 'Yes, sir. It won\'t happen again...', null, null);
INSERT INTO `npc_monstersay` VALUES ('17359', '0', '50', '0', '12', 'Tel\'athion the Impure', 'What have you done to my life\'s work?!  DIE!!!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('17370', '0', '10', '0', '12', 'Laughing Skull Enforcer', 'Lok Narash!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('17371', '0', '50', '0', '12', 'Shadowmoon Warlock', 'For Kargath!  For Victory!', 'Lok narash!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('17375', '0', '50', '0', '12', 'Stillpine Captive', '[Furbolg] The Stillpine furbolgs will not soon forget your bravery!', '[Furbolg] The prophecy is true!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('17392', '0', '50', '0', '12', 'Stillpine Ancestor Tikti', '[Furbolg] Far you mixik tak come. Gaze upon the kitch\'kal river. Follow. Ilog to Yor.', '[Furbolg] Go... Search yitix\'kil bottom river. South!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('17393', '0', '50', '0', '12', 'Stillpine Ancestor Yor', '[Furbolg] Come, $N. Let us leave the water together, purified.', '[Furbolg] We go now, together. We will seek Vark.', '[Furbolg] Follow me to Vark.', '[Furbolg] Hurry, $N. Vark awaits you.', null);
INSERT INTO `npc_monstersay` VALUES ('17395', '0', '50', '0', '12', 'Shadowmoon Summoner', 'For Kargath!  For Victory!', 'Lok\'tar Illadari!\r\n', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('17397', '0', '10', '0', '12', 'Shadowmoon Adept', 'For Kargath! For Victory!', 'Lok\'tar Illidari!', 'This world is OURS!', null, null);
INSERT INTO `npc_monstersay` VALUES ('17424', '0', '50', '0', '12', 'Anchorite Paetheus', 'Now, let\'s see...', 'In the process of translating this, I believe I\'ve learned more about the drunken exploits of a certain \'Andrew Clementine\' than about the journal\'s author.', 'But, no matter. Mister Clemetine\'s... err, \'contribution\' notwithstanding, this seems to be the journal of a man named Nolkai. The entries toward the end describe a brutal battle.', 'Take a look at this passage...', null);
