INSERT INTO `npc_monstersay` VALUES ('18537', '0', '50', '0', '12', 'Adyen the Lightwarden', 'Excellent. We must focus our efforts on the cleansing of Nagrand!', 'Well, out with it, neophyte...', 'It is exactly as I expected, soldier. They know we watch them closely.', 'They mask their intentions with meaningless movements to confuse us.', 'In time, you may learn to see through their deceptions.');
INSERT INTO `npc_monstersay` VALUES ('18538', '0', '50', '0', '12', 'Ishanah', 'The Light wants its lost son back, Socrethar.', 'I offer myself in exchange.  You will have Ishanah, the High Priestess of the Aldor, as your prisoner if you release Kaylaan from your dark grasp.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18547', '0', '50', '0', '12', 'Scryer Arcanist', 'We\'re rather selective of who we befriend, $N.  You think you have what it takes?', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18548', '0', '50', '0', '12', 'Firewing Courier', 'This belongs to Firewing Point.  You\'ll not be putting your grubby hands on it!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18549', '0', '50', '0', '12', 'Aldor Vindicator', 'These are holy grounds!  You are not welcome here.', 'Take your time away from here to reflect upon your actions.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18554', '0', '50', '0', '12', 'Sharth Voldoun', 'You come into my house and threaten ME?  I think not!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18584', '0', '50', '8', '12', 'Sal\'salabim', 'Ka kalix!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18585', '0', '50', '0', '12', 'Raliq the Drunk', 'Raliq teach you lesson now!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18586', '0', '50', '0', '12', 'Coosh\'coosh', 'F\'woooroonn! Coosh\'coosh pay nothing! Die!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18588', '0', '100', '0', '12', 'Floon', 'I choose the third option: KILLING YOU!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18596', '0', '50', '0', '12', 'Arcanist Adyria', 'Are you joking?  I\'m a Scryer, I always watch what I say.  More enemies than allies in this city, it seems.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18597', '0', '50', '0', '12', 'Sha\'nir', 'A newcomer to Shattrath!  Make sure to drop by later.  We can always use a hand with the injured.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18598', '0', '50', '0', '12', 'Orc Prisoner', 'When can we eat again?', 'Don\'t feel... so good.', 'So-ngh-thirsty.', 'Hm, hungry.', null);
INSERT INTO `npc_monstersay` VALUES ('18622', '0', '50', '0', '12', 'Iz\'zard', 'Be mindful of what you say, this one\'s being shown around by Khadgar\'s pet.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18633', '0', '50', '0', '12', 'Cabal Acolyte', 'The end comes for you!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18635', '0', '50', '0', '12', 'Cabal Deathsworn', 'I shall be rewarded!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18638', '0', '50', '0', '12', 'Cabal Zealot', 'In Sargeras\' name!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18653', '0', '50', '0', '12', 'Seth', 'Coming, momma!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18655', '0', '50', '0', '12', 'Jay Lemieux', 'Tag! You\'re it!', 'I wanna play soldier!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18656', '0', '50', '0', '12', 'Julie Honeywell', 'Ew, boys are gross!', 'Can I play?', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18688', '0', '50', '0', '12', 'Ancient Orc Ancestor', 'What is happening to me?', 'The spirits call... I am consumed.', 'I remember everything...', 'What will become of us when it falls?', 'The pain is unbearable.');
INSERT INTO `npc_monstersay` VALUES ('18698', '0', '100', '0', '12', 'Ever-Core the Punisher', 'Intruder detected! Neutralizing threat immediately!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18718', '0', '50', '0', '12', 'Shadowy Hunter', 'You!  You don\'t belong here!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18723', '0', '50', '0', '12', 'Erozion', 'I believe I can explain everything to you two if you give me a moment of your time.', 'That spell should wipe their memories of us and what just happened. All they should remember now is what reality would be like without the attempted temporal interference. Well done. Thrall will journey on to find his destiny, and Taretha...', 'Her fate is regrettably unavoidable.', null, null);
INSERT INTO `npc_monstersay` VALUES ('18744', '0', '50', '1', '12', 'Aurosalia', 'Will you stop--', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18760', '0', '50', '0', '12', 'Isla Starmane', '<cough> <cough> Damsel in distress over here!', 'Hello?  Help?', 'Ok, let\'s get out of here!', 'You sure you\'re ready?  Take a moment.', 'Alright, let\'s do this!');
