INSERT INTO `npc_monstersay` VALUES ('18810', '0', '50', '7', '12', 'Otonambusi', 'If you don\'t eat your fish, you can\'t have any pudding!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18887', '0', '50', '0', '12', 'Taretha', 'Thrall, you escaped!', 'I\'m free! Thank you all!', 'Thrall, I\'ve never met these people before in my life.', null, null);
INSERT INTO `npc_monstersay` VALUES ('18896', '0', '50', '7', '12', 'Exodar Holographic Emitter', 'Function: Secret Police, Interrogator', 'Their skill in battle should never be underestimated.', 'Succubus', 'Species: Sayaad', 'Affiliation: Burning Legion');
INSERT INTO `npc_monstersay` VALUES ('18897', '0', '50', '0', '12', '\"King\" Dond', 'Welcome, welcome, to the recently appropriated and callously renamed Steamwheedle Stadium!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18909', '0', '100', '0', '12', 'Zabra\'jin Guard', 'I gonna make you into mojo!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18917', '0', '50', '7', '12', 'Chakaa', 'Stand, my children... that\'s enough for now.', 'Stop here a moment, my children, and close your eyes. Can you feel the air currents?', 'The spirits of the air can be found even here, underground. They dislike being surrounded, constricted by the earth... their precious freedom is limited here.', 'Close your eyes, my children, and listen to the water within these halls.', 'The water spirits are perhaps the most outspoken. They are generous and understanding. Indeed, if you are having trouble hearing the voices, it is with water that you should begin.');
INSERT INTO `npc_monstersay` VALUES ('18926', '0', '50', '0', '12', 'Sleyin', 'Something must be done about these Wretched...\r\n', 'Once they\'re dealt with we can dedicate more resources to the war effort.\r\n', 'Having trouble dealing with the Wretched? I\'ve got just what you need.\r\n', null, null);
INSERT INTO `npc_monstersay` VALUES ('18930', '0', '100', '1', '12', 'Vlagga Freyfeather', 'Arrrhhh...Guards!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18931', '0', '100', '7', '12', 'Amish Wildhammer', 'Guards!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18934', '0', '50', '0', '12', 'Durnholde Mage', 'I was just... following orders.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18937', '0', '100', '7', '12', 'Amerun Leafshade', 'Guards!', null, null, null, null);
