INSERT INTO `npc_monstersay` VALUES ('18952', '0', '20', '0', '12', 'Bonechewer Scavenger', 'Death to the interlopers!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18953', '0', '100', '1', '12', 'Unoke Tenderhoof', 'Arrrhhh...Guards!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18954', '0', '50', '0', '12', 'Sailor Melinan', 'Hic... What a calim... a culamu... uhh calamit... Err, what a disaster!\r\n\r\n', 'It\'s enough to drive a man to drink.\r\n\r\n', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18994', '0', '50', '0', '12', 'Infinite Executioner', 'The wizard will fall!', 'More will take my place.', 'Enforcers! Kill them all!', 'We will not be stopped!', 'Your efforts... are in vain.');
INSERT INTO `npc_monstersay` VALUES ('18995', '0', '50', '0', '12', 'Infinite Vanquisher', 'A minor victory...', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('18999', '0', '50', '7', '12', 'Allerian Defender', 'Hey, did you read the bounty on this wanted poster?', 'I don\'t know and I don\'t care.', 'The bonelashers of the wastes are no laughing matter.  I hear they carry diseases!', 'This wanted poster says that they\'re offering gold to these so-called adventurers to go kill things.  Isn\'t that what we do?  I don\'t think I\'m being paid enough!', 'This wanted poster has a bounty on bonelashers.  You think I should do that?');
INSERT INTO `npc_monstersay` VALUES ('19035', '0', '50', '0', '12', 'Wazat', 'If only I could get this blasted machine working!', 'They look so delicious...', 'Oh sweet, delicious, spotted eggs - you will be mine... ', null, null);
INSERT INTO `npc_monstersay` VALUES ('19138', '0', '50', '0', '12', 'Anchorite Attendant', 'May it be so.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19142', '0', '50', '0', '12', 'Aldor Anchorite', 'Light be with you, $N.  And welcome to Shattrath.', 'May it be so.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19149', '0', '50', '7', '12', 'Telaari Citizen', 'How does he do it?', 'He\'s so well disciplined!', 'I wanna be just like the pitfighter!', 'The pitfighter will be victorious! Just look at him!', null);
INSERT INTO `npc_monstersay` VALUES ('19153', '0', '50', '0', '12', 'Aldor Neophyte', 'May it be so.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19164', '0', '50', '0', '12', 'Refugee Child', 'Look, one of the eggs hatched!', 'I want to see the baby!', 'I wonder where the baby is?', null, null);
INSERT INTO `npc_monstersay` VALUES ('19181', '0', '100', '7', '12', 'Lemla Hopewing', 'Guards!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19193', '0', '50', '0', '12', 'High Exarch Commodus', 'On the contrary, my friend.', 'With the intelligence you\'ve provided on the enemy and their tactics we\'ve gained a significant edge in the field.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19202', '0', '50', '0', '12', 'Emissary Mordin', 'You give me too much credit, good sir. I regret we cannot be of more assistance in such perilous times.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19216', '0', '50', '0', '12', 'Grand Anchorite Almonen', 'Sadly, there are those who wander through mortality in defiance of the Light.', 'It has been made known to me that inside each of us, the Light resides...', 'Simple kindnesses, charitable deeds, service to those in need. These are all fruits of the Light.', 'It rewards those who heed its promptings with blessings, both seen and unseen.', 'From these the Light withdraws, until only darkness remains.');
INSERT INTO `npc_monstersay` VALUES ('19218', '0', '100', '0', '12', 'Gatewatcher Gyro-Kill', 'I predict a painful death.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19218', '5', '100', '0', '12', 'Gatewatcher Gyro-Kill', 'An unforeseen... contingency.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19219', '0', '100', '0', '12', 'Mechano-Lord Capacitus', 'You should split while you can.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19219', '5', '100', '0', '12', 'Mechano-Lord Capacitus', 'Bully!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19223', '0', '50', '0', '12', 'Granny Smith', 'I could have sworn I had more apples.  My eyes must be getting worse.', 'Huh? Did someone say something?', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19227', '0', '50', '0', '12', 'Griftah', 'Amazing amulets! Incredible curios! The newfangled jewelcrafters be havin\' nothin\' on the tried and true mystical methods of ol\' Griftah! Improve yerself through these magical talismans for a bargain price!', 'C\'mere, $r. Ya don\'t want to be riskin\' yerself in a dungeon, do ya? Filthy work, and for what? Some worthless trinket? Nah, what ya need be one of my amulets. Guaranteed to work!', 'Ya look lovesick. Special someone be gettin\' ya down, eh? No worries, no worries. Ya get this medallion from me, ya wear it when ya see \'em, and they be all over ya, $G man:sweetheart;!', 'Hey! Hey, $r! I be havin\' just the thing for ya. Ya ever see a tikbalang? Frightenin\' creatures. Prone to stealin\' ya away. What ya need is one of my tikbalang wards. Guaranteed to ward away all tikbalangs!', 'Dangerous out there, innit? Know what ya need? Protection. I can help ya defy death itself! Ya wear this madstone \'round yer neck, see, and then ya be able to come back if ya find yerself in the spirit world! Just a quick jaunt to yer corpse and up ya com');
INSERT INTO `npc_monstersay` VALUES ('19255', '0', '50', '1', '12', 'General Krakork', 'I\'m not gonna lie to you, soldiers. We\'re getting our butts kicked out there! Now which one of you is orc enough to do something about it?', 'How many more of you grunts do I need to send back to mommy and daddy in a body bag before you grow a brain and realize that runnin\' head first into a Legion kill squad is suicide? And don\'t nod your thick skulls at me as if you know what I\'m talkin\' abou', 'The warchief\'s put a price on Arazzius\'s head! As of right now, that no-good piece of demon refuse is enemy number one.', 'Yea, we got problems. I think that\'s crystal clear. The question is, what are we gonna do about it?', null);
INSERT INTO `npc_monstersay` VALUES ('19257', '0', '50', '1', '12', 'Arcanist Torseldori', 'INCOMING! Do not let them through! Focus fire! NOW, NOW, NOW!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19272', '0', '50', '0', '12', 'Harbinger Argomen', 'May it be as you say, Grand Anchorite.', 'We are graced to hear from our revered Grand Anchorite Almonen. Take a knee men.', 'It would be an honor, Grand Anchorite.', 'Listen up Men!', 'Thank you for speaking to the men, Almonen. They\'ll be better for it.');
INSERT INTO `npc_monstersay` VALUES ('19273', '0', '50', '0', '12', 'Forward Commander To\'arch', 'Speak with Wing Commander Brack, $N.  He\'ll outfit you with one of our armored wyvern destroyers.  And, good luck!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19283', '0', '50', '0', '12', 'Vagrant', 'Please don\'t ignore us...', 'Spare a copper, $G sir:madam;?', 'Any spare copper?', null, null);
INSERT INTO `npc_monstersay` VALUES ('19294', '0', '50', '0', '12', 'Earthbinder Galandria Nightbreeze', 'I have an idea.  Let\'s plant this shard in the ground and see what happens.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19308', '0', '50', '7', '12', 'Marshal Isildor', 'Do you think that demons will play fair? These beasts are more savage than orcs and more cunning than the undead. Do not attempt to engage Legion by normal standards of war.', 'This war - and don\'t mistake this for anything but a war - is not going to win itself. If we do not fight, scratch, claw, and kick for every speck of land on this shattered world, all will be lost.', 'I regret to inform you all that we sent home another ten soldiers today - in body bags.', 'How many more soldiers need to die before we start paying attention out there?', null);
INSERT INTO `npc_monstersay` VALUES ('19316', '0', '50', '7', '12', 'Field Marshal Rohamus', 'We\'re gonna melt this hunk of junk down and make weapons with the ingots! Let\'s see how the Legion likes a taste of fel reaver served up Alliance style!', 'Tear it down, soldiers! We\'re shippin\' this thing back to Stormwind!', 'Well done, soldiers! We weren\'t about to let the filthy animals of the Horde show us up!', 'Let them throw another one of these overgrown tin cans at us! We\'ll show them what the Alliance is made of!', null);
INSERT INTO `npc_monstersay` VALUES ('19317', '0', '100', '1', '12', 'Drek\'Gol', 'Arrrhhh...Guards!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19337', '0', '50', '0', '12', 'Aldor Marksman', 'Uhh, whoops...', '...at least we know my rifle is sighted properly.', 'May it be so.', null, null);
INSERT INTO `npc_monstersay` VALUES ('19346', '0', '50', '0', '12', 'Harbinger Erothem', 'May it be so.', 'Sir, it appears that another of our special units have arrived.', 'Understood, sir.', 'By your leave, sir, I have pressing matters to attend to.', 'Listen up, soldiers!');
INSERT INTO `npc_monstersay` VALUES ('19377', '0', '50', '0', '12', 'Neophyte Nemarn', 'Sir, I am here to deliver my report from the Scryer\'s Tier.', 'Sir, it\'s impossible to read anything into their actions.', 'Their movements are totally chaotic, as if they themselves know not what they are about.', 'My apologies, sir. I wish I had more to report.', 'As you command, sir.');
INSERT INTO `npc_monstersay` VALUES ('19378', '0', '50', '0', '12', 'Anchorite Nindumen', 'Into our struggle against the darkness we have sent our children.', 'Please bless and protect them, and grant them success over those who seek to do us harm...', 'Continue to bless this, our ancient home, we beg.', 'Bless us with tolerance for our elven visitors.', 'By the Naaru, may it be so.');
INSERT INTO `npc_monstersay` VALUES ('19380', '0', '50', '1', '12', 'Guard Untula', 'Hurry up with that axe, you! I want it sharp enough to cut the wind!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19383', '0', '50', '7', '12', 'Captured Gnome', 'Please don\'t beat me!', 'I don\'t understand you!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19394', '0', '50', '7', '12', 'Barimoke Wildbeard', 'Nae, that\'s fer wimps. Hit me with yer hammer!', 'DO IT, YE PANSY!', '...THAT WAS GREAT!', 'Bron! \'Ey, Bron! Hit me!', null);
INSERT INTO `npc_monstersay` VALUES ('19395', '0', '50', '7', '12', 'Bron Goldhammer', 'What, with me fists?', 'Er... \'Moke?', 'I\'m... not sure that\'s a good idea, \'Moke. Ye might get hurt...', null, null);
INSERT INTO `npc_monstersay` VALUES ('19417', '0', '50', '0', '12', 'Ramdor the Mad', 'No, spirit, I am afraid that they are all trapped inside - FOREVER! Now leave me be...', 'Why? WHY! Why was I \"gifted\" such a burden?', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19426', '0', '50', '0', '12', 'Peon Overseer', 'WORK FASTER!', 'I want those catapults built YESTERDAY!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19440', '0', '100', '0', '12', 'Eye of Grillok', 'You are spotted by the eye of Grillok!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19445', '0', '50', '0', '12', 'Wounded Soldier', 'Friends... I\'ve failed you...', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19446', '0', '50', '0', '12', 'Operations Officer', 'NOOOOOOOOOO!!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19466', '0', '50', '0', '12', 'Exarch Orelis', 'Hold your tongue, knave!  Do not dare use that tone with a priestess of the Light!', 'Even you must admit that your information from defectors is outdated and unreliable.  Let us make calculated attacks on these manaforges and gather what information we can.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19467', '0', '50', '0', '12', 'Anchorite Karja', 'The magistrix will be happy to know  I\'m  fluent in Thalassian.  Your courtesy is not lost on me.', 'Back to the topic...  we should focus our attention on these manaforges.  We need to learn what the enemy is doing with them and why.', 'With all due respect, A\'dal sent us here to investigate Kael\'s activities, not mount a full-scaled attack.', null, null);
INSERT INTO `npc_monstersay` VALUES ('19468', '0', '50', '0', '12', 'Spymaster Thalodien', 'Look, sweetheart.  No one\'s talking about storming Tempest Keep ourselves, but when we see a target of opportunity we have to take it.', '...and just look at where that got us!', 'Finally some talk of action from the big guy!', null, null);
INSERT INTO `npc_monstersay` VALUES ('19469', '0', '50', '10', '12', 'Magistrix Larynna', 'I\'m surrounded by idiots!', 'We\'ve told you all there is to know!  Do you forget that many of us used to be trusted followers of Kael\'thas?', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19475', '0', '50', '0', '12', 'Harbinger Haronem', 'Soup\'s on, men! Eat up. We\'ve got a long day ahead.', 'Fall in men! It\'s time to drill!', 'Alright now, let\'s get those rifles sighted in...', 'Soldier...', 'You DO know that marksmen are issued blanks for practice drills, right?');
INSERT INTO `npc_monstersay` VALUES ('19481', '0', '50', '0', '12', 'Archmage Vargoth', 'I can\'t thank you enough for your help, $N. At long last, I will be free of this tower and Kael\'s damnable magic!', 'No! How could the ritual fail?', 'The curse remains intact. We\'ll have to find another way.', null, null);
INSERT INTO `npc_monstersay` VALUES ('19501', '0', '50', '0', '12', 'Lower City Operative', 'Need some help!', 'Um, healer?!', 'Less watching, more healing!', 'Need a heal over here!', null);
INSERT INTO `npc_monstersay` VALUES ('19533', '0', '50', '0', '12', 'Dealer Aljaan', 'A tradesman, perhaps? You need look no further than I for supplies. My prices are quite fair, friend. These items were difficult to acquire, but what does the source matter, eh? You\'ll have what you need.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19534', '0', '50', '0', '12', 'Dealer Digriz', 'The creatures of this place are strong, wayfarer. You\'ll need ample ammunition if you hope to survive. You\'re in luck - I am well-stocked with fine arrows, bullets, and throwing weapons. The price is reasonable, considering circumstances.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19535', '0', '50', '0', '12', 'Dealer Zijaad', 'Ahh, another powerful traveler. The nether can be dangerous; you\'ll want the most potent spells at your disposal. I carry the finest reagents, common and exotic. Come, buy - you won\'t be sorry!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19541', '0', '50', '0', '12', 'Netherstorm Agent', 'Netherock?!  That thing\'ll squish you flat with one step of its massive foot!  No thanks!', 'Hey!  Come over here and take a look at this wanted poster.  You\'re not going to believe this!', 'I can\'t believe that Papa Wheeler thinks he\'s going to dupe anyone into going out to Manaforge B\'naar!', 'Maybe I should gather up some other agents and head out there after this shift?', 'Hmm, arcane annihilators are pretty tough.  I wonder if Papa Wheeler knows what he\'s asking for?');
INSERT INTO `npc_monstersay` VALUES ('19543', '0', '50', '0', '12', 'Battle-Mage Dathric', 'I don\'t know what I was thinking, going out without my sword. I would\'ve put it on if I\'d seen it here...', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19544', '0', '50', '0', '12', 'Conjurer Luminrath', 'I can\'t possibly go out without my cloak. I hope it\'s in here...', 'There it is! I could\'ve sworn it wasn\'t here last time I checked...', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19545', '0', '50', '0', '12', 'Cohlien Frostweaver', 'Phew! There\'s my lucky hat. I\'ve been looking for it everywhere.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19561', '0', '50', '1', '12', 'Hagash the Blind', 'I\'m blind, you stupid sow-spawn, not deaf. Don\'t even think about it.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19578', '0', '50', '0', '12', 'Bot-Specialist Alley', 'Alright, Mk. I, that should just about do it.  Let\'s just flip your switch here...', 'Oh no!  Will I ever get you working?!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19606', '0', '50', '0', '12', 'Grek', 'Grek try!', 'This oil no good for Grek! What Grek look like to you, some weakling in robes?', 'Someone come read this wanted poster to Grek.  Grek can\'t read!', 'Grek get drink!', null);
INSERT INTO `npc_monstersay` VALUES ('19610', '0', '50', '0', '12', 'Irradiated Worker', 'We\'ve taken some strange readings from nether ribbons.', 'My quad-corder has detect some anomolous readings from the nether anomolies.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19612', '0', '50', '0', '12', 'Irradiated Manager', 'We\'ll need to collect more data to substantiate these readings.  Get back in there and collect that data.', 'Well hand it over and let\'s see what you\'ve got!', 'Great Gazlowe!', 'We need to confrim this data, we don\'t want to rip apart any more of our facility.', 'Fascinating.');
INSERT INTO `npc_monstersay` VALUES ('19617', '0', '50', '0', '12', 'Boots', 'Woot!  Here we go!', 'Uh oh!', 'I\'m ok, I\'m ok!  Ah well, back to the drawing board on that rocket fuel!', 'Thanks...', null);
INSERT INTO `npc_monstersay` VALUES ('19621', '0', '50', '0', '12', 'Bill', 'Someone called?', 'A pleasure!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19623', '0', '50', '0', '12', 'Doc', 'I\'m a doctor!  What seems to be the problem?', 'Oh!  Someone needs to get those fires out so I can get in there and save that goblin!', 'Right... let me in there!', 'Everyone clear!', 'Come on!  Clear!');
INSERT INTO `npc_monstersay` VALUES ('19634', '0', '50', '0', '12', 'Lead Sapper Blastfizzle', 'Ack!  Everyone run... the last asset is materializing!!!', 'Crash, get out of there!!!  Oh no!!!  Not another of my proteges!!!', 'Uh oh.  The wagon looks unstable... it\'s going to blow!!!', 'Fun?!!  You nearly gave me a heart attack!  Yeah, I guess you\'re right.  That was fun!!!', null);
INSERT INTO `npc_monstersay` VALUES ('19645', '0', '50', '0', '12', 'Papa Wheeler', 'Chubis, after you\'re done there, make sure that you put another coat of wax on the Wheeler mobile.  I want to take mama out for a spin tonight.', 'I just knew that I shouldn\'t have fired Beebo!', 'Okay, if you can manage to get even one of those fixed, I\'ll let you take a ten minute break.  How\'s that sound?  Pretty good, huh?', 'When I was your age I could have had all of those shredders fixed before breakfast and still had time for all of my other chores.  In the Winterspring snow, no less.  And it\'s uphill both ways there!', 'Chubis Chubisimo!  Get those things fixed... we haven\'t got all day!');
INSERT INTO `npc_monstersay` VALUES ('19664', '0', '50', '0', '12', 'Muffin Man Moser', 'Nothing like a tankard full to wash down one of my magnificent muffins.', 'Yea, that hit the spot.  Muffins for sale!  Get your fresh muffins right here!', 'Hey you!  Get over here and check out my hot muffins!', 'What\'s up?  I hope you\'re having a fabulous day.  Come buy one of my muffins!', 'Get \'em hot and fresh while you can.  You won\'t find a better price on muffins anywhere else in Shattrath.');
INSERT INTO `npc_monstersay` VALUES ('19666', '0', '100', '0', '12', 'Shadow Lord Xiraxis', 'Never underestimate the other ethereal\'s greed.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19667', '0', '50', '0', '12', 'Consortium Nether Runner', 'Hail, Gezhe!  Seventeen more orders have been placed in the Hara\'samid Sector.  The markets of heavy veldarite have all but collapsed, as you predicted.', 'By your leave, sir.', 'Heraazi exports are on the rise, sir.  We\'ve already bribed the appropriate taxation officers and contraband will begin to arrive within days.', 'Word from Netherstorm, sir.  Stop all bloodgem imports; client has reneged payments.$B$BThe nexus-prince will buy out our stock at full price as compensation.', 'No word from the nexus-prince as of yet.  Rumors of a dispute with our clients in Netherstorm have begun to circulate.  Our Sodaraami backers speak of divesting.  We\'ve dispatched our top negotiators to dissuade them, sir.');
INSERT INTO `npc_monstersay` VALUES ('19671', '0', '50', '0', '12', 'Cryo-Engineer Sha\'heen', 'You made it! Well done, $r. Now if you\'ll excuse me, I have to get the rest of our crew inside.', 'Let\'s not waste any time! Take anything that isn\'t nailed down to the floor and teleport directly to Stormspire! Chop chop!', 'This should\'t take very long. Just watch my back as I empty these nether collectors.', 'Fantastic! Let\'s move on, shall we?', 'Looking at these energy levels, Shaffar was set to make a killing!');
INSERT INTO `npc_monstersay` VALUES ('19672', '0', '50', '0', '12', 'Consortium Laborer', 'I\'m giving her all that I\'ve got, captain. I don\'t think she can take much more of this!', 'It is if I\'m not the one that\'s going to be sent in to test it... Otherwise, no.', 'I can\'t bang on this device any faster, captain! My wrapping is already beginning to unravel!', 'Will you stop asking that?', 'Why don\'t you pick up a hammer and start banging on these structures instead of asking us if it\'s done every 5 minutes?');
INSERT INTO `npc_monstersay` VALUES ('19673', '0', '50', '0', '12', 'Consortium Engineer', 'I don\'t get paid enough for this nonsense.', 'I made it! I made it!', 'If I make it through, I expect a pay raise.', 'This is gonna leave a mark...', 'Here goes nothing!');
INSERT INTO `npc_monstersay` VALUES ('19674', '0', '50', '0', '12', 'Nexus-Prince Haramad', 'I\'m close to the edge, Morphalius. Another screw up and it\'ll be you running through that portal for Kaftiz\'s tests.', 'I don\'t pay you two exorbitant amounts of gold so that I can hear the words \"no\" or \"can\'t\" exit your wrappings. Bring that barrier down or you\'ll find yourselves working the energy reclamation line on X-17.', 'I don\'t give a damn what Shaffar\'s ethereals have placed over the entrance to the Mana-Tombs. You will work until those wards are removed and the Consortium can move in safely and take what\'s rightfully ours!', null, null);
INSERT INTO `npc_monstersay` VALUES ('19675', '0', '50', '0', '12', 'Nexus-Prince Haramad', 'That definitely ended our business relationship with Kael\'thas.  Please stay here.  I will return shortly and there is something that I want you to do.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19676', '0', '50', '0', '12', '\"Captain\" Kaftiz', 'I can\'t believe you imbeciles went to school for this! Does anybody here know what they\'re doing?', 'Alright, it looks safe. Get in there, engineer!', 'You know, we could easily replace you with goblins. Is that what you want???', null, null);
INSERT INTO `npc_monstersay` VALUES ('19684', '0', '50', '0', '12', 'Haggard War Veteran', 'Khadgar himself is showing you around?  You must have made a good impression, $N.', 'The market sure looks busy today.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19685', '0', '50', '0', '12', 'Khadgar\'s Servant', 'Follow me, stranger.  This won\'t take long.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19693', '0', '50', '0', '12', 'Clarissa', 'Give me a sign... anything...', 'Father, give me strength to go on.', 'Mommy is going to make sure you\'re ok, baby.', null, null);
INSERT INTO `npc_monstersay` VALUES ('19720', '0', '50', '0', '12', '\"Dirty\" Larry', 'Time to teach you a lesson in manners, little $g boy:girl;!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19732', '0', '50', '0', '12', 'Ango\'rosh Warlock', 'Me whant fight!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19737', '0', '50', '0', '12', 'Engineering Crewmember', 'The ship\'s not ready yet.  We still need to calibrate the fuse length to make sure that it doesn\'t burn out and leave you up there alone.', 'Essentially, it\'s going to be a long, long time till we are ready to launch.  Maybe you should just head back home to your family.', 'Why not? A family can give you strength.', 'Couldn\'t you raise them?', 'Well, it\'s like I said; it\'s going to be a while before we get this ship ready for flight.  We\'ll keep you informed.');
INSERT INTO `npc_monstersay` VALUES ('19763', '0', '50', '0', '12', 'Manni', 'Thank goodness you got here, it was almost dinner time!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19764', '0', '50', '0', '12', 'Moh', 'I thought I was a goner for sure.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19766', '0', '50', '0', '12', 'Jakk', 'I don\'t know which is worse, getting eaten by fel orcs or working for that slave master Razelcraz! Oh well, thanks anyways!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19776', '0', '50', '0', '12', 'Experimental Pilot', 'All my bags are packed, I\'m ready to go.', 'You need to do what? All this science, I don\'t understand... Look, this is just my job, five days a week.', 'I can\'t. I\'m not the man they think I am at home.  Besides, I didn\'t bring them out here.', 'This isn\'t the kind of place to raise your kids. It\'s cold, and there\'d be no one to raise them.', 'Oh no, no, no... I\'m a rocket man.');
INSERT INTO `npc_monstersay` VALUES ('19780', '0', '50', '0', '12', 'Off-Duty Engineer', 'Yikes!  Someone call the fire marshal!  Medic!!!  Is there a doctor in the house?', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19830', '0', '50', '0', '12', 'Arcanist Ardonis', 'You cannot be serious!  We are severely understaffed and can barely keep this manaforge functional!', 'Yes, my lord.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19831', '0', '50', '0', '12', 'Commander Dawnforge', 'We need you to send reinforcements to Manaforge Duro, Ardonis.  This is not a request, it\'s an order.', 'You will do as ordered.  Manaforge Duro has come under heavy attack by mana creatures and the situation is out of control.  Failure to comply will not be tolerated!', 'My lord!', 'Yes, my lord.', 'See to it, Ardonis!');
INSERT INTO `npc_monstersay` VALUES ('19832', '0', '50', '0', '12', 'Doctor Vomisa, Ph.T.', 'Oh no!  What\'s that?  Quickly, defend us with the Scrap Reaver X6000!!!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19847', '0', '50', '8', '12', 'Levixus', 'Justin Thavirat! Roman Kenney! Our entire awesome art and animation team!', 'Pat Nagle! World design team! Cory Stockton, Paul C., Jim Chadwick, Staats, Ed Hanes, Morris, Gotcher, Jesse!, Sarah B., Victor C., Dave A.! All of our amazing exterior artists!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19862', '0', '50', '0', '12', 'Urtrak', 'Urtrak kill you!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19882', '0', '50', '1', '12', 'Jero\'me', 'Bip!', 'What up son!  Wait....is anyone there?', 'Spare some change for a starving artist?', null, null);
INSERT INTO `npc_monstersay` VALUES ('19926', '0', '30', '0', '12', 'Spellreaver Marathelle', 'Looks like today was the wrong day to quit mana tapping...', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19932', '0', '50', '0', '12', 'Andormu', 'These beings aren\'t giving themselves the winning numbers for the Stormwind lottery, dear.', 'Something very, very disturbing. We\'re starting to see more of them across the timeways.', 'Nozari, I\'m not talking about a greedy wizard looking to improve his station in life by making a small adjustment to the past.', 'My apologies, Nozari. I have been a bit \'on edge\' as of late. These creatures are attempting to alter the timeways.', 'You do recall the last time that this occurred, yes? Let us hope that the master need not be involved.');
INSERT INTO `npc_monstersay` VALUES ('19933', '0', '50', '0', '12', 'Nozari', 'I hate it when you speak to me in that manner. I am your equal in every way, brother. Remember that the next time you are trapped amidst a temporal vortex, crying for help.', 'Fascinating. What is it, Andormu?', 'Hrm, are they tampering with timeways?', 'Shall we?', null);
INSERT INTO `npc_monstersay` VALUES ('19948', '0', '50', '0', '12', 'Bloodmaul Skirmisher', 'Raaar!!! Me smash $R!', 'Me mad. You get smash in face!', 'Stupid puny thing.  Me smash!', 'I\'ll crush you!', 'Me angered. Raaah!');
INSERT INTO `npc_monstersay` VALUES ('19950', '0', '50', '0', '12', 'Custodian of Time', 'This specimen is fading from existence. Something or someone is altering the timeway.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19991', '0', '50', '0', '12', 'Bloodmaul Brute', 'Raaar!!! Me smash $R!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19992', '0', '50', '0', '12', 'Bloodmaul Shaman', 'I\'ll crush you!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19993', '0', '50', '0', '12', 'Bloodmaul Mauler', 'Me mad. You get smash in face!', 'You no cast spells!', 'Me angered. Raaah!', 'Stupid puny thing.  Me smash!', null);
INSERT INTO `npc_monstersay` VALUES ('19995', '0', '50', '0', '12', 'Bladespire Brute', 'Raaar!!! Me smash $R!', 'I\'ll crush you!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19996', '0', '50', '0', '12', 'Bladespire Battlemage', 'I\'ll crush you!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('19998', '0', '50', '0', '12', 'Bladespire Shaman', 'I\'ll crush you!', 'Raaar!!! Me smash $R!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20071', '0', '50', '0', '12', 'Wind Trader Marid', 'Let us hold our discussion in a more... private place. Follow me, $N.', 'You didn\'t really think I\'d do business with you again, did you? Sometimes, the best way to return to profitability is to know when to cut your losses.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20084', '0', '50', '0', '12', 'Image of Nexus-Prince Haramad', 'No, I think not.  The selflessness that I have just been witness to has cleared my mind.  My dealings with your master are at an end.  You may return and let him know that I have decided to turn the ata\'mal crystal over to the naaru.', 'Guards!  Hold on, I will be right there!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20133', '0', '50', '0', '12', 'Image of Socrethar', 'What are these things that you have summoned to serve you?!  Just who do you think you are!', 'Culuthas, deliver the crystal to me now!', 'Return the crystal to me or face annihilation!', 'Culuthas, you will return the crystal to me at once!', 'Culuthas, when I find you, I am going to make certain that you suffer for all eternity!');
INSERT INTO `npc_monstersay` VALUES ('20159', '0', '50', '1', '12', 'Magister Aledis', 'If you think I\'ll betray my friend that easily, think again!  Face me, you scoundrel!', 'Spare my life!  I will tell you about Arelion\'s secret.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20163', '0', '50', '0', '12', 'Ambassador Solannas', 'Indeed, Nexus-Prince Haramad, you do have much to think on.  Such as turning over that ata\'mal crystal to my master, Prince Kael\'thas.', 'WHAT!  This is outrageous!  You will pay for this Haramad!  You and your assistant here!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20201', '0', '50', '0', '12', 'Sa\'at', 'Stop! Do not go any further, mortal. You are ill-prepared to face the forces of the Infinite Dragonflight. Come, let me help you.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20218', '0', '50', '0', '12', 'Sunfury Technician', 'The naaru\'s emergency shutdown will disable the manaforge permanently!  It must be aborted!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20227', '0', '50', '0', '12', 'Apprentice Tedon', 'Looks like it\'s time to let you out of this cage.', 'Now, to put the neutralizing agent on it...', 'Success!', null, null);
INSERT INTO `npc_monstersay` VALUES ('20232', '0', '50', '0', '12', 'Wing Commander Gryphongar', 'Welcome to Shatter Point, $n!  I hope you survive the experience!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20234', '0', '100', '7', '12', 'Runetog Wildhammer', 'Guards!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20242', '0', '50', '0', '12', 'Karaaz', 'Pssst!  I have something that might interest you, $N.  It fell off a pack mule in Nagrand!', 'The latest blueprints, patterns and schematics are available right here.  These instructions are so simple to follow they practically craft themselves!', 'Of course none of my goods are stolen.  But if someone asks, you didn\'t get them from me!', 'The best selection of smuggled goods is available right here, $N.  Great for the whole family!  Avoid goblin taxation and naaru prohibition - the Consortium is here to fill your every shopping need!', null);
INSERT INTO `npc_monstersay` VALUES ('20244', '0', '50', '10', '12', 'Nova', 'Oooh! Look, a shiny one!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20271', '0', '50', '0', '12', 'Haelga Slatefist', 'Don\'t say much, do yeh?', 'I miss me home, sometimes. Or maybe I just miss fightin\' for it.', 'Odd city, ain\'t it?', 'I haven\'t seen me brothers in ages.', null);
INSERT INTO `npc_monstersay` VALUES ('20272', '0', '50', '0', '12', 'Lylandor', 'Mmm.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20281', '0', '50', '0', '12', 'Drijya', 'Very well.  Before we head down there, take a moment to prepare yourself.', 'Let\'s proceed at a brisk pace.', 'I\'m done with this pylon.  On to the next.', 'We\'ll start at that first energy pylon, straight ahead.  Remember, try to keep them off of me.', null);
INSERT INTO `npc_monstersay` VALUES ('20345', '0', '50', '0', '12', 'Commander Mograine', 'Keep your voices down. There are strangers about...', 'Brothers and sisters, I have called you here today to discuss the fate of Lordaeron.', 'I hear things... Things that should not be.', 'The dead rise... Undead, from the frozen northlands. Whole cities have gone missing. I...', 'We must stand at the ready. I have faced undead before. They are ruthless killing machines, devoid of any emotion or compassion.');
INSERT INTO `npc_monstersay` VALUES ('20349', '0', '50', '0', '12', 'Tirion Fordring', 'Aye, I\'ve battled them as well. We are ill-prepared as a kingdom to withstand such an assault.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20352', '0', '50', '0', '12', 'Arcanist Doan', 'What do you propose, Mograine?', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20365', '0', '50', '0', '12', 'Bartolo Ginsetti', 'You there! Yes, you, peasant $g boy:girl;. Come quickly, I must tell you something... I must tell you about my greatness, lest such things are lost in the void!', 'Ooooh, YAH! Gaze upon my rippling musculature. Bask in the glow of my physique.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20393', '0', '50', '0', '12', 'Foreman Sundown', 'We were duped.  This is definitely not the promised land!', 'Come.  Let\'s put down these crude instruments and walk away from all of this.  It isn\'t why we came to Outland.', 'They had no right to demote me to these menial tasks!', null, null);
INSERT INTO `npc_monstersay` VALUES ('20415', '0', '50', '0', '12', 'Bessy', 'Mooooo...', 'Moooooooooo!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20427', '0', '100', '0', '12', 'Veneratus the Many', 'You will be destroyed!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20444', '0', '50', '0', '12', 'Ango\'rosh Shadowmage', 'Raaar!!! Me smash $R!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20452', '0', '50', '0', '12', 'Ethereum Assassin', 'Pathetic. Is that the best you can do?', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20453', '0', '50', '0', '12', 'Ethereum Shocktrooper', 'Pathetic. Is that the best you can do?', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20456', '0', '50', '0', '12', 'Ethereum Researcher', 'Pathetic. Is that the best you can do?', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20458', '0', '50', '0', '12', 'Ethereum Archon', 'Pathetic. Is that the best you can do?', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20459', '0', '50', '0', '12', 'Ethereum Overlord', 'Pathetic. Is that the best you can do?', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20482', '0', '50', '0', '12', 'Image of Commander Ameer', 'Protectorate transmission complete.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20513', '0', '50', '7', '12', 'Honor Hold Defender', 'I feel the same. But there\'s still a job to do out here. It\'ll all be for naught if this evil finds it way back to Azeroth - back to our homes. We just have to hold on a while longer. Trust Danath - he\'s never let us down before.', 'I am a grumpy old man!', 'C\'mon - you sound like a grumpy old man.', 'Twenty years we\'ve been stranded in this hell-hole. It\'s all I can do not to run screaming through that blasted Portal. I want to see my loved ones again! I want them to know I\'m still alive.\r\n', 'It\'s not him I\'m worried about. It\'s these new rookies around here - and those creepy purple elves they brought with \'em. They ain\'t even proved themselves yet.\r\n');
INSERT INTO `npc_monstersay` VALUES ('20552', '0', '50', '0', '12', 'Agent Ya-six', 'Hey! Hey you! $R! Over here!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20603', '0', '50', '0', '12', 'Reflection of Ya-six', 'Look at that handsome grouping of chaotic energies!', 'Turn its back to me! I need a clean shot!', 'Not a bad shot. For a fleshling...', 'This is a waste of my time. Where\'s the action?', 'I hate flesh beasts...');
INSERT INTO `npc_monstersay` VALUES ('20677', '0', '50', '0', '12', 'Morod the Windstirrer', 'You\'ve freed me!  The winds speak to my people once again and grant us their strength.  I thank you, stranger.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20678', '0', '50', '0', '12', 'Akoru the Firecaller', 'I am free!  I will ask the spirit of fire to return to us so that my people may have the courage to fight their masters!  I thank you, $r.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20679', '0', '50', '0', '12', 'Aylaan the Waterwaker', 'I\'m free!  The spirit of water returns to my people.  It will bring us the wisdom we need to survive in this harsh land.  I am in your debt, $N.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20727', '0', '50', '0', '12', 'Captain Zovax', 'For Salhadaar! For the Nexus-King!', 'Your battle prowess is nothing compared to mine!', 'Pathetic. Is that the best you can do?', 'Run away while you still can!', 'Taste pure energy!');
INSERT INTO `npc_monstersay` VALUES ('20730', '0', '50', '0', '12', 'Glumdor', 'Stupid, squishy $r. That weapon mine now! Give!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20732', '0', '50', '0', '12', 'Gorr\'Dim', 'Raaar!!! Me smash $R!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20753', '0', '50', '0', '12', 'Dorgok', 'Raaar!!! Me smash $R!;', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20762', '0', '100', '1', '12', 'Gur\'zil', 'Arrrhhh...Guards!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20763', '0', '50', '0', '12', 'Captured Protectorate Vanguard', 'For the Protectorate! DIE ETHEREUM FILTH!', 'We\'re not too far from the Protectorate Watch Post, $N. This way!', 'Commander! This fleshling rescued me!', null, null);
INSERT INTO `npc_monstersay` VALUES ('20765', '0', '50', '0', '12', 'Bladespire Crusher', 'I\'ll crush you!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20780', '0', '50', '0', '12', 'Kaylaan', 'Do not die on me, vindicator!', 'I\'ve failed.  The Light has abandoned me.', 'Light!  Do not fail me!', 'It is hopeless... I\'ve done all I can.', 'Kael\'thas and the Legion... just what we needed!');
INSERT INTO `npc_monstersay` VALUES ('20794', '0', '50', '0', '12', 'Kaylaan the Lost', 'Yes... master.', 'Teacher...', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20802', '0', '50', '0', '12', 'Protectorate Demolitionist', 'Let\'s do this... Just keep me covered and I\'ll deliver the package.', 'With this much void waste and run-off, a toxic void horror can\'t be too far behind.', 'By the second sun of K\'aresh, look at this place! I can only imagine what Salhadaar is planning. Come on, let\'s keep going.', 'Look there, fleshling! Salhadaar\'s conduits! He\'s keeping well fed....', 'Alright, keep me protected while I plant this disruptor. This shouldn\'t take very long.');
INSERT INTO `npc_monstersay` VALUES ('20812', '0', '50', '0', '12', 'Corki', 'Congratulations. It smells worse in here than in the ruins.', 'This is the last time I get caught! I promise! Bye!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('20825', '0', '50', '0', '12', 'Captain Tyralius', 'You\'ve saved me, fleshling! Ameer will hear of this noble act.', null, null, null, null);
