INSERT INTO `npc_monstersay` VALUES ('21140', '0', '50', '0', '12', 'Rift Lord', 'The rift must be protected!', 'The end is at hand!', 'One step closer...', null, null);
INSERT INTO `npc_monstersay` VALUES ('21188', '0', '50', '1', '12', 'Thadok', 'Excellent news, Tooki! We\'re saved!', 'Listen up, you lot! Our worries our over! FREE BOOZE FOR EVERYONE!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('21193', '0', '50', '1', '12', 'Gralga', 'Learning to read is a thirsty business. A toast, eh?', null, null, null, null);
