INSERT INTO `npc_monstersay` VALUES ('21257', '0', '50', '0', '12', 'Apothecary Zelana', 'Thank you, $N.  Now, to perform the test...', 'Ah, I see...', 'Yes, this is unfortunate.', '$N, I have confirmed that this blood is from the Bonechewer clan of orcs, tainted with demonic power.  I\'m afraid, however, that my current equipment can delve no deeper into this mystery...', null);
INSERT INTO `npc_monstersay` VALUES ('21259', '0', '50', '0', '12', 'Crash Bigbomb', 'I\'m ok!  It\'s just a flesh wound...  That was fun!!!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('21279', '0', '50', '0', '12', 'Apothecary Albreck', 'Please excuse me while I begin my tests...', 'Interesting...', 'But what is this?', 'Oh, my!', 'If my blood hadn\'t clotted long ago, it would be boiling with rage right now...');
INSERT INTO `npc_monstersay` VALUES ('21284', '0', '50', '0', '12', 'Auchenai Initiate', 'You have chosen death.', 'You will pay for this violation.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('21285', '0', '50', '0', '12', 'Auchenai Doomsayer', 'You have chosen death.', 'You will pay for this violation.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('21291', '0', '50', '0', '12', 'Grom\'tor, Son of Oronok', 'I\'m through asking nicely, fish-man.', 'DO I HONESTLY LOOK THAT STUPID TO YOU, NAGA? NOW YOU FEEL PAIN!', 'GET UP YOU ROTTEN PIECE OF TRASH! GET UP BEFORE I END YOU!', 'I\'m listening...', 'Where is the Cipher of Damnation? I know a third of it is in this village.');
INSERT INTO `npc_monstersay` VALUES ('21295', '0', '50', '0', '12', 'Coilskar Commander', 'Do not beat the Coilsssskaarrr commander anymore, orc! I will tell you! The cipherrr... It is hidden in one of the chests at Coilssskarrr Point... The naga there hold the keysss...', 'COILSSSKARRR!', 'Whhaaat... are you sssspeaking of, ssssoft-sskin?', 'The naga do nottt... have thissss... cipherrr...', 'Yesss... Yesss... Ssset me freee...');
INSERT INTO `npc_monstersay` VALUES ('21315', '0', '50', '0', '12', 'Ruul the Darkener', 'Your world is at an end.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('21318', '0', '50', '0', '12', 'Spirit of Ar\'tor', 'I... I have died... I was in so much pain... engulfed in darkness... Can you see me, $r?', null, null, null, null);
