INSERT INTO `npc_monstersay` VALUES ('21409', '0', '50', '0', '12', 'Envoy Icarius', 'Their loss is most definitely my gain...', 'Ah, damn it all! Clever trap. Too bad you\'ll never live to tell the tale...', 'Zarath, I am perfectly capable of making it up this road to Eclipse Point. If we do not deliver the missive, Lord Illidan will have both of our heads! You are dismissed!', 'Ah, sweet, sweet bloodthistle... Probably left behind by one of those filthy addicts at Eclipse Point.', null);
INSERT INTO `npc_monstersay` VALUES ('21410', '0', '50', '0', '12', 'Blood Lord Zarath', 'What is it, my lord?', 'My lord, surely you do not expect me to leave you unattended. Lord Illidan would have my head if anything were to happen to you.', 'As you wish, my lord.', null, null);
INSERT INTO `npc_monstersay` VALUES ('21411', '0', '50', '0', '12', 'Tobias the Filth Gorger', 'That was life-changing... Excuse me for one minute.', 'Ok then, back to business.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('21434', '0', '50', '0', '12', 'Gordie', 'It\'s a complete and total barf-o-rama! RUN FOR YOUR LIVES!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('21453', '0', '50', '0', '12', 'Ashtongue Shaman', 'Let me know right away if you find something important.', 'Dig! Dig for the glory of the master!', 'Keep up the good work.  Do well and you will be rewarded by the master.', null, null);
INSERT INTO `npc_monstersay` VALUES ('21456', '0', '15', '0', '12', 'Ogre Building Bunny Small', 'i kill you $N', 'I\'ll crush you!', 'I will trample you $N', 'ah you is pitiful cockroach!', null);
INSERT INTO `npc_monstersay` VALUES ('21469', '0', '50', '0', '12', 'Daranelle', 'Good, $n, you are under the spell\'s influence. I must analyze it quickly, then we can talk.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('21496', '0', '50', '0', '12', 'Dertrok', 'Cansis, hold still. I\'m going to try to use this crystal.', 'Interesting... Let me try something.', 'Cansis. You are a chicken.', 'Fascinating.', null);
INSERT INTO `npc_monstersay` VALUES ('21500', '0', '50', '0', '12', 'Morgroron', 'We will conquer all, for we are the Burning Legion!  Our enemies shall be consumed in flame and crushed beneath our might!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('21501', '0', '50', '0', '12', 'Makazradon', 'None can stand against the Burning Legion!  This world is ours!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('21502', '0', '50', '0', '12', 'Image of Warbringer Razuun', 'Doom Lord Kazzak will be pleased. You are to increase the pace of your attacks. Destroy the orcish and dwarven strongholds with all haste.', 'Don\'t worry about that. I\'ve increased production at the Deathforge. You\'ll have all the infernals you need to carry out your orders. Don\'t fail, Jovaan.', 'Yes?', 'Crate? I didn\'t send you a crate, Jovaan. Don\'t you have more important things to worry about? Go see to them!', null);
INSERT INTO `npc_monstersay` VALUES ('21504', '0', '50', '0', '12', 'Pathaleon the Calculator\'s Image', 'And just what do you think you are doing?  You dare to interfere with my master\'s experiment?', 'Do you like what we\'ve done here?  Perhaps we will drop these crystals from the sky all over Outland.', 'I grow bored with your attempt to heal the land and quell the energies summoning and driving the colossi mad.  Goliathon, King of the Colossi, Prince Kael\'thas and I demand that you defend the crystal!', 'I believe I may recognize them.  Deal with this quickly, Sharth.  Then take the mana bomb and destroy their town!', 'Indeed, it is not a request.');
INSERT INTO `npc_monstersay` VALUES ('21633', '0', '50', '0', '12', 'Deathbringer Jovaan', 'Everything is in readiness, warbringer.', 'Warbringer, that will require the use of all the hold\'s infernals. It may leave us vulernable to a counterattack.', 'It shall be as you say, warbringer. One last question, if I may...', 'What\'s in the crate?', null);
INSERT INTO `npc_monstersay` VALUES ('21648', '0', '50', '11', '12', 'Mature Netherwing Drake', 'Thank you, mortal.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('21660', '0', '50', '0', '12', 'Cabal Abjurist', 'I do as I must!', 'Ruin finds us all!', 'The end comes for you!', null, null);
INSERT INTO `npc_monstersay` VALUES ('21661', '0', '50', '0', '12', 'Cabal Skirmisher', 'Ruin finds us all!', 'I do as I must!', 'I shall be rewarded!', 'The end comes for you!', 'The Legion reigns!');
INSERT INTO `npc_monstersay` VALUES ('21662', '0', '50', '0', '12', 'Cabal Tomb-Raider', 'The Legion reigns!', 'I shall be rewarded!', 'The end comes for you!', 'Ruin finds us all!', null);
INSERT INTO `npc_monstersay` VALUES ('21690', '0', '100', '0', '12', 'R-3D0', 'WARNING!  WARNING!  AWAY HOSTILE BEINGS!  DO NOT HEAD UP THE RIDGE TO TOSHLEY\'S STATION!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('21699', '0', '50', '0', '12', 'Maiev Shadowsong', 'Release me immediately or face my wrath!', 'If we truly desire the same thing, Akama, then release me!  If Illidan is to die, it shall be by my hand!', 'Curse you, Akama!  I am not a pawn in your game... my will is my own.  When I unleash my wrath upon Illidan it will have nothing to do with your foolish scheme!', null, null);
INSERT INTO `npc_monstersay` VALUES ('21700', '0', '50', '0', '12', 'Akama', 'Forgive my harsh methods, but the Betrayer cannot learn of the truth.  My secret must be kept at all costs!', 'In due time, Maiev.  I\'ve spent years preparing to make my move - I can\'t afford to put my plans in peril by tipping my hand too soon.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('21717', '0', '50', '1', '12', 'Dragonmaw Wrangler', 'Your bones will break under my boot, $R!', 'For the Dragonmaw!', 'Your bones will break under my boot, $R!', null, null);
INSERT INTO `npc_monstersay` VALUES ('21718', '0', '50', '1', '12', 'Dragonmaw Subjugator', 'Your bones will break under my boot, $R!', 'For the Dragonmaw!', 'Long live the Dragonmaw! Die you worthless $R!', null, null);
