INSERT INTO `npc_monstersay` VALUES ('21742', '0', '50', '0', '12', 'Sunfury Eradicator', 'As you say, sir.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('21743', '0', '50', '0', '12', 'Sunfury Blood Lord', 'Guardians! To me!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('21768', '0', '50', '0', '12', 'Vagath', 'You\'d do well not to toy with me, Akama.  Illidan has given me specific orders to keep close a watch on the Warden.  If I find out you\'re hiding anything from me, I will crush you with my own hands!', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('21769', '0', '50', '1', '12', 'Overlord Or\'barokh', 'What is it that you want from me, shaman?', 'Do you not have eyes? Did you not see the demons on our very doorstep? I do not have the soldiers to spare!', 'Pray to your gods, shaman. Perhaps they will come to your aid. The Horde, however, cannot...', null, null);
INSERT INTO `npc_monstersay` VALUES ('21771', '0', '50', '0', '12', 'Warcaller Sardon Truslice', 'Wit a reward like dis, how could they refuse, mon?', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('21773', '0', '50', '7', '12', 'Thane Yoregar', 'I really wish I could help ya, but I can\'t! Kurdran\'s given me direct orders. Now move along!', 'Look, if I\'ve told ya once, I\'ve told ya a hundred times: WE CANNOT HELP YOU!', 'I don\'t get it! Are ya blind? Can ya not see the demons bangin\' on our door? Who am I gonna send out to talk to your shaman friends? Point \'em out!', null, null);
INSERT INTO `npc_monstersay` VALUES ('21789', '0', '50', '0', '12', 'Nakansi', 'I\'ve rigged a few control consoles down the slope. Get to one quickly and take control of the fel reaver! You\'ll only have a limited amount of time.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('21790', '0', '50', '0', '12', 'Plexi', 'I\'ve rigged a few control consoles down the slope. Get to one quickly and take control of the fel reaver! You\'ll only have a limited amount of time.', null, null, null, null);
