INSERT INTO `npc_monstersay` VALUES ('21822', '0', '50', '0', '12', 'Vindicator Aluumen', 'The Betrayer hasn\'t been the most predictable of foes, Exarch.  I have no fear however, with the Light on our side we shall defeat his forces today, tomorrow, or whenever he chooses to attack.', null, null, null, null);
