INSERT INTO `npc_monstersay` VALUES ('21983', '0', '50', '0', '12', 'Samia Inkling', 'Has he found the one, yet?', 'Yes, he\'s the second-in-command of the wyrmcult at the Blackwing Coven to the west.', 'What does the baron want?', 'Tell him that our Cenarion friends have been most helpful.', null);
INSERT INTO `npc_monstersay` VALUES ('21998', '0', '50', '1', '12', 'Kor\'kron Wind Rider', 'Speak quickly, $N. We haven\'t much time!', 'Enemies approach! Call for me when you are at a safe location.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('22007', '0', '50', '0', '12', 'Tree Warden Chawn', 'Samia, do you know who Kolphis Darkscale is?', 'Oh no!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('22059', '0', '50', '7', '12', 'Wildhammer Gryphon Rider', 'What is it, $r? Have you gathered some new information?', 'We\'re under attack! Defend yourself! I\'m taking the high road. Call me back when the coast is clear!', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('22062', '0', '50', '0', '12', 'Dr. Whitherlimb', 'It is very kind of you to donate your organs!', 'Be proud, your brain will be put to good use in one of my abominations.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('22073', '0', '50', '0', '12', 'Marcus Auralion', 'Return to Shattrath, hero. Seek out the wisdom of A\'dal.', null, null, null, null);
INSERT INTO `npc_monstersay` VALUES ('22103', '0', '50', '0', '12', 'Baron Sablemane', 'Very well, but make certain she knows that the blackwhelps are to be captured first.', 'There, it is completed.', null, null, null);
INSERT INTO `npc_monstersay` VALUES ('22112', '0', '50', '0', '12', 'Karynaku', 'Mordenaku... Your allies have saved us and destroyed the wretch, Zuluhed. Forever they will be a friend to the Netherwing.', 'In time, we will again require the assistance of the mortals. Across this world our birthright is shattered. It will be the mortal races that help us regain our rightful place on Draenor. Watch over them, Mordenaku...', 'Farewell, mortal. We are forever indebted to you.\r\n', null, null);
