INSERT INTO `npc_monstersay` VALUES ('22314', '0', '50', '0', '12', 'Captive Child', 'Yay!  We love you!  You saved us!', 'Woot!', 'I think those weird bird guys were going to eat us.  Gross!', 'Yay!  We\'re free!', 'Gross!');
