INSERT INTO `npc_text_localized` VALUES ('197', 'deDE', 'He, $GBürger:Bürgerin ! Ihr seht recht kräftig aus. Wir Wachen sind hier eher spärlich gesät. Daher könnten ich Eure Hilfe gut gebrauchen...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
