INSERT INTO `npc_text_localized` VALUES ('518', 'deDE', 'Viel zu tun, viel zu tun! Der Verband hat uns Fristen gesetzt, die wir besser nicht überschreiten sollten!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('519', 'deDE', 'Direktor Riddlevox hat mir die Aufsicht über das Entwicklungsprogramm für Schredder übertragen, das der Verband durchführt. Ich bin erst wenige Monate dabei und sitze schon in der Tinte! Ich könnte Eu', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('520', 'deDE', 'Seht Ihr, der neue Schredder der Venture Company ist besser als mein letzter, von den austauschbaren Greifern bis hin zu den Steuerungshebeln! Mein Boss hat mir einen ziemlich knappen Ablieferungsterm', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('521', 'deDE', 'Ihr habt Geistheuler getötet. Ihr seid ein toller Hecht!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('522', 'deDE', 'Kommt näher, mein Kind. Zu meiner Zeit konnte ich das flatternde Herz meiner Beute hören, aber heute müsst Ihr laut sprechen, damit Eure Worte gehört werden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('523', 'deDE', 'Viele, viele Jahreszeiten lang zog ich durch diese Ebenen. Und ich habe gewaltige Wildtiere gejagt. Und nie wurde ich übertroffen... bis ich Geistheuler sah.$B$BVor langer Zeit war der enorme Wolf ein', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('538', 'deDE', 'Ich grüße Euch, Magier. Darf ich Euch die Welt der Magie noch ein wenig näher bringen?', 'Ich grüße Euch, Magier. Darf ich Euch die Welt der Magie noch ein wenig näher bringen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('539', 'deDE', 'Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.', 'Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('558', 'deDE', 'Seid gegrüßt. Ich bin Magielehrer und Ihr seid Magier.', 'Seid gegrüßt. Ich bin Magielehrer und Ihr seid Magier.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('559', 'deDE', 'Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.', 'Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('560', 'deDE', 'Seid gegrüßt. Ich bin Gnomenmagielehrer und Ihr seid Magier.', 'Seid gegrüßt. Ich bin Gnomenmagielehrer und Ihr seid Magier.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('561', 'deDE', 'Seid gegrüßt, $C. Ihr erbittet einen Rat? Freundet Euch mit einem Magier an! Wir haben da einen Trick oder zwei auf Lager, ja das haben wir...', 'Seid gegrüßt, $C. Ihr erbittet einen Rat? Freundet Euch mit einem Magier an! Wir haben da einen Trick oder zwei auf Lager, ja das haben wir...', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('562', 'deDE', 'Seid gegrüßt. Ich bin Untoten-Magielehrer und Ihr seid Magier.', 'Seid gegrüßt. Ich bin Untoten-Magielehrer und Ihr seid Magier.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('563', 'deDE', 'Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.', 'Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('564', 'deDE', 'Seid gegrüßt, $C. Seid Ihr hier, um die verborgenen Künste zu erlernen?', 'Seid gegrüßt, $C. Seid Ihr hier, um die verborgenen Künste zu erlernen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('565', 'deDE', 'Seid gegrüßt, $C. Ich bemitleide Eure Art, da Euch die Geheimnisse der wahren Magie verwehrt sind...', 'Seid gegrüßt, $C. Ich bemitleide Eure Art, da Euch die Geheimnisse der wahren Magie verwehrt sind...', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('580', 'deDE', '', 'Für Euch ist die Zeit noch nicht reif. Ich werde Euch bei der Reise zurück in die Welt der Lebenden behilflich sein... gegen Bezahlung.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('581', 'deDE', 'Mit Zustimmung von Fürstin Sylvanas steigt die Anzahl der Todespirscher an. Mehr Klingen, die unsere Feinde treffen, wenn sie es am wenigsten erwarten.', 'Mit Zustimmung von Fürstin Sylvanas steigt die Anzahl der Todespirscher an. Mehr Klingen, die unsere Feinde treffen, wenn sie es am wenigsten erwarten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('623', 'deDE', 'Was wollt Ihr, $C? Ich bin spät dran und mein Arbeitgeber sieht Unpünktlichkeit nicht gern.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('624', 'deDE', 'Ja bin ich. Was gibts zu tun?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('638', 'deDE', 'Ungeachtet der Ängstlichkeit des Kriegshäuptlings wird der Klan der Zerschmetterten Hand der Horde zu ihrem früheren Ruhm verhelfen.', 'Ungeachtet der Ängstlichkeit des Kriegshäuptlings wird der Klan der Zerschmetterten Hand der Horde zu ihrem früheren Ruhm verhelfen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('678', 'deDE', 'Warum besucht ein junger $C wie Ihr einen alten Bauern wie mich?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('698', 'deDE', 'Naralex schläft wieder!$B$BVor langer Zeit reiste Naralex mit dem hehren Ziel zu dieser Höhle, in den Smaragdgrünen Traum einzutreten und aus dem Brachland wieder einen üppigen Wald werden zu lassen. ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('699', 'deDE', 'Die Giftzahnlords sind tot! Aber noch ist nicht alle Hoffnung verloren!$B$BDas Erweckungsritual ist jedoch sehr kompliziert. Es kann nicht allein vollzogen werden.$B$Blch werde mich um die notwendigen', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('718', 'deDE', 'Die Maschine richtet sich auf Euch aus.$B$BAuf ihrer Rückseite ist ein leerer, seltsam deformierter Schlitz mit einer Inschrift: \"Eigntuhm vom Käpt\'n Gelkak Gyromast\"', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('720', 'deDE', 'He! Seid Ihr auf einem Botengang für Tirth? An Eurer Stelle würde ich mich möglichst fern von ihm halten! Seine Gedanken sind zwar vom vielen Trinken benebelt, aber seine Magie ist so stark wie immer!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('738', 'deDE', 'Sicher werde ich Euch den Satz sagen. Solange ich nicht in Tirths Nähe muss!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('758', 'deDE', 'Gelkaks Schlüssel gleitet in den leeren Schlitz.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('759', 'deDE', 'Macht Ihr Euch etwa über mich lustig, während ich an dieses Eisen gefesselt bin und mich nicht verteidigen kann?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('760', 'deDE', 'Einer der Menschen hat den Schlüssel zu diesem Höllenapparat. Ich meine, jemand hätte gesagt, er hieße Eston.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('761', 'deDE', 'Verschwindet von hier, $C, oder sie werden auch Euch in Ketten legen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('762', 'deDE', 'Marlgen... Einer der Kerkermeister. Er hat den Schlüssel zu meinen Fesseln.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('764', 'deDE', 'Die Bank von Sturmwind befindet sich im Handelsdistrikt nordwestlich der Stadttore. Hier, ich zeige es Euch auf Eurer Karte.', 'Die Bank von Sturmwind befindet sich im Handelsdistrikt nordwestlich der Stadttore. Hier, ich zeige es Euch auf Eurer Karte.', 'Das Zählhaus Sturmwind findet Ihr in der Nähe der vorderen Tore im Handelsdistrikt von Sturmwind. Und wenn Ihr keine Lust mehr habt, Euer Geld zu zählen, dann schaut mal in der Güldenen Rose vorbei un', 'Das Zählhaus Sturmwind findet Ihr in der Nähe der vorderen Tore im Handelsdistrikt von Sturmwind. Und wenn Ihr keine Lust mehr habt, Euer Geld zu zählen, dann schaut mal in der Güldenen Rose vorbei un', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('766', 'deDE', 'Ich wünschte, der Schnee würde einfach wegschmelzen. Er macht meine Robe immer nass und hinterlässt nichts als Dreck!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('767', 'deDE', 'Blöder Schnee... He, Ihr seid doch $N, nicht wahr? Ich habe gerade eine Nachricht erhalten, in der nach Euch gefragt wurde.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('768', 'deDE', 'Ihr wurdet von Lago Finsterzang beschworen, dem Meister aller Hexenmeister von Eisenschmiede. Ihr tätet gut daran, Euch unverzüglich bei ihm zu melden. Ihr findet ihn in der Stadt im Distrikt des düst', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('769', 'deDE', 'Seid gegrüßt, $R. Mein Name ist Tharnariun Baumwahrer.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('778', 'deDE', 'Wenn Ihr mich fragt, sind die Goblins ein todsicherer Tipp. Wir haben das Werkzeug und das Talent!', 'Wenn Ihr mich fragt, sind die Goblins ein todsicherer Tipp. Wir haben das Werkzeug und das Talent!', 'Hätten die nicht dieses verfluchte Gnomenglück, würden sie niemals ein Rennen gewinnen!', 'Hätten die nicht dieses verfluchte Gnomenglück, würden sie niemals ein Rennen gewinnen!', 'An Eurer Stelle würde ich einen Goblinrennwettschein kaufen. Das andere Team anzufeuern ist reine Zeitverschwendung.', 'An Eurer Stelle würde ich einen Goblinrennwettschein kaufen. Das andere Team anzufeuern ist reine Zeitverschwendung.', 'Ich habe mein Geld auf die Goblins gesetzt! Und wenn Ihr einen Funken Verstand habt, tut Ihr es mir gleich!', 'Ich habe mein Geld auf die Goblins gesetzt! Und wenn Ihr einen Funken Verstand habt, tut Ihr es mir gleich!', 'Ich hörte, sie wollen den Wagen des Gnoms im nächsten Rennen hochgehen lassen! Das mag stimmen oder auch nicht, aber auf jeden Fall würde ich mich von Ihrer Box fern halten.', 'Ich hörte, sie wollen den Wagen des Gnoms im nächsten Rennen hochgehen lassen! Das mag stimmen oder auch nicht, aber auf jeden Fall würde ich mich von Ihrer Box fern halten.', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('779', 'deDE', 'Oh, Ihr meint Johnson? Ich hörte, er würde sich verstecken.', 'Oh, Ihr meint Johnson? Ich hörte, er würde sich verstecken.', 'Ha! Tirth sucht ihn schon seit Tagen, aber er will nicht gefunden werden. Er ist so ein Angsthase!', 'Ha! Tirth sucht ihn schon seit Tagen, aber er will nicht gefunden werden. Er ist so ein Angsthase!', 'Ihr sucht nach Eisenherz? Tja, das habt Ihr nicht von mir, aber... wenn Ihr Kronkreiter auf dem Turm fragt, sagt er es Euch vielleicht...', 'Ihr sucht nach Eisenherz? Tja, das habt Ihr nicht von mir, aber... wenn Ihr Kronkreiter auf dem Turm fragt, sagt er es Euch vielleicht...', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('780', 'deDE', 'Hätten die nicht dieses unglaubliche Goblinglück, würden die in jedem Rennen einen Unfall bauen!', 'Hätten die nicht dieses unglaubliche Goblinglück, würden die in jedem Rennen einen Unfall bauen!', 'In diesen Rennen können wir endlich mal zeigen, wie genial wir sind!', 'In diesen Rennen können wir endlich mal zeigen, wie genial wir sind!', 'Ich habe im Matrix-Regulometer unseres Wagens ein neues Gerät eingebaut. Damit sollte sich die Beschleunigung um satte fünfzehn Prozent erhöhen lassen!', 'Ich habe im Matrix-Regulometer unseres Wagens ein neues Gerät eingebaut. Damit sollte sich die Beschleunigung um satte fünfzehn Prozent erhöhen lassen!', 'Mein Sohn könnte einen schnelleren Wagen bauen als diesen klobigen Goblinschrotthaufen!', 'Mein Sohn könnte einen schnelleren Wagen bauen als diesen klobigen Goblinschrotthaufen!', 'Mein Gizmotik-Almanakotron sagt, dass wir gegen die Goblins noch mal 20 Sekunden Vorsprung rausholen können!', 'Mein Gizmotik-Almanakotron sagt, dass wir gegen die Goblins noch mal 20 Sekunden Vorsprung rausholen können!', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('798', 'deDE', 'Endlich! Endlich bin ich aus dieser Zelle befreit!$B$BDie Kirin Tor schickten mich von Dalaran aus, um Arugals Fortschritte mit den Untoten hier im Silberwald zu untersuchen. Wir wussten nicht, dass d', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('799', 'deDE', 'Endlich! Jemand, der mich aus dieser Zelle befreit!$B$BHochexekutor Hadrec schickte uns aus, Informationen über die Burg zu sammeln, sodass ein Plan entwickelt werden konnte, Arugal ein für alle Mal a', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('818', 'deDE', 'Einst folgte ich der Denkart der Schattenhammerkultisten. Ich war besessen davon, die Alten Götter wiederzubeleben, und so drang ich tief in diesen Unterschlupf vor, um mir die Macht der verfluchten B', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('820', 'deDE', 'Willkommen in meinem Gasthaus, $G müder Reisender:müde Reisende;. Was kann ich für Euch tun?', 'Willkommen in meinem Gasthaus, $G müder Reisender:müde Reisende;. Was kann ich für Euch tun?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('821', 'deDE', 'Ruht Eure müden Knochen für einen Zauber aus.', 'Ruht Eure müden Knochen für einen Zauber aus.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('822', 'deDE', 'Wie der Wind in den Ebenen seid Ihr hier immer willkommen.', 'Wie der Wind in den Ebenen seid Ihr hier immer willkommen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('823', 'deDE', 'Füllt Euren Krug und zieht einen Stuhl heran. Wir haben Geschichten zu erzählen und Humpen zu leeren.', 'Füllt Euren Krug und zieht einen Stuhl heran. Wir haben Geschichten zu erzählen und Humpen zu leeren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('824', 'deDE', 'Euer Körper sieht geschwächt und Euer Geist sieht müde aus. Ruht Euch unter unserem Dach aus und erlaubt es Elune, Eure Träume zu führen.', 'Euer Körper sieht geschwächt und Euer Geist sieht müde aus. Ruht Euch unter unserem Dach aus und erlaubt es Elune, Eure Träume zu führen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('825', 'deDE', 'Kommt herein, kommt nur herein. Setzt Euch nur nicht zu nahe an den Ofen. Er ist heute zwar noch nicht explodiert, aber nur um sicherzugehen solltet Ihr Euch woanders hinsetzen.', 'Kommt herein, kommt nur herein. Setzt Euch nur nicht zu nahe an den Ofen. Er ist heute zwar noch nicht explodiert, aber nur um sicherzugehen solltet Ihr Euch woanders hinsetzen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('838', 'deDE', '', 'Hallo, $R, es ist mir ein Vergnügen, Eure Bekanntschaft zu machen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('878', 'deDE', '', 'Tja, tja... schaut nur, wer da hereinschneit... und wie kann ich Euch helfen, $C?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('879', 'deDE', 'Ihr findet den Greifenmeister am nordöstlichen Wall, der das Tal der Helden überblickt.', 'Ihr findet den Greifenmeister am nordöstlichen Wall, der das Tal der Helden überblickt.', 'Ahhh, Ihr sucht nach Dungar Trinkfest. Er besitzt einige der schnellsten Greife diesseits von Khaz Modan! Ihr findet ihn auf den Wällen des Handelsdistrikts.', 'Ahhh, Ihr sucht nach Dungar Trinkfest. Er besitzt einige der schnellsten Greife diesseits von Khaz Modan! Ihr findet ihn auf den Wällen des Handelsdistrikts.', 'Ihr wollt einen Greifenritt unternehmen, was? Dungar hält seine Vögel oben auf dem Wall des Handelsdistrikts. Denkt aber immer daran, Euch festzuhalten!', 'Ihr wollt einen Greifenritt unternehmen, was? Dungar hält seine Vögel oben auf dem Wall des Handelsdistrikts. Denkt aber immer daran, Euch festzuhalten!', 'Greife, ja? Hab mich nie wirklich um die Biester gekümmert, aber jedem das seine.$B$BIhr findet Dungar Trinkfest auf dem Wall des Handelsdistrikts.', 'Greife, ja? Hab mich nie wirklich um die Biester gekümmert, aber jedem das seine.$B$BIhr findet Dungar Trinkfest auf dem Wall des Handelsdistrikts.', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('882', 'deDE', 'Ihr findet Aldwin Laughlin im Besucherzentrum des Handelsdistrikts von Sturmwind.', 'Ihr findet Aldwin Laughlin im Besucherzentrum des Handelsdistrikts von Sturmwind.', 'Wollt \'ne Gilde aufmachen, was? Dann viel Glück!$B$BSprecht mit Aldwin Laughlin im Besucherzentrum von Sturmwind. Wenn Ihr durch das Tal der Helden hineinkommt, befindet es sich gleich am Anfang des H', 'Wollt \'ne Gilde aufmachen, was? Dann viel Glück!$B$BSprecht mit Aldwin Laughlin im Besucherzentrum von Sturmwind. Wenn Ihr durch das Tal der Helden hineinkommt, befindet es sich gleich am Anfang des H', 'Ahhh, dann sucht Ihr also nach dem Besucherzentrum von Sturmwind. Es ist das erste Gebäude auf der linken Seite, wenn Ihr durch die vorderen Tore vom Tal der Helden aus kommt.', 'Ahhh, dann sucht Ihr also nach dem Besucherzentrum von Sturmwind. Es ist das erste Gebäude auf der linken Seite, wenn Ihr durch die vorderen Tore vom Tal der Helden aus kommt.', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('898', 'deDE', 'Einen Lehrer für welche Klasse sucht Ihr?', 'Einen Lehrer für welche Klasse sucht Ihr?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('899', 'deDE', 'Die Magier versammeln sich im Magiersanktum im Magierviertel. Dort gibt es einen recht eindrucksvollen Turm. Der lässt unsere Kasernen wie Arbeiterhütten aussehen.', 'Die Magier versammeln sich im Magiersanktum im Magierviertel. Dort gibt es einen recht eindrucksvollen Turm. Der lässt unsere Kasernen wie Arbeiterhütten aussehen.', 'Oh, Ihr sucht also nach arkaner Erleuchtung? Dann gibt es für Euch keinen besseren Ort als das Magiersanktum im Magierviertel.', 'Oh, Ihr sucht also nach arkaner Erleuchtung? Dann gibt es für Euch keinen besseren Ort als das Magiersanktum im Magierviertel.', 'Hört sich an, als wolltet Ihr mit Maginor Dumas sprechen! Ihr findet ihn normalerweise im Magiersanktum im Magierviertel. Natürlich weiß man nie, wann oder wohin sich diese Magier gerade teleportieren', 'Hört sich an, als wolltet Ihr mit Maginor Dumas sprechen! Ihr findet ihn normalerweise im Magiersanktum im Magierviertel. Natürlich weiß man nie, wann oder wohin sich diese Magier gerade teleportieren', '', '', '', '', '', '', '', '', '', '');
