INSERT INTO `npc_text_localized` VALUES ('1118', 'deDE', 'Schmied wollt Ihr also werden? Dann schnappt Euch mal Hammer und Schürze, $GBursche:Mädchen;, und lasst mich sehen, was Ihr draufhabt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
