INSERT INTO `npc_text_localized` VALUES ('1202', 'deDE', 'Ich hoffe, Ihr habt einen guten Grund für Eure Störung, ich war gerade mitten bei der Arbeit.', 'Ich hoffe, Ihr habt einen guten Grund für Eure Störung, ich war gerade mitten bei der Arbeit.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1203', 'deDE', 'Ein Kräuterkundiger, so, so. Ich bezweifle, dass Ihr Euch mit meinen Fertigkeiten messen könnt, aber vielleicht kann ich Euch ja das eine oder andere beibringen.', 'Ein Kräuterkundiger, so, so. Ich bezweifle, dass Ihr Euch mit meinen Fertigkeiten messen könnt, aber vielleicht kann ich Euch ja das eine oder andere beibringen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
