INSERT INTO `npc_text_localized` VALUES ('1451', 'deDE', 'Würdet Ihr glauben, dass ich versagt habe... in meiner dunkelsten Stunde und auf dem Höhepunkt meines Lebens?$B$BAls ich vor der Übermacht der gegnerischen Soldaten floh, konnte ich nur die Schreie me', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
