INSERT INTO `npc_text_localized` VALUES ('1597', 'deDE', 'Man trifft nicht viele Schmiede so weit von Eisenschmiede entfernt. Was kann ich für Euch tun?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1598', 'deDE', 'Ohne eine Decke aus stabilem Granit über mir fühle ich mich einfach nicht wohl. All diese Sterne da oben, da läuft\'s einem ja kalt den Rücken runter.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1605', 'deDE', 'Seid willkommen in meiner Schmiede. Lasst mich Euch ein Geheimnis anvertrauen. Ihr könnt niemandem vertrauen. Keinem Mann, keiner Frau, keiner Bestie. Vertrauen könnt Ihr nur hierauf: Stahl.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1606', 'deDE', 'Was führt Euch in meine Schmiede? Eine Ausbildung? Oder ist es das Geheimnis des Stahls? Die Rätsel der Schmiede? Sprecht!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1615', 'deDE', 'Ihr seht mir aus wie $Gein Mann:eine Frau; auf der Suche nach Wissen. Vielleicht sucht Ihr sogar die Lehre von Schmiede und Hammer. Vielleicht kann ich Euch auf diesem Weg geleiten. Aber erst müsst Ih', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1618', 'deDE', 'Wie ist die Schneide Eures Stahls? Scharf genug, Eurem Feind den Bauch aufzuschlitzen? Lasst mich Euch zeigen, wie man sie noch schärfer macht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1619', 'deDE', 'Ihr wollt Schmied werden? Ihr wollt Klingen schmieden, mit denen Ihr Euren Gegner aufschlitzen könnt? Und Hämmer, mit denen Ihr ihm den Schädel einschlagen könnt? Und ob ich Euch helfen kann. Nur zu g', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1620', 'deDE', 'Begebt Euch in die Orcstadt Orgrimmar. Dort findet Ihr ihn im Tal der Ehre in einem Laden namens \'Legendäre Waffen\'.', 'Begebt Euch in die Orcstadt Orgrimmar. Dort findet Ihr ihn im Tal der Ehre in einem Laden namens \'Legendäre Waffen\'.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1621', 'deDE', 'Das sind gute Klingen, die Ihr da macht, $N. Sie zerteilen Stein und sind danach doch noch scharf genug, eine Kehle aufzuschlitzen. Und doch habe ich schon bessere gesehen, von einem Orc namens Kelgru', 'Das sind gute Klingen, die Ihr da macht, $N. Sie zerteilen Stein und sind danach doch noch scharf genug, eine Kehle aufzuschlitzen. Und doch habe ich schon bessere gesehen, von einem Orc namens Kelgru', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1634', 'deDE', 'Guter Stahl, $N. Schnappt Euch einen Hammer und kommt her. Ich zeige Euch, wie man eine legendäre Waffe schmiedet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1635', 'deDE', 'Was ist? Seht Ihr nicht, dass ich beschäftigt bin?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1637', 'deDE', 'Ihr wollt noch mehr lernen, $N? Dann kommt her und zeigt mir, dass Ihr bereit dazu seid.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1640', 'deDE', '', 'Ich bilde keine Schmiede ohne Fertigkeit aus. Kommt wieder, wenn Ihr etwas vorzuweisen habt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1641', 'deDE', '', 'Ich sehe Potenzial in Euch, $N. Dann schnappt Euch mal einen Hammer und ein paar Stücke Alteisen, Ihr braucht jede Menge Übung.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
