INSERT INTO `npc_text_localized` VALUES ('1653', 'deDE', 'Eraka no Kimbul$BGott der Tiger, Lord der Bestien, König der Katzen, Verhängnis der Beutetiere$B$BUeetay no Mueh\'zala$BGott des Todes, Vater des Schlafes, Sohn der Zeit, Freund der Nacht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
