INSERT INTO `npc_text_localized` VALUES ('1673', 'deDE', '', 'Gebt mir einen Moment, $N. Kindal ist unten im Grimmtotemlager und versucht die Feendrachen zu befreien. Wir hatten großes Glück, dass zufällig Hilfe kam. Wer weiß, was die Tauren ihnen sonst angetan ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1674', 'deDE', 'Willkommen. Ich bin der Wächter des Eingangs. Wenn sich in Eurem Scheibensatz nicht die richtigen Platten von Uldum befinden, werde ich Euch den Zugang zum Areal von Uldum nicht gewähren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1675', 'deDE', 'Die Platten von Uldum sind von den Schöpfern hergestellte Scheiben, die den Zugang zum Areal von Uldum ermöglichen. Auf diesen Scheiben sind spezielle Daten zu den Aktivitäten der Schöpfer in dieser G', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1676', 'deDE', 'Ich kontrolliere den Zugang zum Uldum-Komplex für die Schöpfer. Ich gewähre den Zugang zum Areal nur, wenn der Bittsteller die Scheiben in der richtigen Reihenfolge einlegt.$B$BEuer Satz Scheiben enth', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1677', 'deDE', 'Die Schöpfer haben mehrere Sätze der Platten von Uldum zur ihrem speziellen Zweck geschaffen. Die Schöpfer kontrollieren die Verteilung der besagten Scheiben für alle Bereiche der Welt. So ist denn au', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1678', 'deDE', 'Die Schöpfer nutzen Besuche, wenn sie die Kontrolle über von ihnen erschaffene Welten zurückerlangen wollen. Dies geschieht, wenn externe oder interne Kräfte die Matrixdynamik der jeweiligen Welt durc', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1679', 'deDE', 'Uldum ist ein Forschungsgebiet der Schöpfer. Dort versuchen sie, die Biosphäre von Azeroth zu verbessern. Genauere Angaben zum Wirken der Schöpfer in Uldum sind Personen vorbehalten, die in ihrem Satz', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1693', 'deDE', '', 'Dem Licht sei Dank, dass Ihr hier seid! Im Augenblick würde ich auch Hilfe von einem zweiköpfigen Oger annehmen.$B$BDie Stadt ist von Troggs überrannt worden! Versuche, sie zu vernichten, endeten mit ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1713', 'deDE', 'Gebt mir ein paar Minuten, $N. Eben gab es hier einige Aufregung und ich will rasch schauen, ob alles wieder ruhig ist. Ich setze mich bald mit Euch in Verbindung.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1734', 'deDE', 'Willkommen bei \'Blaumond Dies-und-Das\', $C. Lasst es mich wissen, wenn Ihr Fragen habt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
