INSERT INTO `npc_text_localized` VALUES ('1754', 'deDE', 'Brzzzzt!$B$BWillkomen beim Matrix-Prägographen 3005-B, einem Sicherheitsterminal der Stufe Beta.$B$BGelbe Lochkarte erfasst. Zugriff gewährt.$B$B01001011 01101001 01110011 01110011 00100000 01101101 0', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
