INSERT INTO `npc_text_localized` VALUES ('1820', 'deDE', 'Wenn Reethe etwas mit dem Brand im Gasthaus Zur süßen Ruh zu tun hat, müsst Ihr ihn finden und Antworten von ihm verlangen.$B$BIch habe Ogron losgeschickt, um ihn aufzuspüren. Er brach nach Südosten i', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1833', 'deDE', '', 'Ich will das gar nicht hören, $N. Ihr werdet unter gar keinen Umständen in die sengende Schlucht gelassen. Unmöglich. Das wird nicht passieren. Wie oft soll ich Euch noch sagen, dass ich Euch nicht ei', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1834', 'deDE', '', 'Seid Ihr toll? Wisst Ihr, was für Bestien diesen Ort bevölkern? Schlimmstenfalls könnte passieren, dass ich Eure Überreste mit einer Schaufel aufsammeln muss. Wir haben schon manch guten Zwerg an dies', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1835', 'deDE', '', 'Ich sollte das wahrscheinlich tunlichst für mich behalten, aber wenn Ihr es schafft, an all den Tieren und Bestien des Ödlands vorbeizukommen, dann erreicht Ihr vielleicht auch die sengende Schlucht.$', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1836', 'deDE', '', 'Unverwundbar?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1837', 'deDE', '', 'Ihr seid ein Dummkopf und solltet auf mich hören. Niemand ist unverwundbar. Ihr tätet gut daran, das zu beherzigen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
