INSERT INTO `npc_text_localized` VALUES ('1875', 'deDE', 'Wir nennen dieses Land zwar Brachland, doch wer näher hinsieht, der bemerkt, dass der Ort voller Leben steckt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1914', 'deDE', 'Ich verkaufe die beste Stoff- und Lederkleidung diesseits des Tales!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1916', 'deDE', '', 'Ihr seid weiter gekommen als viele andere, $N. Wenn Ihr die Elemente um Euch herum respektiert, dann wird es Euch weit besser ergehen als den meisten anderen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1918', 'deDE', 'Der Altar scheint Euch anzuziehen, so, als sei er lebendig.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1920', 'deDE', 'Lobet Elune! Es ist schon viele Jahre her, dass ich Besucher hatte.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1921', 'deDE', 'Als junger Nachtelf in Dunkelküste kreuzten sich meine Wege einst mit einem widerlichen Pack, das sich der Schattenhammerklan nannte. Mit einigen von ihnen freundete ich mich an und zu meiner Schande ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1922', 'deDE', 'Ich ätzte das Zeugnis meiner Reue, meiner Scham, meines Bedauerns und meiner Trauer in eine Schrifttafel aus reinstem Gold. Leider fand der Schattenhammerklann die Tafel und brandmarkte mich als Ketze', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
