INSERT INTO `npc_text_localized` VALUES ('1933', 'deDE', 'Wenn Schneidern Euer Metier ist, dann hat Vizzklick wahrscheinlich, was Ihr braucht. Seht Euch nur um, $N, und sagt mir, wenn Ihr etwas zu kaufen wünscht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
