INSERT INTO `npc_text_localized` VALUES ('2604', 'deDE', 'Ihr möchtet das alte Handwerk erlernen? Ihr wollt dunkles Eisen verhütten?$B$BStimmt mich milde, $R. Zeigt mir ein Opfer und ich denke darüber nach!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
