INSERT INTO `npc_text_localized` VALUES ('3094', 'deDE', 'Bei allem, was Euch lieb und teuer ist, sagt mir bitte, dass Ihr uns aus diesem verlassenen Land wegbringen wollt. Ich bin zwar mit \'Marschalls Expeditionen\' hier, aber ich würde Euch jeden Goldzahn i', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
