INSERT INTO `npc_text_localized` VALUES ('4022', 'deDE', 'Wandert die Straße entlang Richtung Norden nach Orgrimmar. Einen der Grunzer dort könnt Ihr bitten, Euch den Weg zum Kochkunstlehrer zu beschreiben.', 'Wandert die Straße entlang Richtung Norden nach Orgrimmar. Einen der Grunzer dort könnt Ihr bitten, Euch den Weg zum Kochkunstlehrer zu beschreiben.', 'Hmmmm, Essen. Der Weg ist zum Glück nicht allzu weit, aber Ihr müsst trotzdem nach Orgrimmar und einen der Grunzer dort fragen.', 'Hmmmm, Essen. Der Weg ist zum Glück nicht allzu weit, aber Ihr müsst trotzdem nach Orgrimmar und einen der Grunzer dort fragen.', '', '', '', '', '', '', '', '', '', '', '', '');
