INSERT INTO `npc_text_localized` VALUES ('4295', 'deDE', 'Ach... ein Paladin? Na, $GJungchen:Mädel;, da habt Ihr Glück! Azar Hammerkraft ist zurück und hält sich momentan in der Brauerei Donnerbräu in Kharanos auf.', 'Ach... ein Paladin? Na, $GJungchen:Mädel;, da habt Ihr Glück! Azar Hammerkraft ist zurück und hält sich momentan in der Brauerei Donnerbräu in Kharanos auf.', 'Ihr braucht \'ne Ausbildung? Also, Azar Hammerkraft is\' immer dafür gut, jüngeren Paladinen was beizubringen. Warum geht Ihr nich\' mal zu ihm? Er is\' in der Brauerei Donnerbräu.', 'Ihr braucht \'ne Ausbildung? Also, Azar Hammerkraft is\' immer dafür gut, jüngeren Paladinen was beizubringen. Warum geht Ihr nich\' mal zu ihm? Er is\' in der Brauerei Donnerbräu.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4296', 'deDE', 'Soso, ein Priester, ja? Na, der gute alte Maxan ist garantiert auf einen Schluck in der Brauerei Donnerbräu. Ihr findet ihn bestimmt dort, wenn Ihr ein bisschen Unterweisung braucht.', 'Soso, ein Priester, ja? Na, der gute alte Maxan ist garantiert auf einen Schluck in der Brauerei Donnerbräu. Ihr findet ihn bestimmt dort, wenn Ihr ein bisschen Unterweisung braucht.', 'Den Maxan Anvol findet Ihr garantiert in der Brauerei Donnerbräu, wo er sich \'n paar genehmigt. Warum geht Ihr nich\' selber hin, trinkt \'n Schluck und seht zu, ob er Euch nich\' was Neues beibringen ka', 'Den Maxan Anvol findet Ihr garantiert in der Brauerei Donnerbräu, wo er sich \'n paar genehmigt. Warum geht Ihr nich\' selber hin, trinkt \'n Schluck und seht zu, ob er Euch nich\' was Neues beibringen ka', '', '', '', '', '', '', '', '', '', '', '', '');
