INSERT INTO `npc_text_localized` VALUES ('4357', 'deDE', 'Vor langer Zeit, vor dem letzten großen Krieg, führte mein Vater das Sägewerk des Nordkamms. Das Sägewerk war sein ganzes Leben, es war sein Ein und Alles. Doch dann kam der Krieg, und so sehr ihn das', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4393', 'deDE', 'Wir sind die letzten Angehörigen des Volks der Furbolgs... Noch ist der Stamm der Holzschlundfeste zwar nicht verderbt, doch von Fremden schlägt uns dieselbe Feindseligkeit entgegen wie den anderen. D', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4394', 'deDE', 'Ihr müsst einen Weg finden, wie Ihr ihnen zeigen könnt, dass Ihr auf derselben Seite kämpft wie sie. Selbst wenn die Furbolgs der Holzschlundfeste Euch jetzt vielleicht noch angreifen, werdet Ihr best', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4395', 'deDE', 'Wir von der Holzschlundfeste können Euch zwar die unbehelligte Durchreise durch unser Land gewähren, aber zuerst müsst Ihr Euch als unserer Hilfe würdig erweisen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4396', 'deDE', 'Die Furbolgs der Winterfelle, die hier in Winterquell leben, waren einst unsere Verbündeten... doch eines Tages änderte sich das.$B$BGetrieben von den verqueren Forderungen ihres Oberhäuptlings begann', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4398', 'deDE', 'Ich traue nur sehr wenigen Eurer Art, $N. Aber ich kann sehen, dass Ihr $Gein Freund:eine Freundin; seid.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4399', 'deDE', 'Ja, ja. Ich sehe Euch.$B$BMacht Euch auf den Weg. Ihr habt vielleicht das Vertrauen von ein paar von uns erworben, aber es gehört mehr dazu als das, um Euch mir zu beweisen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4401', 'deDE', 'Wenn Ihr beweist, dass Ihr $Gein echter Verbündeter:eine echte Verbündete; seid, dann ziehe ich vielleicht in Erwägung, Euch auszubilden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4433', 'deDE', 'Ich nehme an, das Licht ist mit Euch, $N. Kann ich Euch bei irgendetwas helfen?', 'Ich nehme an, das Licht ist mit Euch, $N. Kann ich Euch bei irgendetwas helfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4434', 'deDE', 'Wendet Euch nicht vom Licht ab, $C. Es könnte eines Tages das Einzige sein, was Euch rettet.', 'Wendet Euch nicht vom Licht ab, $C. Es könnte eines Tages das Einzige sein, was Euch rettet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4435', 'deDE', 'Das Licht möge Euch beschützen, $C.', 'Das Licht möge Euch beschützen, $C.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4436', 'deDE', 'Ich hoffe, das Licht ist mit Euch, $N. Gibt es irgendetwas, das ich für Euch tun kann?', 'Ich hoffe, das Licht ist mit Euch, $N. Gibt es irgendetwas, das ich für Euch tun kann?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4437', 'deDE', 'Elune sei mit Euch, $C.', 'Elune sei mit Euch, $C.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4438', 'deDE', 'Mein Wissen und meine Kenntnisse gehören Euch. Was kann ich für Euch tun, $N?', 'Mein Wissen und meine Kenntnisse gehören Euch. Was kann ich für Euch tun, $N?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4439', 'deDE', 'Ja, $C?', 'Ja, $C?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4440', 'deDE', 'Passt bloß auf, $C. Achtet unsere Bräuche, sonst möge Euch und Eure ganze Familie ein Fluch treffen!', 'Passt bloß auf, $C. Achtet unsere Bräuche, sonst möge Euch und Eure ganze Familie ein Fluch treffen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4441', 'deDE', 'Ich tu, was ich kann, um Euch was über den Weg des Priesters zu erzählen, wenn\'s das ist, warum Ihr hier seid.', 'Ich tu, was ich kann, um Euch was über den Weg des Priesters zu erzählen, wenn\'s das ist, warum Ihr hier seid.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4442', 'deDE', 'Was kann dieser unbedeutende Diener für Euch tun, $N? Euch Unterricht geben, vielleicht?', 'Was kann dieser unbedeutende Diener für Euch tun, $N? Euch Unterricht geben, vielleicht?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4443', 'deDE', 'Ich bin auf der Suche nach wem, $C. Also lasst mich doch bitte in Ruh - es is\' wirklich wichtig, dass ich den find.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4445', 'deDE', 'Seid still! Seht Ihr denn nicht, dass der Meister spricht?', 'Seid still! Seht Ihr denn nicht, dass der Meister spricht?', 'Klappert nicht so viel und hört lieber zu!', 'Klappert nicht so viel und hört lieber zu!', 'Wir müssen uns konzentrieren, wenn unser Drachenschwarm Wirklichkeit werden soll!', 'Wir müssen uns konzentrieren, wenn unser Drachenschwarm Wirklichkeit werden soll!', 'Seid ruhig! Ich kann es mir nicht leisten, auch nur eine Sekunde dieses Vortrags zu verpassen!', 'Seid ruhig! Ich kann es mir nicht leisten, auch nur eine Sekunde dieses Vortrags zu verpassen!', 'Habt Ihr Eure Studentenkleidung vergessen? Glaubt Ihr etwa, Mode sei wichtiger als die Herrschaft der Geißel?', 'Habt Ihr Eure Studentenkleidung vergessen? Glaubt Ihr etwa, Mode sei wichtiger als die Herrschaft der Geißel?', 'Habt Ihr die gestrige Aufgabe beendet? Wenn ja, dann setzt Euch zu mir!', 'Habt Ihr die gestrige Aufgabe beendet? Wenn ja, dann setzt Euch zu mir!', 'Passt hier drin besser auf, $GJungchen:Mädchen;. Der Meister sieht alles...', 'Passt hier drin besser auf, $GJungchen:Mädchen;. Der Meister sieht alles...', 'Habt Ihr Eure Lehrbücher mitgebracht? Kann ich mit Euch zusammen hineinschauen?', 'Habt Ihr Eure Lehrbücher mitgebracht? Kann ich mit Euch zusammen hineinschauen?');
INSERT INTO `npc_text_localized` VALUES ('4446', 'deDE', 'Was, darf ich fragen, tut Ihr hier? Zieht Eure Studentenkleidung an und studiert!', '', 'Seid ruhig! Glaubt Ihr etwa, unsere verseuchten Großdrachen schlüpfen von selbst und bilden sich von ganz allein aus?', '', 'Kommt Ihr aus der brennenden Steppe? Habt Ihr meine schwarzen Dracheneier?', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4447', 'deDE', 'Euer Geist ist nicht konzentriert. Verbergt Ihr etwas vor mir?', '', 'Ich spüre, wie Euch eine Unruhe umwabert...', '', 'Eure Zukunft ist umschattet. Konzentriert Euch und fragt noch einmal.', '', 'Ihr seht nicht wie ein Schüler aus...', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4449', 'deDE', 'Dieser Kodo sieht kein bisschen aus wie das Tier, das ich ursprünglich angelockt habe! Ich frage mich, ob ich den Kombobulator wohl auch auf mich selbst anwenden könnte...', 'Dieser Kodo sieht kein bisschen aus wie das Tier, das ich ursprünglich angelockt habe! Ich frage mich, ob ich den Kombobulator wohl auch auf mich selbst anwenden könnte...', 'Warum sind kleine Kreaturen wie Goblins nur immer so fasziniert von größeren Kreaturen?', 'Warum sind kleine Kreaturen wie Goblins nur immer so fasziniert von größeren Kreaturen?', 'Wie schaffen es die Goblins nur, diesen Viechern Packs aufzubinden? Kodos sind zehnmal größer als sie!', 'Wie schaffen es die Goblins nur, diesen Viechern Packs aufzubinden? Kodos sind zehnmal größer als sie!', '', '', '', '', '', '', '', '', '', '');
