INSERT INTO `npc_text_localized` VALUES ('4475', 'deDE', 'Sprecht mich an, wann immer Ihr wollt, $N. Ihr habt Magatha gut gedient. Ich bin sicher, sie wird auch in Zukunft auf Eure Hilfe zurückgreifen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
