INSERT INTO `npc_text_localized` VALUES ('4522', 'deDE', 'Es wäre klug, Euch zu erklären, welche Mühen unsere Feinde auf sich nehmen, um sich direkt unter unseren Augen zu verstecken.$B$BEines müsst Ihr vor allem wissen: Unser Feind ist der Schattenrat. Ganz', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
