INSERT INTO `npc_text_localized` VALUES ('4573', 'deDE', 'Als Angehöriger der Forscherliga suche ich hier in der Gegend nach Artefakten. Es heißt, in Winterquell gäbe es hochgradig magische Gegenstände... die meisten davon schwer bewacht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
