INSERT INTO `npc_text_localized` VALUES ('5753', 'deDE', 'Ich sehe hier am Hafen einige interessante Leute kommen und gehen. Passt auf Euch auf, $N. Man weiß nie genau, wem man vertrauen kann, bei den ganzen zwielichtigen Aktivitäten, die hier ablaufen...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
