INSERT INTO `npc_text_localized` VALUES ('5913', 'deDE', 'Dieses blaue Leder sticht ins Auge, ich weiß. Ihr seht, hier laufen sehr viele eifrige Rekruten herum, die mit mehr Enthusiasmus als Weisheit jagen und töten. Da muss man Vorsichtsmaßnahmen treffen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5918', 'deDE', '', 'Zwischen dem Licht und dem Dunkel liegen Schatten... und diese Schatten verfügen über Kräfte, wie Ihr sie noch nicht gesehen habt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
