INSERT INTO `npc_text_localized` VALUES ('5920', 'deDE', 'Ihr findet keinen besseren Rattenkebab diesseits von Khaz Modan!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5921', 'deDE', 'Ihr seht aus, als ob Ihr Arbeit gebrauchen könntet, $G Kumpel:Mädel;. Ihr solltet die Bahn besteigen und meinen Bruder Monty beim Tiefenbahndepot in Eisenschmiede besuchen. Ich bin sicher, dass er etw', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5933', 'deDE', 'Seid bloß wachsam, Fremdling.', '', 'Auf dem Rabenholdtanwesen kennt man Euch nicht, $C.', '', 'Dreist. Meister Rabenholdt mag dreist nicht.', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5934', 'deDE', 'Schön, Euch wiederzusehen, $N.', '', 'Seid gegrüßt, $C. Habt Ihr schon mit Fahrad gesprochen?', '', 'Das Syndikat besteht aus Dummköpfen - unorganisiert, ungelernt und faul...', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5938', 'deDE', 'Ein Wurm wie Ihr wagt es, seinen Fuß auf das Rabenholdtanwesen zu setzen?', 'Ein Wurm wie Ihr wagt es, seinen Fuß auf das Rabenholdtanwesen zu setzen?', 'Es wäre eine Leistung, ohne eine Klinge im Rücken oder Gift in den Adern hier herauszukommen.', 'Es wäre eine Leistung, ohne eine Klinge im Rücken oder Gift in den Adern hier herauszukommen.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5939', 'deDE', 'Ihr macht Euch gut, $N. Ich habe gute Dinge gehört...', 'Ihr macht Euch gut, $N. Ich habe gute Dinge gehört...', 'Ihr solltet mit Winstone sprechen. Ich habe gehört, dass er nach Dieben mit Eurer Fertigkeit sucht.', 'Ihr solltet mit Winstone sprechen. Ich habe gehört, dass er nach Dieben mit Eurer Fertigkeit sucht.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5943', 'deDE', '', 'Möge Elune mit Euch sein, $C. Der Tempel des Mondes ist den verängstigten Bürgern von Auberdine zu Hilfe gekommen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5973', 'deDE', 'Shoja\'my steht drüben vor dem Gasthaus. Und jetzt geht weiter, ich bin beschäftigt.', 'Shoja\'my steht drüben vor dem Gasthaus. Und jetzt geht weiter, ich bin beschäftigt.', 'Um Tiere kümmern? Hauptsache Ihr räumt hinter ihnen auf! Sucht nach Shoja\'my vor dem Gasthaus.', 'Um Tiere kümmern? Hauptsache Ihr räumt hinter ihnen auf! Sucht nach Shoja\'my vor dem Gasthaus.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5974', 'deDE', 'Geht zum Tal der Ehre und sucht Xon\'cha in der Nähe der Ställe auf.', 'Geht zum Tal der Ehre und sucht Xon\'cha in der Nähe der Ställe auf.', 'Xon\'cha wird Eure zusätzlichen Tiere unterbringen. Ihr findet ihn im Tal der Ehre bei den Ställen zwischen der Arena und der Jägerhalle. Nun hinfort!', 'Xon\'cha wird Eure zusätzlichen Tiere unterbringen. Ihr findet ihn im Tal der Ehre bei den Ställen zwischen der Arena und der Jägerhalle. Nun hinfort!', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5976', 'deDE', 'Ah ja, da müsst Ihr Seikwa aufsuchen. Ich glaube, ich habe sie zuletzt vor dem Gasthaus gesehen.', 'Ah ja, da müsst Ihr Seikwa aufsuchen. Ich glaube, ich habe sie zuletzt vor dem Gasthaus gesehen.', 'Seikwa kann sich um Eure tierischen Freunde kümmern, wenn Ihr das wollt. Ihr findet sie vor dem Gasthaus.', 'Seikwa kann sich um Eure tierischen Freunde kümmern, wenn Ihr das wollt. Ihr findet sie vor dem Gasthaus.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5977', 'deDE', 'Ihr benötigt die Hilfe von Bulrug. Den findet Ihr draußen auf der unteren Anhöhe, zwischen dem Gasthaus und der Bank von Donnerfels.', 'Ihr benötigt die Hilfe von Bulrug. Den findet Ihr draußen auf der unteren Anhöhe, zwischen dem Gasthaus und der Bank von Donnerfels.', 'Die Besorgnis, die Ihr für das Wohlergehen Eurer tierischen Begleiter an den Tag legt, ist lobenswert. Bulrug auf der unteren Anhöhe kann Euch dabei helfen. Ihr findet ihn zwischen der Bank und dem Ga', 'Die Besorgnis, die Ihr für das Wohlergehen Eurer tierischen Begleiter an den Tag legt, ist lobenswert. Bulrug auf der unteren Anhöhe kann Euch dabei helfen. Ihr findet ihn zwischen der Bank und dem Ga', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5978', 'deDE', 'Ist kaum zu glauben, ich weiß, aber Ihr findet Morganus, den Stallmeister, tatsächlich vor den Ställen.', 'Ist kaum zu glauben, ich weiß, aber Ihr findet Morganus, den Stallmeister, tatsächlich vor den Ställen.', 'Ihr seid nicht sehr schlau, was? Hmm, also wenn ich ein Stallmeister wäre, dann würde ich mich in der Nähe, sagen wir mal, der Ställe aufhalten... Sucht dort nach Morganus.', 'Ihr seid nicht sehr schlau, was? Hmm, also wenn ich ein Stallmeister wäre, dann würde ich mich in der Nähe, sagen wir mal, der Ställe aufhalten... Sucht dort nach Morganus.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5979', 'deDE', 'Anya Maulray bei Gasthaus auf oberer Ring von des Handwerksviertel.', 'Anya Maulray bei Gasthaus auf oberer Ring von des Handwerksviertel.', 'Wir viele Tiere wollen. Anya Maulray Euch helfen sich um sie kümmern. Ihr sie finden bei Gasthaus auf oberer Ring von Handwerksviertel.', 'Wir viele Tiere wollen. Anya Maulray Euch helfen sich um sie kümmern. Ihr sie finden bei Gasthaus auf oberer Ring von Handwerksviertel.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5980', 'deDE', 'Ihr könnt Euch darauf verlassen, dass sich Alassin gut um all Eure Tiere kümmern wird. Er befindet sich in der Enklave des Cenarius, wo sich die Jäger versammeln.', 'Ihr könnt Euch darauf verlassen, dass sich Alassin gut um all Eure Tiere kümmern wird. Er befindet sich in der Enklave des Cenarius, wo sich die Jäger versammeln.', 'Seit Hunderten von Jahren kümmert sich Alassin um die Tiere, die unsere Jäger zähmen. Seine Pflege der Tiere wird von den Jägern hier sehr geschätzt. Wenn auch Ihr Euch seiner Dienste versichern möcht', 'Seit Hunderten von Jahren kümmert sich Alassin um die Tiere, die unsere Jäger zähmen. Seine Pflege der Tiere wird von den Jägern hier sehr geschätzt. Wenn auch Ihr Euch seiner Dienste versichern möcht', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5981', 'deDE', 'Wir sind alle Teile eines viel größeren Puzzles, $N. So wie Ihr ein Teil eines Puzzles zu seinem Ziel führt, so werden auch wir durch einen anderen geführt.$B$BUnd vielleicht werden die, die uns leite', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5982', 'deDE', 'Ah, die sanfte Seriadne. Alle Tiere sind in ihrer Obhut sicher. Ihr findet sie vor dem Gasthaus in Dolanaar.', 'Ah, die sanfte Seriadne. Alle Tiere sind in ihrer Obhut sicher. Ihr findet sie vor dem Gasthaus in Dolanaar.', 'Ihr sucht jemanden, der sich um Tiere kümmert? Dann solltet Ihr Seriadne aufsuchen, gleich neben dem Gasthaus in Dolanaar. Sie ist perfekt dafür.', 'Ihr sucht jemanden, der sich um Tiere kümmert? Dann solltet Ihr Seriadne aufsuchen, gleich neben dem Gasthaus in Dolanaar. Sie ist perfekt dafür.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5983', 'deDE', 'Die gute alte Erma, sie kümmert sich jetzt schon seit Jahren um die hiesigen Tiere. Ich bin sicher, dass sie sich auch gern um alle kümmern wird, die Ihr ihr bringt. Ihr findet sie beim Gasthaus Zur H', 'Die gute alte Erma, sie kümmert sich jetzt schon seit Jahren um die hiesigen Tiere. Ich bin sicher, dass sie sich auch gern um alle kümmern wird, die Ihr ihr bringt. Ihr findet sie beim Gasthaus Zur H', 'Erma kümmert sich um alle Tiere hier. Sie befindet sich beim Gasthaus Zur Höhle des Löwen, wenn Ihr mit ihr sprechen wollt.', 'Erma kümmert sich um alle Tiere hier. Sie befindet sich beim Gasthaus Zur Höhle des Löwen, wenn Ihr mit ihr sprechen wollt.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5984', 'deDE', 'Jenova Steinschild drüben im Zwergendistrikt dürfte Euren Vorstellungen entsprechen. Sie kümmert sich um die Tiere der Jäger, während die bei ihrem Vater ausgebildet werden.', 'Jenova Steinschild drüben im Zwergendistrikt dürfte Euren Vorstellungen entsprechen. Sie kümmert sich um die Tiere der Jäger, während die bei ihrem Vater ausgebildet werden.', 'Wenn Ihr Euer Tier unterbringen müsst, während Ihr ein anderes ausbildet, dann solltet Ihr mit Jenova Steinschild drüben im Zwergendistrikt sprechen.', 'Wenn Ihr Euer Tier unterbringen müsst, während Ihr ein anderes ausbildet, dann solltet Ihr mit Jenova Steinschild drüben im Zwergendistrikt sprechen.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5985', 'deDE', 'Tja, $GJungchen:Mädel;, Ihr könnt Shelby Steinsplint direkt vor der Brauerei Donnerbräu in Kharanos finden. Sie wird sich um alle Tiere kümmern, die Ihr habt.', 'Tja, $GJungchen:Mädel;, Ihr könnt Shelby Steinsplint direkt vor der Brauerei Donnerbräu in Kharanos finden. Sie wird sich um alle Tiere kümmern, die Ihr habt.', 'Shelby Steinsplint betreut sehr viele Tiere für die hiesigen Jäger. Ihr findet sie drüben bei der Brauerei Donnerbräu, falls Ihr wollt, dass sie sich um ein Tier kümmern soll.', 'Shelby Steinsplint betreut sehr viele Tiere für die hiesigen Jäger. Ihr findet sie drüben bei der Brauerei Donnerbräu, falls Ihr wollt, dass sie sich um ein Tier kümmern soll.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5986', 'deDE', 'Die Pflege von Tieren ist etwas, das Ulbrek Feuerhand ziemlich ernst nimmt. Er treibt sich normalerweise bei den Jägern in der Halle der Waffen herum, falls Ihr wollt, dass er sich für Euch um ein ode', 'Die Pflege von Tieren ist etwas, das Ulbrek Feuerhand ziemlich ernst nimmt. Er treibt sich normalerweise bei den Jägern in der Halle der Waffen herum, falls Ihr wollt, dass er sich für Euch um ein ode', 'Drüben in der Halle der Waffen könnt Ihr unseren hiesigen Stallmeister, Ulbrek Feuerhand, finden. Er wird sicherstellen, dass alle Tiere, die Ihr habt, gut gefüttert und betreut werden.', 'Drüben in der Halle der Waffen könnt Ihr unseren hiesigen Stallmeister, Ulbrek Feuerhand, finden. Er wird sicherstellen, dass alle Tiere, die Ihr habt, gut gefüttert und betreut werden.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5993', 'deDE', 'Ich habe etwas für Euch, $N. Könnte wichtig sein.', 'Ich habe etwas für Euch, $N. Könnte wichtig sein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5994', 'deDE', 'Willkommen im Hauptquartier von SI:7, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5996', 'deDE', 'Boten von Rabenholdt haben mir heute Morgen diesen Brief ausgehändigt. Er ist an Euch adressiert, $N.', 'Boten von Rabenholdt haben mir heute Morgen diesen Brief ausgehändigt. Er ist an Euch adressiert, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6033', 'deDE', 'Ja, $C? Erstattet Ihr Bericht?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6062', 'deDE', 'Dieses Monument ist all denen gewidmet, die beim Schutz von Sturmwind gefallen sind. Unsere Leute haben Unglaubliches durchgemacht, um ihre Freiheit zu bewahren und ihr Schicksal in die eigenen Hände ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6066', 'deDE', 'Im Alteractal toben Kämpfe und es gibt wenig Nachschub.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6073', 'deDE', 'Unsere Einheiten sind nicht ausgebaut und ich habe nicht genug Vorräte, um sie auszubauen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6093', 'deDE', '', 'Das Blut unserer Feinde soll das Schlachtfeld tränken. Die großen Elementare werden unserem Ruf folgen und alles zerschmettern, was sich uns in den Weg stellt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6095', 'deDE', '', 'Gute Arbeit, Soldat! Ich muss zum Stützpunkt zurück! Gebt Ihr mir Deckung?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6096', 'deDE', '', 'Wurde auch Zeit, dass mal jemand kommt. Was hat Euch so lange aufgehalten? Ach, egal. Gebt mir Deckung, Soldat. Ich muss zum Stützpunkt zurück!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6098', 'deDE', '', 'Es wartet noch viel Arbeit auf Euch, $N. Kämpft weiter gegen unsere Feinde!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6099', 'deDE', '', 'Ja, $N. Die Elemente rühren sich. Schon bald werden wir ihre Macht über unseren Feinden entfesseln. Bei unseren Schlägen wird ihnen Hören und Sehen vergehen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6100', 'deDE', '', 'Ich habe Kontakt hergestellt, $N. Es ist Lokholar der Eislord. Er erachtet unsere Sache als würdig - unsere Opfer an die Erde als ausreichend. Er wird bald kommen... macht Euch bereit.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6101', 'deDE', 'Ich brauche ein paar gute Flügelmänner, die mich sicher hier weg und zurück zum Stützpunkt bringen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6104', 'deDE', 'Die Seiten des Tagebuchs scheinen mit Informationen über Geschäfte, Käufer und andere Geschäftsdinge gefüllt zu sein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6105', 'deDE', '', 'Seht dem Kommandant niemals direkt in die Augen.', '', 'Wir haben direkte Befehle von Cairne, $R. Ihr dürft Euch in die Aufgabe des Kommandanten nicht einmischen.', '', 'Man darf den Schamanen-Kommandanten nicht unterschätzen, $R.', '', 'Bald schon wird sich Lokholar erheben... Bald... Sehr, sehr bald...', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6107', 'deDE', '', 'Die Flotte ist bereit, $N. Ihr müsst dieses Signal nehmen und im östlichen Krater auf dem zentralen Schlachtfeld platzieren. Verteidigt das Signal gegen Angreifer, bis die Flotte die Koordinaten des S', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6153', 'deDE', 'Wenn Ihr nach Informationen sucht, die der Zirkel des Cenarius irgendwo archiviert haben könnte, bin ich genau der richtige Ansprechpartner. Ihr müsst allerdings schon wissen, welche Informationen Ihr', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6154', 'deDE', 'Hört nicht auf die Herzen der Erde - die Antwort liegt im Inneren. Ihr kennt die Wahrheit.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6155', 'deDE', 'In diesen Wüsten lebt die Vergangenheit, $C. Wir müssen vorsichtig sein, damit wir sie nicht wecken.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6158', 'deDE', 'Ihr seid bereit für die allerletzte Aufgabe, $N. Die feurigen Runen, die in den Boden des geschmolzenen Kerns eingraviert wurden, leiten ins Reich des Feuers und sie sind für uns nicht nur ein Affront', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6159', 'deDE', 'Willkommen bei der Gildenregistratur von Unterstadt. Was wollt Ihr?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6160', 'deDE', 'Willkommen beim Zirkel des Cenarius, $C. Wie kann ich Euch dienen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6164', 'deDE', 'Ich glaub nicht, dass Ihr irgendwas lernen wollt, was ich Euch beibringen kann. $G Ein:Eine:C; $C sollte sich auch einen entsprechenden Lehrer suchen.', 'Ich glaub nicht, dass Ihr irgendwas lernen wollt, was ich Euch beibringen kann. $G Ein:Eine:C; $C sollte sich auch einen entsprechenden Lehrer suchen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6165', 'deDE', 'Ihr seht aus wie jemand, in dessen Gegenwart ich auf meinen Geldbeutel achten sollte. Kann ich Euch mit etwas behilflich sein?', 'Ihr seht aus wie jemand, in dessen Gegenwart ich auf meinen Geldbeutel achten sollte. Kann ich Euch mit etwas behilflich sein?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6173', 'deDE', 'Man hat uns von Darnassus hergeschickt, um den Sturmlanzenklan in Zeiten der Not beizustehen.', '', 'Die Erzdruide glaubt, dass Ivus der Waldlord kommen wird, sobald er von der Zerstörung dieses Tales erfährt.', '', 'Es ist nicht an uns, diese Gräueltaten zu beurteilen. Ivus wird darüber richten, sobald er hier eingetroffen ist.', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6174', 'deDE', '', 'Mein Glaube leitet meine Aktionen, $R. Ich weiß, dass Ivus kommen wird.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6175', 'deDE', '', 'Ich habe mich in dem Traum verloren. Ich spüre seine Lebenskraft, aber meine Kräfte sind noch nicht stark genug, um Kontakt zu ihm aufzunehmen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6176', 'deDE', '', 'Ja. $N, ich habe den Waldlord gefunden. Wir müssen unsere Anstrengungen verdoppeln! Es werden weitere Kristalle benötigt!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6177', 'deDE', '', 'Der Waldlord weiß um die Zerstörung in diesem Tal, $N. Bald müssen wir zum Kreis des Rufens. Macht Euch bereit!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6178', 'deDE', 'Dieser tollkühne Narr Mulverick hat mich abgeschossen. Ich fürchte, wenn ich es nicht zurück zum Stützpunkt schaffe, ist alles verloren! Mulverick und seine Truppe aus Kriegsreitern müssen aufgehalten', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6179', 'deDE', 'Ich kann es nicht glauben... Ich war auf einer routinemäßigen Aufklärungsmission in der zentralen DMZ, als plötzlich die Hölle losbrach. Dort wurde ich Zeuge, wie der Schwadronskommandant der Horde Mu', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6180', 'deDE', 'Ich hörte noch ihr Hohngelächter, als mein Greif zu Boden stürzte und verbrannte, $C.$B$B\"Slidore, du stinkst!\", riefen sie.$B$BNiemand spricht so mit Slidore! Helft mir hier raus, damit ich denen zei', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6193', 'deDE', '', 'Dieser Wald kann einen ganz schön verwirren! Seid vorsichtig da draußen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6194', 'deDE', 'Schöne Grüße an Euch und die Euren!', 'Schöne Grüße an Euch und die Euren!', 'Frisch von unserem Hof auf Euren Tisch - beste Vollwertkost von Kokelwälder!', 'Frisch von unserem Hof auf Euren Tisch - beste Vollwertkost von Kokelwälder!', 'Schnäppchenzeit ist angebrochen! Tra la la la laaaa, la la, la laaa!', 'Schnäppchenzeit ist angebrochen! Tra la la la laaaa, la la, la laaa!', 'Ich wünsche ein wunderbares Winterhauchfest! Läutet es ein mit Leckereien von Kokelwälder!', 'Ich wünsche ein wunderbares Winterhauchfest! Läutet es ein mit Leckereien von Kokelwälder!', 'Vergesst nicht, Altvater Winter zu sagen, welche Überraschung Ihr Euch dieses Winterhauchfest wünscht!', 'Vergesst nicht, Altvater Winter zu sagen, welche Überraschung Ihr Euch dieses Winterhauchfest wünscht!', 'Möge das Klingeln der Glöckchen Euch nicht vom Klingeln in Eurem Geldbeutel ablenken! Kauft Leckereien von Kokelwälder für Eure Winterhauchfeiern!', 'Möge das Klingeln der Glöckchen Euch nicht vom Klingeln in Eurem Geldbeutel ablenken! Kauft Leckereien von Kokelwälder für Eure Winterhauchfeiern!', 'Wenn ich ein paar Kastanien hätte, würde ich sie sofort über dem Feuer rösten. Mmmm!', 'Wenn ich ein paar Kastanien hätte, würde ich sie sofort über dem Feuer rösten. Mmmm!', 'Vergesst nicht, Euch für die diesjährigen Festtage mit Geschenkpapier einzudecken! Nichts sagt \'Fröhliches Winterhauchfest\' schöner als hübsch verpackte Rüstungen und Waffen für Eure Freunde und Famil', 'Vergesst nicht, Euch für die diesjährigen Festtage mit Geschenkpapier einzudecken! Nichts sagt \'Fröhliches Winterhauchfest\' schöner als hübsch verpackte Rüstungen und Waffen für Eure Freunde und Famil');
INSERT INTO `npc_text_localized` VALUES ('6217', 'deDE', 'Unsere Einheiten sind zu \'Erfahren\' ausgebaut, aber ich habe nicht genug Vorräte, um sie zu \'Veteranen\' auszubauen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6218', 'deDE', 'Unsere Einheiten sind zu \'Veteranen\' ausgebaut, aber ich habe nicht genug Vorräte, um sie zu \'Champions\' auszubauen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6220', 'deDE', 'Ich habe genügend Vorräte, um unsere Truppen zu \'Erfahren\' auszubauen', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6222', 'deDE', 'Unsere Einheiten sind zu \'Champions\' ausgebaut, aber ich kann noch weitere Vorräte auftreiben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6223', 'deDE', 'Ich kann keine weiteren Vorräte unterbringen. Das, was ich habe, reicht aus.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6233', 'deDE', 'Seid Ihr hier, um den Umgang mit einer neuen Waffe zu lernen, oder nur, um meine Zeit zu vergeuden?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6235', 'deDE', 'Sagt mir, für welche Waffe Ihr Ausbildung sucht oder verschwindet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6250', 'deDE', 'Ein fröhliches Winterhauchfest wünsch ich,... äh... $GJungchen:Mädchen;. Brave Kinder bekommen besondere, äh, Sonderkonditionen bei allen Verkäufern von Kokelwälder. Lecker.', '', 'Ich kann mir gut vorstellen, was der alte Altvater Winter tut, wenn seine Schicht um ist. Ich hab hier \'n spezielles Winterhauchtröpfchen in der Tasche, das ich mir mit Genuss einverleiben werde.', '', 'Auf meinem Schoß sitzen? Kommt nicht in Frage.', '', 'Ja, sicher, man sollte Altvater Winter erzählen, was man sich zum Winterhauchfest wünscht.$B$BHoho, Tempo!', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6255', 'deDE', 'Ein leerer Magen marschiert nicht gern, und das ist die Wahrheit! Wenn Eure Offensive erfolgreich sein soll, sorgt dafür, dass Eure Truppen wohlgenährt und gut ausgerüstet sind.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6257', 'deDE', 'Einige sagen, dass Stärke und Tapferkeit das Wichtigste für den Erfolg einer Armee sind. Und das ist wahr! Doch braucht eine Armee auch Vorräte, um in guter Verfassung zu bleiben, und es ist meine Auf', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6273', 'deDE', '', 'Seid gegrüßt, Soldat. Seid Ihr hier, um der Sache zu dienen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6274', 'deDE', '', 'Gut gemacht, Soldat. Noch ein paar mehr und unsere Ställe werden vor Reittieren überquellen, auf denen unsere Kavallerie in die Schlacht ziehen kann!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6275', 'deDE', 'He, Mann. Bei mir seid Ihr genau richtig, wenn Ihr lernen wollt, wie man eine neue Waffe führt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6277', 'deDE', 'Was willst du lernen, Mann?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6279', 'deDE', 'Es wäre mir eine Ehre, Euch im Umgang mit den Waffen zu unterweisen, die ich beherrsche, so Ihr die Fähigkeit, sie zu erlernen, in Euch tragt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6281', 'deDE', 'Wenn Ihr weitere Ausbildung sucht, werde ich Euch gerne sagen, an wen Ihr Euch wenden müsst. Welche Waffe interessiert Euch?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6282', 'deDE', 'Tod dem Sturmlanzenklan!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6286', 'deDE', 'Ich nehme an, Ihr seid hier, um Euch im Umgang mit einer neuen Waffe ausbilden zu lassen, ja? Beeilt Euch, ich bin sehr beschäftigt!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6287', 'deDE', 'Ich werde Euch den Weg weisen, sofern Ihr mir sagt, was Euch interessiert.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6288', 'deDE', '$N, es tut gut, Veteranen wie Euch in der Schlacht zu sehen! Ich muss Euch leider mitteilen, dass unsere Vorräte im Moment zu gering sind, um einen Bodenangriff gegen die Horde starten zu können.$B$BS', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6289', 'deDE', 'Seid Ihr gekommen, um Euch im Kampf mit Waffen ausbilden zu lassen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6290', 'deDE', 'Für welche Waffen interessiert Ihr Euch denn?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6291', 'deDE', '', 'Die Anmut, mit der ich kämpfe, kann ich Euch leider nicht beibringen, aber ich kann Euch in der Benutzung diverser Waffen unterweisen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6292', 'deDE', '', 'Wenn es Euch lieber ist, kann Ich Euch den Weg zu den anderen Waffenmeistern weisen. Wofür genau interessiert Ihr Euch?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6293', 'deDE', 'Ihr wollt Euch im Umgang mit einer neuen Waffe ausbilden lassen, richtig? Dann steht da nicht rum und haltet Maulaffen feil, $GBursche:Mädel;!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6294', 'deDE', 'Welche Waffenfertigkeit wolltet Ihr erlernen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6295', 'deDE', '', 'Hallo auch! Seid Ihr hier, um die Feinheiten im Umgang mit einer neuen Waffe zu erlernen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6296', 'deDE', '', 'Ihr möchtet Euch also nicht von mir ausbilden lassen? Nun gut, gibt es noch andere Waffen, die Euch interessieren?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6313', 'deDE', 'Tod den Frostwölfen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6316', 'deDE', '', 'Die in Alterac beheimateten Widder sind lebenswichtig für die Einsatzfähigkeit unserer Kavallerie!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6333', 'deDE', 'Grüße, $N. Fasst Euch kurz, denn es gibt viel zu tun.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6334', 'deDE', 'Möge Cenarius Euch leiten, $C.$B$BIch bin mit meinen Schwestern hier in diesem verderbten Land, um eine äußerst wichtige Mission für den Zirkel des Cenarius zu erfüllen. Allerdings bitte ich darum, da', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6335', 'deDE', '', 'Meine Schwestern und ich kamen aus dem Steinkrallengebirge herunter, um eine Tragödie aus der fernen Vergangenheit zu beenden. Leider sind wir nicht stark genug, um unsere Ziele allein zu erreichen, u', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6336', 'deDE', 'Ich grüße Euch, $R.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6353', 'deDE', 'Ich brauche kein Heim, um zu wissen, dass ich in dieser Welt immer noch einen Platz habe, $C. Die Stämme werden das erkennen... alle werden es. Sie kämpfen gegeneinander, und wofür? Krumen, die die Ge', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6354', 'deDE', 'Endlich bin ich von der Verderbnis befreit, die mich einst an diesen Ort band...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6373', 'deDE', 'Vielen Dank für Eure Mühen, $N.$B$BPasst gut auf das Szepter auf. Möge es Euch bei Euren Kämpfen gegen das Böse, das in diesen Ort eingedrungen ist, helfen...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6375', 'deDE', 'Mein Szepter... Nach so vielen Jahren... Ihr habt es gefunden! Ihr seid wahrlich der Retter dieses Ortes...$B$BBitte hört mir gut zu, $N. Der Kopf und der Griff des Szepters können nur beim Stein von ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6377', 'deDE', 'Zwischen den Farben... ein Weg.$B$BBlinzelt hinüber ins Licht.$B$BAus dem Wasser steigt Ihr empor.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6378', 'deDE', 'Zwischen dem orangefarbenen und dem lila Durchgang in der Nähe der Türen nach Maraudon findet Ihr einen dritten Weg.$B$BDas Szepter in Eurer Hand ist der Schlüssel dazu.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6379', 'deDE', 'Ich bin es langsam leid...$B$BIhr könnt von hier aus die Wasserfälle sehen, $N... Das Szepter verleiht Euch die Macht, zu eben jenem Ort zu reisen, falls Ihr jemals den Wunsch verspürt, noch einmal du', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6380', 'deDE', 'Vor langer Zeit begaben sich meine Dryaden-Schwestern und ich in diese Höhlen, um den gequälten Geist meines Onkels Zaetar zu befreien. Die Verderbnis, die sich in diesen Höhlen bereits ausgebreitet h', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6381', 'deDE', 'Ah, Zaetar... <Celebras wendet den Blick ab.>$B$BMein Vater Remulos versuchte, ihm Einhalt zu gebieten...$B$BZaetar war sehr eigensinnig, und als er einer Prinzessin der Erdelementare verfiel, wusste ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6383', 'deDE', 'Willkommen bei der Forscherliga. Wenn Ihr Informationen braucht und diese in Büchern zu finden sind, kann ich Euch bestimmt helfen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6393', 'deDE', 'Ihr fragt nach Cenarius\' Söhnen Remulos und Zaetar.$B$BMein Vater Remulos war stark und gut aussehend und mein Onkel Zaetar äußerst schlau und von schlanker Gestalt. Obwohl man zwar beide gleichermaße', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6413', 'deDE', 'Seid gegrüßt, $N. Ich bin Doktor Gregory Victor, Cheftraumachirurg der Traumatologie der Horde.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6414', 'deDE', 'Schön, Euch wiederzusehen, Doktor $N. Ich habe Eure Fortschritte verfolgt und würde sagen, dass es für Euch an der Zeit ist, ein paar der eher komplizierteren Verbandstechniken zu erlernen.$B$BBesucht', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6415', 'deDE', 'Seid gegrüßt, $N. Ich bin Doktor Gustav van Howzen, Cheftraumachirurg der Traumatologie der Allianz.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6435', 'deDE', '', 'Es ist gut, Veteranan wie Euch im Alteractal zu sehen, $N. Wir haben zu wenig Vorräte, um einen Bodenangriff durch das Tal zu starten. Redet mit unserem Rüstmeister - er wird Euch erklären, wie wir un', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6437', 'deDE', '', 'Ihr! $N! Wieso wandert Ihr hier einfach so im Dorf herum? Allianztruppen sind in Alterac eingedrungen! Wir müssen sie vertreiben!$B$BUnsere Vorräte sind zu gering, um einen massiven Angriff zu starten', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6475', 'deDE', 'Ich bin verantwortlich für die Anwerbung beim Frostwolfklan. Als ein Soldat der Frostwölfe ist es Eure Pflicht, Euch bei mir zu melden, sobald Ihr innerhalb des Klans im Rang aufsteigt.$B$BEhre und Re', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6476', 'deDE', 'Seid gegrüßt, $R! Ich bin Leutnant Haggerdin, verantwortlicher Offizier in Sachen Nachschub für die Sturmlanzengarde.$B$BWenn Euer Ruf bei den Sturmlanzen sich verbessert hat, kommt zurück zu mir, und', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6513', 'deDE', 'Unsere Vorfahren lehren uns, dass der Einzelne stark sein muss, wir aber nur mit Vielen Sicherheit finden. Wollt Ihr die Ehre von Donnerfels verteidigen, indem Ihr eine Gilde gründet?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6514', 'deDE', 'Ihr seht wahrlich wie ein Anführer aus. Seid Ihr gekommen um eine Gilde zu organisieren, um für die Allianz zu kämpfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6533', 'deDE', 'Seid gegrüßt, $C. Ich bin Rokaro.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6534', 'deDE', '', 'Was wollt Ihr vom blauen Drachenschwarm?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6535', 'deDE', 'Meister Nesingwary geht auf eine anstrengende Jagd. Ich finde es wesentlich entspannender, ihm bei den Vorbereitungen für einen Tag im Dschungel zu helfen, als ihn für einen Kampf auszurüsten. Allerdi', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6553', 'deDE', '', 'Ich kenne die Wälder so gut, als wären sie ein Teil von mir... Doch wisset, dass man noch immer viel von ihnen lernen kann und sie bergen auch viel Gefahr.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6555', 'deDE', '', 'Ihr habt Teronis Mission erfüllt, und dafür bin ich Euch sehr dankbar, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6556', 'deDE', '', 'Hi da! Diese Werkstatt gehört mir gar nicht, nein! Aber eines Tages, wenn ich meine Ausbildung beendet habe, werde ich ein Erfinder, genau wie mein Held Marvon Nietensucher.$B$BIhr habt noch nie von i', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6557', 'deDE', '', 'Hallo! Nein, Marvon ist nicht hier, und er wird wohl für eine Weile nicht zurückkommen. In Wahrheit beginne ich bereits, mir Sorgen um ihn zu machen...$B$BIch hoffe, er kommt bald zurück. Es ist nämli', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6573', 'deDE', 'Ihr habt bereits alles gelernt, was ich Euch bis jetzt beibringen kann, Doktor $N. Kommt zu mir zurück, wenn Ihr geübter seid, und ich werde Euch in der fortgeschrittenen medizinischen Lehre unterweis', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6574', 'deDE', '', 'Dieser Ort... ist verflucht.$B$BIch habe es kaum geschafft zu entkommen. Daher bringe ich es kein zweites mal fertig, in diese Tunnel hinabzusteigen$B$BMein Bruder und meine Schwester hatten weniger G', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6594', 'deDE', '', 'Braucht Ihr Vorräte?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6595', 'deDE', 'Soll ich Euch zeigen, was ich im Angebot habe?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6654', 'deDE', 'Ihr sucht Anleitung in der Kriegskunst, $R? Dann seid Ihr hier falsch.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6655', 'deDE', 'Ihr seid hier, um etwas über gewonnene und verlorene Kämpfe auf diesem Schlachtfeld zu erfahren? Hier gibt es keine Gewinner...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6657', 'deDE', 'Beeilt euch, Soldat. Der Geheimdienst hat mir berichtet, dass die Winterax-Trolle einen Meister der Goblin-Ingenieure gefangen genommen haben. Die Frostwolf-Bastarde versuchen bereits, ihn zu retten, ', '', 'Es wird berichtet das, das Syndikat wohl in einer Höhle auf dem östlichen Plateau herumschnüffelt. Findet heraus was sie dort treiben, Soldat! Und achtet auf Frostwolf-Aktivitäten!', '', 'Seid vorsichtig, wenn Ihr das Gebiet der Frostwolf betretet, Soldat! Die ganze verdammte Gegend ist vermint! Massenweise Landminen. Sprecht mit unserem Sprengmeister im ersten Turm in der Frontlinie. ', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6694', 'deDE', 'Hilflos... keine Energie mehr... wieder herstellen... muss wieder herstellen... Zevrim hat Kontrolle... beendet diese Qual...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6695', 'deDE', 'Danke, Sterblicher. Es gibt noch einen Dienst, den ich Euch erweisen kann, bevor ich wieder in den Traum zurückkehre.$B$B.Die Tür zu Zevrims Meister, Alzzin, sie blockiert Euren Weg. Ich kann Euch als', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6721', 'deDE', 'Wir haben kaum noch Vorräte. Wenn wir einen Bodenangriff im Feld des Kampfes starten wollen, werden wir noch sehr viel mehr Vorräte brauchen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6733', 'deDE', 'Wir haben kaum überschüssige Vorräte. Wir müssen noch hart arbeiten, bis wir einen Bodenangriff im Feld des Kampfes starten können.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6735', 'deDE', 'Ich brauche sehr viel mehr Vorräte, um unsere Truppen aufzurüsten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6736', 'deDE', 'Ich habe fast genügend Vorräte, um unsere Truppen auszubauen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6741', 'deDE', 'Verdammt... dieses Ungeziefer hält mich Tag und Nacht beschäftigt! Habt Ihr sie nicht gesehen?$B$BIch wünschte mir jemand würde sich des Problems annehmen...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6755', 'deDE', 'Ja, $C?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6777', 'deDE', 'Ich brauche sehr viel mehr Vorräte, um unsere Truppen auszubauen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6778', 'deDE', 'Ich habe fast genügend Vorräte, um unsere Truppen auszubauen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6779', 'deDE', 'Ich habe fast genügend Vorräte, um unsere Truppen zu \'Veteran\' auszubauen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6780', 'deDE', 'Ich brauche sehr viel mehr Vorräte, um unsere Truppen auszubauen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6781', 'deDE', 'Ich habe die Hälfte der benötigten Vorräte, um unsere Truppen zu \'Veteran\' auszubauen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6784', 'deDE', 'Ich habe kaum Vorräte für Aufrüstungen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6785', 'deDE', 'Unsere Truppen sind nicht aufgerüstet, und ich habe nicht genügend Vorräte für Aufrüstungen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6786', 'deDE', 'Unsere Truppen sind zu \'Erfahren\' aufgerüstet, aber ich habe nicht genügend Vorräte, um sie zu \'Veteran\' auszubauen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6787', 'deDE', 'Unsere Truppen sind zu \'Veteran\' aufgerüstet, aber ich habe nicht genügend Vorräte, um sie zu \'Champion\' auszubauen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
