INSERT INTO `npc_text_localized` VALUES ('6789', 'deDE', 'Ich habe nicht genügend Vorräte, um unsere Truppen zu \'Veteran\' auszubauen.', 'Ich habe nicht genügend Vorräte, um unsere Truppen zu \'Veteran\' auszubauen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6790', 'deDE', 'Ich habe genügend Vorräte, um unsere Truppen zu \'Champions\' auszubauen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6791', 'deDE', 'Mehr Ausrüstung kann ich nicht mehr einlagern. Das war es vorerst.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6793', 'deDE', 'Ich kann Euch nichts mehr lehren. Ich hörte aber, dass der alte Mann Heming in Beutebucht ein Exemplar von \"Der Barsch und du\" vorrätig haben soll. Dieses Buch wird Eure Fertigkeiten sicherlich verbes', 'Ich kann Euch nichts mehr lehren. Ich hörte aber, dass der alte Mann Heming in Beutebucht ein Exemplar von \"Der Barsch und du\" vorrätig haben soll. Dieses Buch wird Eure Fertigkeiten sicherlich verbes', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6794', 'deDE', 'Ich kann Euch nichts mehr lehren. Ich hörte aber, dass der alte Mann Heming in Beutebucht ein Exemplar von \"Der Barsch und du\" vorrätig haben soll. Dieses Buch wird Eure Fertigkeiten sicherlich verbes', 'Ich kann Euch nichts mehr lehren. Ich hörte aber, dass der alte Mann Heming in Beutebucht ein Exemplar von \"Der Barsch und du\" vorrätig haben soll. Dieses Buch wird Eure Fertigkeiten sicherlich verbes', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6795', 'deDE', 'Bitte, bitte, sagt, dass Ihr hier seid, um mich von diesen Ketten zu befreien. Irgendwo hier muss ein Schlüssel sein...$B$B Tötet doch wenigstens die Wache Slip\'kik... Ich helfe Euch auch gerne weiter', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6798', 'deDE', 'Auf den Friedhöfen im Alteractal finden die Geister der Gefallenen Ruhe und Frieden. Wenn Ihr sterbt, erscheint Euer Geist auf dem nächsten Friedhof, der von unserer Fraktion kontrolliert wird.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6812', 'deDE', 'Schaut auf dieses verwüstete Feld, und seid Euch der Kraft der Elemente bewusst, $R.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6842', 'deDE', 'Ich weiß viel, hört zu und lasst euch unterweisen, $R.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6843', 'deDE', 'In der Zeit vor der Zeit, welche die Ursupatoren später das erste Zeitalter der Entstehung genannt haben, herrschten mein Meister und seine Brüder über diese Welt. Sie waren es, die alten Götter, welc', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6844', 'deDE', 'Ihre körperliche Gestalt war geschwächt von den Äonen des Kampfes. Eingesperrt unter der Welt - eine Welt, die sie einst selbst erschaffen hatten. Die Verderbtheit der Titanenformer floss schließlich ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6848', 'deDE', 'Die Hauptmänner der Horde und der Allianz haben ihre Stellungen bezogen. Hauptmann Galvangar befehligt die Frostwölfe von der Eisblutgarnison aus und sein Gegenüber, Hauptmann Balinda Steinbruch, vers', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6853', 'deDE', 'Das Alteractal ist lange Jahre die Heimat des Frostwolfklans gewesen. Wir werden die Zwerge und ihre Allianz nicht passieren lassen! Wir werden kämpfen!$B$BUm Alterac zurückzuerobern, müssen wir ihren', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6861', 'deDE', 'Drek\'Thar ist der militärische und spirituelle Anführer des Frostwolfklans in Alterac. Wenn er sich in Alterac befindet, befehligt er seine Glaubensbrüder von der Burg Frostwolf aus.$B$BSollte er besi', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6867', 'deDE', 'Es war während der Trennung der Elemente, als der Feuerfürst Ragnaros versuchte, meinen Fürsten Donneraan, König der Luft, zu verschlingen.$B$B<Demitrian beugt seinen Kopf, sein Gesicht ist von Wut ge', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6868', 'deDE', 'Es sollten Ragnaros Leutnants Geddon und Garr sein, die den Verrat begehen würden. Donneraan ahnte nichts von alledem.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6869', 'deDE', 'Der Vormarsch des Feuerfürsten war Donneraan völlig entgangen. Bevor mein Fürst überhaupt reagieren konnte, streckte Sulfuras, legendärer Hammer von Ragnaros, meinen Herrn nieder. Donneraan ging völli', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6870', 'deDE', 'Was von Donneraans Essenz übrig blieb, wurde in einem Talisman der elementaren Bindung übertragen... Ragnaros zerbrach den Talisman anschließend in zwei makellose Hälften, die er seinen Leutnants über', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6875', 'deDE', 'Ihr habt einen Schlüssel gefunden? Ist es wirklich DER Schlüssel? Ich meine, der Schlüssel um meine Fesseln zu lösen? Nun $G mein Herr :meine Dame;, es gibt nur einen Weg das herauszufinden!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6876', 'deDE', 'Ihr jetzt Big Boss! Ihr König! Lang lebe $G König :Königin; $N, $G König :Königin; der Gordok!$B$BNun da Ihr de\' Boss, alle Gefolgsleute Euch Tribut zahlen werden! Ihr doch welche am Leben gelassen ha', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6877', 'deDE', 'Willst du ein Spiel spielen, Stinker?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6878', 'deDE', 'Ich halte den Schlüssel zu Ländern der Freiheit! Jagt mich und Ihr werdet\'s schon sehen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6879', 'deDE', 'Noch am Leben? Ihr Überrascht! Seht wie ich fliehe!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6880', 'deDE', 'Dreht diesen Finsteren Blick genau anders herum!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6881', 'deDE', 'Dies ging zu weit, mein Freund!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6882', 'deDE', 'Um, Ihr König getötet, jaa? Das heißt, Ihr einnehmen seinen Platz! Das machen Euch neuer Boss! Lang leben de\' König!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6883', 'deDE', 'Also nach dem, was ich hier mitbekommen habe, während ich darauf warten durfte, endlich von diesen Idioten gegessen zu werden... wird es möglicherweise nicht nötig sein gleich jeden Oger, der euch vor', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6894', 'deDE', 'Ihr de\' Boss jetzt, richtig? Lang leben de\' neue König!$B$BKreeg zeigt Euch Alkohol der Gordok. Ogeralkohol, bester von allen Alkohol und Alkohol der Gordok ist beste von allen Ogeralkohol! Vielleicht', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6895', 'deDE', 'OK Boss, wenn du bereit für de\' Tribut deiner Gefolgsleute, du mir sagen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6896', 'deDE', '', 'Verlasst diesen Ort, Fremder, damit er euch nicht auch aufzehrt wie alles andere, was von ihm berührt wurde.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6897', 'deDE', '', 'Wisst Ihr etwas über die Hochgeborenen? Die Shen\'dralar?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6898', 'deDE', '', 'Die Shen\'dralar waren Königin Azsharas meist verehrte Arkanisten. Es war in genau diesen Mauern, in denen die Befehle der Königin ausgeführt wurden - meistens im Verborgenen.$B$BNach der Zerstörung de', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6899', 'deDE', '', 'Der Prinz plante einen Dämonen von großer Stärke hinter einem starken Kraftfeld gefangenzuhalten. Die Pylone würden die latenten Energien in das Feld leiten und es so aufrecht erhalten.$B$BWährend sei', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6900', 'deDE', '', 'Die gesammelten magischen Energien würden die Magie der Hochgeborenen am Leben erhalten und es denen innerhalb der Mauern von Eldre\'Thalas ermöglichen, ihre endlosen magischen Schmerzen zu speisen.$B$', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6901', 'deDE', '', 'Die Geister der Hochgeborenen die Ihr durch den Hof und die Hallen von Eldre\'Thalas wandern seht, wurden nicht etwa von einfallenden Ogern der Gordok oder Satyrn getötet, sondern vom Prinzen selbst!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6902', 'deDE', '', 'Das Volk wurde vernichtet, um das ewige Leben des Prinzen und seine beinahe grenzenlosen Kräfte zu erhalten. Die Kraft, welche sich einst über tausende von Seelen verteilte, fließt nun in den Prinzen ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6903', 'deDE', 'Ihr seid jetzt derjenige welcher, Hund!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6905', 'deDE', 'Boss, du mich gefunden! Wie du hast Slip\'kik gefunden?? Ich schlau und nächstes Mal besser aufpassen! Har har!$B$BNiemand wird Slip\'kik mehr reinlegen. Slip\'kik versprechen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6907', 'deDE', 'Du bist der König von den Gordok! Du bist der Größte!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6909', 'deDE', 'Seit 10.000 Jahren herrsche ich schon über dieses Königreich. In dieser Zeit, habe ich viele faszinierende Geschichten von jenen gehört, die durch die geheiligten Hallen des Hauses von Shen\'dralar wan', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6911', 'deDE', 'Wir dürfen den Meister nicht wütend machen. Sein Zorn geht weit über die Grenzen der Brutalität hinaus.', 'Wir dürfen den Meister nicht wütend machen. Sein Zorn geht weit über die Grenzen der Brutalität hinaus.', 'Senkt Eure Stimme, Fremder. Ihr solltet den Meister besser nicht in seiner Meditation stören.', 'Senkt Eure Stimme, Fremder. Ihr solltet den Meister besser nicht in seiner Meditation stören.', 'Viele sind zu seinem Heiligtum gekommen, nur wenige haben es auch wieder verlassen.', 'Viele sind zu seinem Heiligtum gekommen, nur wenige haben es auch wieder verlassen.', 'Der Hüter des Wissens ist einer der Uralten. Er diente sogar schon unter der glorreichen Königin!', 'Der Hüter des Wissens ist einer der Uralten. Er diente sogar schon unter der glorreichen Königin!', 'Hier verschwinden schon lange Bücher. Der Hüter des Wissens und seine Assistenten sind schon halb wahnsinnig. Sie vermuten ein falsches Spiel in Eldre\'Thalas.', 'Hier verschwinden schon lange Bücher. Der Hüter des Wissens und seine Assistenten sind schon halb wahnsinnig. Sie vermuten ein falsches Spiel in Eldre\'Thalas.', 'Hat der Prinz Euch die Geschichte von Aschenbringer vorgetragen? Von all seinen Geschichten lieben wir diese am meisten. Sein Lieblingswerk ist der Nachfolger zu Aschenbringer, natürlich hat es bisher', 'Hat der Prinz Euch die Geschichte von Aschenbringer vorgetragen? Von all seinen Geschichten lieben wir diese am meisten. Sein Lieblingswerk ist der Nachfolger zu Aschenbringer, natürlich hat es bisher', 'Ich vermute, dass die Dämonen Wissen stehlen.', 'Ich vermute, dass die Dämonen Wissen stehlen.', 'Der Meister hat ein großes Interesse am Hort von Nefarian. Wenn doch nur jemand den Drachen und seine Gefolgschaft besiegen würde und diese Geschichte dem Prinzen erzählen könnte. Es würde die Zunge d', 'Der Meister hat ein großes Interesse am Hort von Nefarian. Wenn doch nur jemand den Drachen und seine Gefolgschaft besiegen würde und diese Geschichte dem Prinzen erzählen könnte. Es würde die Zunge d');
INSERT INTO `npc_text_localized` VALUES ('6913', 'deDE', 'Warum Ihr hier herumlaufen? Wieso kleines Essen nicht in Ketten?!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6914', 'deDE', 'Der Starke Ihr sein! Lang leben de\' neue König de\' Gordok!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6915', 'deDE', 'Sie immer noch haben ihre Waffen? Warum haben ihr gelassen ihnen die Waffen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6916', 'deDE', 'Alle Gefolgsleute von de\' Boss - Stampfer Kreeg, alle drei von de\' Wache, Hauptmann Krombruch und der Beobachter - gebt Tribut an de\' neue Boss. Wenn Ihr Gefolgsmann tötet, dann er nicht geben können ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6917', 'deDE', '', 'Oh, so ein Horror! Eine Tragödie! Wir stehen so kurz vor einem Durchbruch und doch könnte bald alles verloren sein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6918', 'deDE', 'Oh... mir leid tun das ich Euch angegriffen habe! Mich nicht töten, bitte? Ihr jetzt König, Ich werde Euch helfen de\' Tribut bessa zu machen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6919', 'deDE', 'Oberflächlich betrachtet sieht das Skelett aus, als wäre das Fleisch völlig versengt. Ein schrecklicher Anblick.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6920', 'deDE', 'Vielleicht Ihr haben mehr Erfolg das Gordokrelikt von schrecklichen Hochgeborenen Prinzen zurückzubekommen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6922', 'deDE', 'Hoch der neue König!', '', 'Als ich Euch vorhin so angestarrt habe bevor Ihr de\' neue König wurdet... um... hab ich nur Spaß gemacht.$B$BEhrlich!', '', 'De\' anderen Oger warum dumm Euch anzugreifen, Boss! Das\'is warum ich noch leben - Ich bin schlau und so...', '', 'Wir viel mehr sauber als die Oger in Tanaris. In der ganzen Wüste, soweit Auge gucken kann, überall Ogerkacka. Aber hier nicht, nee $G Herr : Frau;!', '', 'Solange Ihr hier bei uns bleiben, Ihr de\' König. Ihr gehen und ein anderer Oger Euren Platz einnehmen wird. Das ist de\' Weg der Gordok!', '', 'Ich bin so schlau! Superschlau!', '', 'Alter König tot? Kann nich\' sein!', '', 'Ich Euch keinen Ärger machen, Boss.', '');
INSERT INTO `npc_text_localized` VALUES ('6923', 'deDE', 'Wenn Ihr nicht sprechen zu Mizzle, Ihr nicht werden König! Sprecht mit Mizzle dem Gewieften!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6924', 'deDE', 'So... Ihr habt also König besiegt? Ich wusste das diese Dummköpfe Euch zu ihrem neuen König machen, ich wusste es einfach! Das ist alles was diese Schwachköpfe verstehen - Gewalt.$B$B Es scheint so, d', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6925', 'deDE', 'Ihr müsst verstehen, dass eine 10.000-jährige Existenz mir eine einzigartige Perspektive auf das Leben ermöglicht. Eure alltäglichen Probleme und Schwierigkeiten sind bedeutungslos.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6926', 'deDE', 'Oh all das Drama und Aufheben das der junge Meister betrieben hatte. Man konnte meinen, dass das Schicksal der Welt auf seinen Schultern lastete. Nur sterbliche können einen so verzerrten Blick auf di', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6927', 'deDE', 'Sie sind gestohlen worden, $R! Drei unserer wertvollsten Bände. Wenn Ihr die Folianten zurückbringt wird Euch mit Sicherheit eine Belohnung erwarten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6929', 'deDE', '<Ihr könnt das technische Kauderwelsch auf dieser Rolle nicht entziffern.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6930', 'deDE', '<Auf diesem Pergament ist die Blaupause für einen Feldreparaturroboter gezeichnet.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6932', 'deDE', '<Ihr seid sehr bewandert im Herstellen und Bedienen eines solchen Roboters.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6933', 'deDE', 'Die Fasern, aus denen unsere Welt gewebt ist, sind sehr empfindlich. Es braucht nur etwas Wissen und die Anwendung dieses Wissens, um die Fasern zu zerstören. Auf diese Art können Risse im Gewebe herv', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6934', 'deDE', 'Blickt auf den Gegner, $R. Seht wie die Macht der Horde selbst den mächtigsten Drachen in den Schatten stellt! Alle die sich uns entgegenstellen soll das gleiche Schicksal ereilen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6935', 'deDE', 'Ich bin der, welcher die Befehle des Kriegshäuptlings ohne Fragen ausführt. Ich bin die Horde.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6936', 'deDE', 'Ich lebe nur, um unserem König zu dienen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6937', 'deDE', 'Eines Tages vielleicht wird euer Mitwirken in der Allianz genauso groß sein wie das der Helden, die Ihr heute gesehen habt. Ehrt sie, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6939', 'deDE', '10.000 Jahre aufgezeichnetes Wissen. Da kommt es schon mal dazu, dass die falschen Elemente vom Athenaeum angezogen werden. Bücher werden ständig gestohlen, wiedergefunden und wieder gestohlen und das', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6940', 'deDE', '', 'Kildrath tendiert dazu die Situation überzudramatisieren. Nehmt zum Beispiel \"Garona: Eine Studie über Heimlichkeit und Verrat\", das wurde schon über 400 mal gestohlen.$B$BDas Siegel des Athenaeums be', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6947', 'deDE', 'Ich habe keine Zeit für Störungen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6948', 'deDE', 'Kann ich Euch helfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6949', 'deDE', 'Gewiss, aber ich kenne den tatsächlichen Namen - Eldre\'Thalas. Bis vor vielen, vielen Jahren war es für die Nachtelfen eine Bastion magischer Kraft.$B$BWisst Ihr was mit Eldre\'Thalas passiert ist? Man', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6950', 'deDE', 'Agenten vom Zirkel des Cenarius haben uns einige beunruhigende Dinge über die momentane Situation in Eldre\'Thalas berichtet. Ich bin nun in einer ausweglosen Situation gefangen und werde etwas unterne', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6951', 'deDE', 'In den vergangenen Jahren war Eldre\'Thalas eine Verknüpfung magischer Kraft, und die, die sich in diesen heiligen Hallen aufhielten, verfügten über immense Macht. Einer dieser Nachtelfen war Millicent', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6952', 'deDE', 'Ach, es sah immer so aus, als wäre das der Fall. Ich wie auch die übrigen Mitglieder des Zirkels des Cenarius glaubten, dass Millis Schöpfung während des Kriegs der Ahnen zerstört worden wäre. Dies is', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6953', 'deDE', 'Ich... will es mir gar nicht vorstellen, $N. Ich weiß nur, dass ich das nicht zulassen darf. Sie müsste zerstört werden, nicht nur zu unserem Schutz vor dämonischer Verderbnis, sondern auch wegen des ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6957', 'deDE', 'Ich grüße Euch, Held! Ich habe Neuigkeiten von beiden Kontinenten und weiterher, die Ihr berücksichtigen solltet.', 'Ich grüße Euch, Held! Ich habe Neuigkeiten von beiden Kontinenten und weiterher, die Ihr berücksichtigen solltet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6960', 'deDE', 'Ihr müsst Nat Pagle in den Düstermarschen finden, um Euch überhaupt verbessern zu können.', 'Ihr müsst Nat Pagle in den Düstermarschen finden, um Euch überhaupt verbessern zu können.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6961', 'deDE', 'Ihr könnt bei mir Eure Angelfertigkeit verbessern.', 'Ihr könnt bei mir Eure Angelfertigkeit verbessern.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6974', 'deDE', 'Die Blaukiemenmarschen, nördlich des Hafens von Menethil im Sumpfland gelegen, bergen noch viele verschlossene Kisten von Wracks, die an die Küste geschwemmt wurden.', 'Die Blaukiemenmarschen, nördlich des Hafens von Menethil im Sumpfland gelegen, bergen noch viele verschlossene Kisten von Wracks, die an die Küste geschwemmt wurden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6975', 'deDE', 'Am Sar\'therisstrand in Desolace oder um die Festung Angor im Ödland herum, gibt es verschlossene Kisten, die geöffnet werden können.', 'Am Sar\'therisstrand in Desolace oder um die Festung Angor im Ödland herum, gibt es verschlossene Kisten, die geöffnet werden können.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6976', 'deDE', 'Der Tränenteich in den Sümpfen des Elends birgt uralte verschlossene Kisten, die in einem anderen Zeitalter verloren gingen.', 'Der Tränenteich in den Sümpfen des Elends birgt uralte verschlossene Kisten, die in einem anderen Zeitalter verloren gingen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6977', 'deDE', 'Die Dunkeleisenzwerge der Schlackengrube in der sengenden Schlucht, verfügen über eine große Anzahl von verschlossenen Kisten.', 'Die Dunkeleisenzwerge der Schlackengrube in der sengenden Schlucht, verfügen über eine große Anzahl von verschlossenen Kisten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6978', 'deDE', 'In der Mast- und Schotbucht in Tanaris und der Bucht der Stürme in Azshara befinden sich Kisten, die zum Verbessern Eurer Fertigkeiten geknackt werden können.', 'In der Mast- und Schotbucht in Tanaris und der Bucht der Stürme in Azshara befinden sich Kisten, die zum Verbessern Eurer Fertigkeiten geknackt werden können.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6979', 'deDE', 'Der Scharlachrote Kreuzzug von Tyrs Hand in den östlichen Pestländern verfügt über zahlreiche Schließkassetten, die Eure Fertigkeit herausfordern werden.', 'Der Scharlachrote Kreuzzug von Tyrs Hand in den östlichen Pestländern verfügt über zahlreiche Schließkassetten, die Eure Fertigkeit herausfordern werden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6980', 'deDE', 'Die Orcs haben viele verschlossene Vorratskisten im Immerruhsee im Rotkammgebirge versenkt.', 'Die Orcs haben viele verschlossene Vorratskisten im Immerruhsee im Rotkammgebirge versenkt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6982', 'deDE', 'Die Venture Company, die ihren Sitz um die Scherwindklippe im Steinkrallengebirge hat, bewahrt ihre Vorräte in verschlossenen Kisten auf.', 'Die Venture Company, die ihren Sitz um die Scherwindklippe im Steinkrallengebirge hat, bewahrt ihre Vorräte in verschlossenen Kisten auf.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6983', 'deDE', 'Das Syndikat bewahrt viele verschlossene Vorratskisten in der Burg Durnholde im Hügelland auf.', 'Das Syndikat bewahrt viele verschlossene Vorratskisten in der Burg Durnholde im Hügelland auf.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6985', 'deDE', 'Ich studiere die bekannten und unbekannten Elemente unserer Welt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6990', 'deDE', 'Es kommt die Zeit, in der unser Leben sich nicht mehr mit unseren Vorstellungen deckt. Wir kommen nicht weiter, suchen nach Antworten und finden Hoffnung, die an dunklen Plätzen in Flammen aufgeht.$B$', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6991', 'deDE', 'Habt Ihr irgendwelche Spinnen oder Käfer, $C? Sie huschen sehr flink herum, aber wenn Ihr schneller seid, werdet ihr sehen, sie sind wirklich ein Leckerbissen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6992', 'deDE', 'Kommt Ihr gerade aus dem Dungeon? Habt Ihr etwas für mich?', '', 'Wenn ich groß bin, möchte ich der König von Sturmwind sein!', '', 'Ich war schon einmal in Eisenschmiede. Es riecht komisch dort.', '', 'Pilze wachsen immer an feuchten Stellen, deshalb sehen sie wie Regenschirme aus.', '', 'Wenn Overalls durch die oberen Knöpfe zusammengehalten werden, warum haben sie dann Gürtelschlaufen?', '', 'Was passiert, wenn Euer Rotz in der Nase gefriert?', '', 'Haben Fische jemals Durst?', '', 'Wenn Ihr versucht zu versagen und dabei erfolgreich seid, was habt Ihr dann getan?', '');
INSERT INTO `npc_text_localized` VALUES ('6993', 'deDE', 'Eure Rüstung sieht beängstigend aus. Seid Ihr ebenso furchterregend?', '', 'Falls Ihr wirklich ein Held der Horde seid, will ich sehen wie Ihr einen Drachen bekämpft! Yeah!', '', 'Wenn ein Blitz ins Meer einschlägt, warum sterben dann nicht alle Fische?', '', 'Wäre ein Flug ohne Flügel ein Spaziergang?', '', 'Ich habe einen Felsen in meiner Nase.', '', 'Ist eine Schildkröte ohne Panzer obdachlos oder nackt?', '', 'Was bedeutet \"zug-zug\"?', '', 'Passt ein Handschuh noch immer wie angegossen, wenn er zu groß ist?', '');
INSERT INTO `npc_text_localized` VALUES ('6995', 'deDE', 'Ihr seid offensichtlich weit weg von zu Hause, $R.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6996', 'deDE', 'So, Ihr habt mich also gefunden. Ich vermute, dass Ihr gekommen seid, um etwas über Unterdrückung und Kontrolle zu lernen.$B$BIch werde Euch weitaus mehr als das beibringen... Ihr werdet auch etwas üb', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6997', 'deDE', 'Das wird die Wahl des Dämonen sein, den Ihr beschwören und unterjochen wollt. Wir beherrschen die dunkle Macht, für die wir einen hohen Preis bezahlen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6998', 'deDE', 'Wir müssen erst ein Gefängnis für das Biest anfertigen, wenn Ihr weiter vorankommen möchtet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7001', 'deDE', 'Verneigt Euch vor meiner Macht, Insekten! Sicher fühlt Ihr meine Macht über Euch.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7002', 'deDE', 'Ihr seid also zu Niby gekommen, um von ihm zu lernen, wie man eine Höllenbestie als Diener aus dem Nether herbeiruft.$B$BSchweigt! Niby weiß alles!$B$BUnd so schnell teilt er sein Wissen nicht mit Fre', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7003', 'deDE', 'Nordöstlich von diesem Lager gibt es eine sehr mächtige Höllenbestie. Nur deswegen ist Niby hier. Kroshius ist eine große Höllenbestie, die weit länger auf dieser Welt existierte als viele seiner Artg', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7004', 'deDE', 'Narr, unterbrecht Niby nicht! Ich werde Euch das Fleisch von den Knochen reißen und Euer Skelett als Kleiderständer benutzen! Nun, wo war ich doch gleich? Ah ja, also hatte Kroshius zehnmal länger Bes', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7005', 'deDE', 'Wenn ich Euch die Beschwörung eines Höllenbestiendieners beibringen soll, müsst Ihr Kroshius zerstören und mir den Höllenbestienkern aus seinen brennenden Überresten bringen. Allerdings ist das nicht ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7010', 'deDE', '', 'Der Krieg hat seinen Preis, $C... das wird gerne übersehen. Es handelt sich dabei um die Waisenkinder - Kinder, die ihre Eltern in den bei weitem zu zahlreichen Konflikten Azeroths verloren haben. Die', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7011', 'deDE', '', 'Der Krieg hat seinen Preis, $C... das wird gerne übersehen. Es handelt sich dabei um die Waisenkinder - Kinder, die ihre Eltern in den bei weitem zu zahlreichen Konflikten Azeroths verloren haben. Die', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7012', 'deDE', '', 'Warum... das ist wirklich reizend von Euch. Ich schließe daraus, dass Ihr ehrenamtlich Zeit für die Kinderwoche aufwendet, $C? Nun, das ist sehr lobenswert. Es gilt an die weniger Glücklichen zu denke', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7013', 'deDE', 'Seid gegrüßt und willkommen in Donnerfels, mein Kind. Ihr werdet hier sichere Zuflucht vor den Leiden dieser Welt finden. Bei uns sind alle Anhänger der Horde gerngesehen.$B$BDie Geister der Vergangen', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7014', 'deDE', 'Ah, ohne Zweifel $G ein junger Krieger:eine junge Kriegerin;! Ihr nehmt an der Kinderwoche teil, ja? Es erwärmt mein altes Herz, wenn ich sehe, dass Ihr als Beispiel vorangeht, dem andere mit Sicherhe', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7015', 'deDE', 'Kochen ist etwas, das man sein Leben lang verfolgt. Ich sehe, wir teilen die gleiche Leidenschaft!', 'Kochen ist etwas, das man sein Leben lang verfolgt. Ich sehe, wir teilen die gleiche Leidenschaft!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7016', 'deDE', 'Sucht nach Shandrina in der Silberwindzuflucht. Sie kann Euch das \"Expertenkochbuch\" verkaufen.', 'Sucht nach Shandrina in der Silberwindzuflucht. Sie kann Euch das \"Expertenkochbuch\" verkaufen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7017', 'deDE', 'Dirge Schnetzelhack von Gadgetzan ist ein Meister der Kochkunst. Um Euch verbessern zu können, werdet Ihr ihn ausfindig machen müssen.', 'Dirge Schnetzelhack von Gadgetzan ist ein Meister der Kochkunst. Um Euch verbessern zu können, werdet Ihr ihn ausfindig machen müssen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7019', 'deDE', 'Wulan in Schattenflucht kann Euch das \"Expertenkochbuch\" verkaufen. Ihr werdet es brauchen, wenn Ihr Euch verbessern wollt.', 'Wulan in Schattenflucht kann Euch das \"Expertenkochbuch\" verkaufen. Ihr werdet es brauchen, wenn Ihr Euch verbessern wollt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7021', 'deDE', 'Ich kann Euch das Kochen beibringen!', 'Ich kann Euch das Kochen beibringen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7024', 'deDE', 'Einzig der berühmte Doktor Gregory Victor kann Euch jetzt noch helfen, Eure Fertigkeiten zu verbessern. Ihr findet Ihn im Arathihochland, in Hammerfall.', 'Einzig der berühmte Doktor Gregory Victor kann Euch jetzt noch helfen, Eure Fertigkeiten zu verbessern. Ihr findet Ihn im Arathihochland, in Hammerfall.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7025', 'deDE', 'Balai Lok\'Wein aus Brackenwall in den Düstermarschen verkauft ein Buch, das Ihr studieren solltet, um Eure Fähigkeiten zu verbessern!', 'Balai Lok\'Wein aus Brackenwall in den Düstermarschen verkauft ein Buch, das Ihr studieren solltet, um Eure Fähigkeiten zu verbessern!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7026', 'deDE', 'Der angesehene Doktor Gustav van Howzen kann Euch weiter ausbilden. Ihr findet ihn in Theramore, er versorgt die Verwundeten in der dort ansässigen Kaserne.', 'Der angesehene Doktor Gustav van Howzen kann Euch weiter ausbilden. Ihr findet ihn in Theramore, er versorgt die Verwundeten in der ansässigen Kaserne.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7027', 'deDE', 'Ich habe gehört, dass Deneb Wandler sich momentan in Stromgarde aufhält. Er ist im Besitz eines Buchs, das Euch helfen wird, Eure Techniken der Ersten Hilfe zu verbessern.', 'Ich habe gehört, dass Deneb Wandler sich momentan in Stromgarde aufhält. Er ist im Besitz eines Buchs, das Euch helfen wird, Eure Techniken der Ersten Hilfe zu verbessern.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7028', 'deDE', 'Ich kann Euch in Techniken der Ersten Hilfe unterweisen.', 'Ich kann Euch in Techniken der Ersten Hilfe unterweisen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7036', 'deDE', 'Der Geist des Schreckensrosses pulsiert vor Wut.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7037', 'deDE', 'Die Wege von Jaedenar sind dunkel und verschlagen. Wacht über Eure Schritte, $C, oder Eure Seele wird von Euren Feinden verzaubert werden...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7038', 'deDE', 'Verschwindet! Ich berate mich mit den Schreckenslords und unsere Angelegenheiten gehen Euch nichts an!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7039', 'deDE', '...Eine Nachricht von Schattenfluch? Was für eine Nachricht ist das? Braucht er die Hilfe seines großen Beraters und Dieners Ulathek? Fordert er mein Erscheinen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7040', 'deDE', 'Was? Aber wie? Nein! Das ist nicht möglich! Ich war so vorsichtig... er konnte es nicht wissen!$B$BIhr lügt! Ihr lügt und Ihr werdet für diese Täuschung sterben!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7041', 'deDE', '', 'Ein herrlicher Tag, um am Leben zu sein! Seht Euch nur diese prächtige Schönheit um Euch herum an, $G mein Freund:meine Freundin;. Genießt, was das Leben Euch bietet!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7042', 'deDE', '*GRRRR* *RUFF* *RUFF*', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7043', 'deDE', 'Lava? Verschwendet doch nicht Eure Gedanken an solche Dinge, mein Freund. Genießt einfach die Freuden der Natur.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7044', 'deDE', 'Ich komme jedes Jahr um diese Zeit nach Silithus. Dieses herrliche Wetter ist einfach phänomenal für meine Gesundheit.$B$BKomm, geht ein wenig mit mir spazieren, $G mein Freund:meine Freundin;. Genieß', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7045', 'deDE', 'Es ist schön jemanden in dieser weit abgelegenen Region der Welt zu treffen. Kommt, gehen wir ein wenig spazieren. Unsere Körper sollen sich gegenseitig Wärme schenken.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7046', 'deDE', 'Sucht hoch oben auf der Westseite des Tals der Stärke.', 'Sucht hoch oben auf der Westseite des Tals der Stärke.', 'Ihr sucht die Halle der Legenden. Sie befindet sich hoch oben im Tal der Stärke, auf der westlichen Seite.', 'Ihr sucht die Halle der Legenden. Sie befindet sich hoch oben im Tal der Stärke, auf der westlichen Seite.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7047', 'deDE', 'Auf einmal seid Ihr ein \"Verteidiger der Allianz\"?! Stimmt doch, oder? Naja, ich nehme also an, dass Ihr auf dem Weg zur Halle der Champions seid. Sie befindet sich drüben in der Altstadt.', 'Auf einmal seid Ihr ein \"Verteidiger der Allianz\"?! Stimmt doch, oder? Naja, ich nehme also an, dass Ihr auf dem Weg zur Halle der Champions seid. Sie befindet sich drüben in der Altstadt.', 'Wenn Ihr die Halle der Champions sucht, müsst Ihr Euch in das südöstliche Ende der Altstadt begeben. Viel Glück.', 'Wenn Ihr die Halle der Champions sucht, müsst Ihr Euch in das südöstliche Ende der Altstadt begeben. Viel Glück.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7055', 'deDE', 'Wenn Ihr auf der Suche nach dem besten handwerklichen Können auf diesem oder jeglichem anderen Kontinent der Welt seid, dann seid Ihr hier wahrlich richtig!$B$BGrimand Elmore, zu Euren Diensten!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7056', 'deDE', 'Um ein Schreckensross herbeizuzaubern, müsst Ihr zunächst das Ritual des mächtigen Beschwörungskreises wirken. Ein Portal nach Xoroth kann nur durch solch einen Kreis herbeigerufen werden. $B$BFür ein', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7074', 'deDE', 'Arr! Ich würde mir nichts lieber ansehen, als ein paar Leute, die sich in der Arena zu Tode bekämpfen. Wenn Ihr zur rechten Zeit hier seid, dann könnt Ihr vielleicht zu Letzteren zählen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7094', 'deDE', 'Ja, es is\' wahr, ich bin blind wie ein Maulwurf! Un\' betrunken wie... äh, betrunken wie was, was betrunken wird.$B$BIch sag\' Euch was, $R. Es is\' der Drink, der meine Sinne scharf hält. Es is\' allgeme', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7095', 'deDE', '', 'Wir haben uns entschlossen den Schutz und die Bequemlichkeit der Tiefen zu verlassen, um die Welt auszubeuten... äh, ich meine... hm, ja genau, AUSZUKUNDSCHAFTEN.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7114', 'deDE', 'Vielleicht war das doch nicht so ein guter Standpunkt für ein Geschäft...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7115', 'deDE', 'Burninate heiß\' ich, Schmieden weiß ich... und, ähm... ich verkaufe auch etwas Verpflegung und Wasser nebenher. Etwas Fleisch vielleicht?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7116', 'deDE', 'Hallo nochmal, $N! Ich weiß nicht wie lange ich noch versuchen werde ein Geschäft mitten im Nirgendwo aufzumachen. Trotzdem, da Ihr mir bereits in der Vergangenheit behilflich gewesen seid, werde ich ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7117', 'deDE', 'Wenn Lolo nichts von seinem geschätzten Ale verschütten oder seine Gewänder schmutzig machen möchte, bin ich gefragt. Yep. Der alte Hansel ist der Zwerg, der gebraucht wird, wenn es darum geht etwas G', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7120', 'deDE', 'Als agierender Zuchtmeister dieses Lagers ist es meine Pflicht dafür zu sorgen, dass ein gewisses Maß an Routine mit allen Arten von Geschäften, Arbeiten, Pausen und Erholungen einhergeht.$B$BErstatte', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7121', 'deDE', 'Ihr habt alles getan, um was ich Euch gebeten hatte, $N. Hier gibt es wirklich nichts mehr für Euch zu tun. Wenn es Euch interessiert, Eure Karriere als Agent der Thoriumbruderschaft noch weiter zu ve', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7122', 'deDE', '', 'Thoriumbruderschaft oder nicht, sollte sich mir einer der Dunkeleisenzwerge nähern, so wird sein Kopf meine Hacke zu spüren bekommen! Nun, wohin soll es gehen $G Junge : Mädel;?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7123', 'deDE', '', 'Solange diese Zwerge bleiben, wo sie sind, werden wir keinen Ärger bekommen. Wohin geht Ihr, $N?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7134', 'deDE', 'Glaubtet Ihr etwa ich würde vor der mächtigen Legion $Ns niederknien?$B$BIhr bedeutet mir nichts. Weniger als Nichts. Hätte ich Euren Kopf gewollt, ich hätte ihn aufgespießt auf einen Speer bekommen.$', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7154', 'deDE', 'Ein großer humanoider Handabdruck zeichnet sich auf der Oberfläche der Kugel ab. Auch konstante Handhabung und Berührung scheinen der Kugel nichts auszumachen, sie bleibt völlig unbeschädigt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7155', 'deDE', 'Deine Hände passen genau in die Handabdrücke auf der Kugel.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7174', 'deDE', 'Ich bin der, der alle Verräter beobachtet. Ich bin die Faust der Vergeltung, die die Widerspenstigen bezwingt. Wagt Ihr es, Euch dem Kriegshäuptling zu widersetzen? Wagt Ihr es, Euch meinem gnadenlose', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7175', 'deDE', 'Könnt Ihr nicht sehen, dass ich hier angle!', '', 'Ihr habt den Fisch vertrieben! Was wollt Ihr?', '', 'Was kann ich für Euch tun, $C?', '', 'Ich werde so lange hier bleiben, bis ich einen 68-pfündigen Zackenbarsch gefangen habe! Ihr habt scheinbar etwas anderes im Sinn, stimmt\'s?', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7176', 'deDE', 'Sieh dir diesen gefallenen Drachen an, $R. Die Macht der Horde ist unbestreitbar!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7177', 'deDE', 'Der Flammenschlund besteht aus einem Netzwerk an vulkanischen Höhlen, der sich unterhalb der Kluft der Schatten, in Orgrimmar befindet. Seit neuestem kursieren Gerüchte, das ein Kult, der dem dämonisc', 'Der Flammenschlund besteht aus einem Netzwerk an vulkanischen Höhlen, der sich unterhalb der Kluft der Schatten, in Orgrimmar befindet. Seit neuestem kursieren Gerüchte, das ein Kult, der dem demonisc', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7179', 'deDE', 'Geht weiter Mitbürger, es gibt hier nichts zu sehen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7180', 'deDE', 'Würdet Ihr gerne einen dieser Dungeons erkunden?', 'Würdet Ihr gerne einen dieser Dungeons erkunden?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7181', 'deDE', 'Ein Stapel Papiere und Baupläne liegen hier verstreut.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
