INSERT INTO `npc_text_localized` VALUES ('7649', 'deDE', 'Die Menschen brauchen Eure Unterstützung im Kampf gegen die Verlassenen im Arathibecken. Geht zur Halle der Waffen und sprecht mit ihrem Beauftragten, Donal Osgood.', 'Die Menschen brauchen Eure Unterstützung im Kampf gegen die Verlassenen im Arathibecken. Geht zur Halle der Waffen und sprecht mit ihrem Beauftragten, Donal Osgood.', 'Der Bund von Arathor versucht seine alten Besitztümer im Arathibecken von den Verlassenen wieder zu beanspruchen. Wenn Ihr ihnen helfen wollt, $N, sprecht mit Donal Osgood in der Halle der Waffen.', 'Der Bund von Arathor versucht seine alten Besitztümer im Arathibecken von den Verlassenen wieder zu beanspruchen. Wenn Ihr ihnen helfen wollt, $N, sprecht mit Donal Osgood in der Halle der Waffen.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7650', 'deDE', 'Im Audienzsaal des Königs werdet Ihr Lady Hoteshem antreffen. Sie wird Euch helfen den Weg zum Arathibecken zu finden. Der Bund von Arathor benötigt dringend Eure Unterstützung gegen die Entweihten.', 'Im Audienzsaal des Königs werdet Ihr Lady Hoteshem antreffen. Sie wird Euch helfen den Weg zum Arathibecken zu finden. Der Bund von Arathor benötigt dringend Eure Unterstützung gegen die Entweihten.', 'Es ist beeindruckend, was der Bund von Arathor im Arathibecken versucht. Zu Schade, dass diese untoten Monstrositäten und ihre Hordenfreunde ihnen im Weg stehen. Lauft hinüber zur Burg Sturmwind. Lady', 'Es ist beeindruckend, was der Bund von Arathor im Arathibecken versucht. Zu Schade, dass diese untoten Monstrositäten und ihre Hordenfreunde ihnen im Weg stehen. Lauft hinüber zur Burg Sturmwind. Lady', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7651', 'deDE', '', 'Die Menschen brauchen definitiv all unsere Hilfe im Arathibecken. Der mit dem Ihr zu sprechen wünscht, nennt sich Keras Wolfheart. Er ist bereit Euch zu helfen, sucht ihn auf der Terasse der Krieger a', '', 'Wenn dies Euer Wille ist, $C, müsst Ihr mit Keras Wolfheart auf der Terrasse der Krieger sprechen. Er ist einer der Beauftragten des Bunds von Arathor. Er kann Euch helfen zum Arathibecken zu gelangen', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7652', 'deDE', 'Throm\'ka $C! Seid Ihr bereit, für die Horde in der Kriegshymnenschlucht zu kämpfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7653', 'deDE', '', 'Seid gegrüßt, $N. Wollt Ihr, dass ich Euch auf Eurem Weg zur Kriegshymnenschlucht helfe?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7654', 'deDE', '$N, die Schildwachen der Silberschwingen müssen zerstört werden! Seid Ihr bereit für die Kriegshymnenschlucht?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7655', 'deDE', 'Seid Ihr hier um für uns in der Kriegshymnenschlucht zu kämpfen? Lok\'tar Ogar $C!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7656', 'deDE', '', '$C, wir brauchen Eure Unterstützung in der Kriegshymnenschlucht. Werdet Ihr uns helfen, gegen die Vorhut des Kriegshymnenklans zu kämpfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7657', 'deDE', 'Genau, $G Junge : Mädchen;, seid Ihr bereit, ins Alteractal zu reisen und der Horde das zu geben, worum sie gebettelt hat?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7658', 'deDE', '$N, habt Ihr auch nur die geringste Ahnung, gegen wen wir im Alteractal bestehen müssen? Seid Ihr bereit den Frostwolfklan zu bekämpfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7659', 'deDE', 'Die Sturmlanzengarde muss zerschlagen werden! Bereit für das Alteractal, $C?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7660', 'deDE', '', 'Lok\'tar, $C! Wir brauchen Eure Unterstützung im Alteractal. Soll ich Euch der Liste hinzufügen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7661', 'deDE', 'Unsere Streitkräfte im Alteractal benötigen Weisheit, um den Sieg zu erlangen, $C. Ihr seht aus wie jemand, der sie führen könnte.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7662', 'deDE', 'Ich hoffe Ihr seid hier um in das Alteractal zu gehen, $N. Wir können alle Hilfe gebrauchen, die wir kriegen können.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7663', 'deDE', '', 'Die Menschen und ihr sogenannter Bund von Arathor gedenken, uns aus dem Arathibecken zu vertreiben. Doch das soll ihnen nicht gelingen, nicht wahr, $C? Seid Ihr bereit, dem Ruf zu folgen und den Mensc', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7664', 'deDE', 'Das Arathibecken war einst unser Zuhause und wird es mit Eurer Hilfe wieder sein! Werdet Ihr uns helfen, $C?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7665', 'deDE', 'Ich hoffe Ihr seid hier, um uns zu helfen, das Arathibecken von den Entweihten zurückzuerobern. Was sagt Ihr $N?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7666', 'deDE', '', 'Ich habe Euch erwartet, $N. Seid Ihr bereit uns im Arathibecken zu unterstützen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7667', 'deDE', 'Ihr glaubt also Ihr habt das Zeug um den Bund von Arathor zu zerstören, $C? Ich hoffe es. Was wollt Ihr genau von mir oder seid Ihr hier, um meine Zeit zu verschwenden?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7668', 'deDE', 'Seht Euch um, $C. Ihr befindet Euch zwar in außergewöhnlicher Gesellschaft, aber dennoch finde ich, dass Ihr Euch beweisen solltet. Hmm, vielleicht sollte ich es Euch möglich machen für unsere Sache i', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
