INSERT INTO `npc_text_localized` VALUES ('7670', 'deDE', 'Hazza\'rah, der Traumweber.$B$BSein ist die Macht über Alpträume, mögen seine Feinde für immer schlafen.$B$BHazza\'rah weilt nun weit entfernt von hier. Eines Tages wird er vielleicht zurückkehren...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
