INSERT INTO `npc_text_localized` VALUES ('7674', 'deDE', 'Wushoolay, der Sturmhexer.$B$BSeine Macht ist die Macht des Himmels, des Regens und der zersprungenen Erde. Möge er eines Tages wieder als der Vater der Gurubashi regieren.$B$BWushoolay weilt nun weit', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
