INSERT INTO `npc_text_localized` VALUES ('7677', 'deDE', 'Heya heya hey! Wenn Ihr Euer Angelgeschick testen möchtet, dann habe ich genau das Richtige für Euch! Diesen Sonntag gibt es einen Angelwettbewerb im Schlingendorntal!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7678', 'deDE', 'Ihr seid noch nicht erfahren genug, $C. Geht Eurer Wege.', 'Ihr seid noch nicht erfahren genug, $C. Geht Eurer Wege.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
