INSERT INTO `npc_text_localized` VALUES ('7683', 'deDE', 'Ihr müsst erfahrener sein, $C, bevor Ihr uns im Alteractal helfen könnt.', 'Ihr müsst erfahrener sein, $C, bevor Ihr uns im Alteractal helfen könnt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
