INSERT INTO `npc_text_localized` VALUES ('7699', 'deDE', 'Vielen Dank für das Angebot, $C. Allerdings seid Ihr noch nicht erfahren genug, um in den Gefahren des Arathibeckens bestehen zu können.', 'Vielen Dank für das Angebot, $C. Allerdings seid Ihr noch nicht erfahren genug, um in den Gefahren des Arathibeckens bestehen zu können.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
