INSERT INTO `npc_text_localized` VALUES ('7712', 'deDE', 'Seid gegrüßt, Meister der Angelrute und Spule! Wenn Ihr gewillt seid, Euer Geschick zu testen und gegen die besten Angler des Landes anzutreten, dann hört mir zu! An diesem Sonntag wird ein Wettbewerb', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
