INSERT INTO `npc_text_localized` VALUES ('7717', 'deDE', 'Hinter mir ist der Altar von Zanza, ein heiliger Ort für uns Zandalari. Wir beten hier, um unsere Entschlossenheit hinsichtlich der Kriegsvorbereitungen gegen den Blutgott Hakkar zu stärken.$B$BBeweis', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
