INSERT INTO `npc_text_localized` VALUES ('7801', 'deDE', '', 'Seid gegrüßt, Reisender. Ja, es war hier, vor fast 1000 Jahren, als die Qiraji ihren Marsch der Zerstörung, der sich über ganz Kalimdor erstrecken sollte, begannen.$B$BTausende verloren ihr Leben in d', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7802', 'deDE', '', 'Ich wurde schon hunderte Male getötet, doch wache ich immer wieder hier auf, lebendig. Einige behaupten, ich sei verflucht, andere jedoch...', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7803', 'deDE', '', 'Sie sagen ich sei gesegnet: Ein Herold, um eine neue Ära anzukündigen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7804', 'deDE', '', 'Uralte Mächte erwachen - Mächte die seit Tausenden von Jahren geschlafen haben. Die Welt wurde in Angst und Schrecken versetzt, $R. Eine neue Trennung ist zu befürchten, eine Seuche, die aus der Tiefe', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7805', 'deDE', '', 'Gruselige Schlotternächte! Mögt Ihr meine Maske? Ganz schön gruselig!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7806', 'deDE', '', 'Gruselige Schlotternächte! Wie findet Ihr meine Maske? Ganz schön gruselig!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7808', 'deDE', '', 'Ich bin traurig! Jetzt sind Schlotternächte und ich bin krank. Ich werde all die Süßigkeiten und das Streichespielen verpassen, weil ich nicht nach draußen kann!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7810', 'deDE', 'Ich bin traurig! Jetzt sind Schlotternächte und ich bin krank. Ich werde all die Süßigkeiten und das Streichespielen verpassen, weil ich nicht nach draußen kann!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7812', 'deDE', '', 'Was kann man NICHT machen, meint Ihr wohl!$B$BIn der ganzen Welt verteilen alle Gastwirte Schlotternachtssüßigkeiten und viele Ortschaften sind festlich geschmückt! Abends gibt es zum Beispiel die Fei', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
