INSERT INTO `npc_text_localized` VALUES ('8101', 'deDE', 'Seid gegrüßt, $C. Ich hoffe Ihr seid gekommen, um Euch über den Stand der Kriegsanstrengungen für den Krieg von Ahn\'Qiraj zu informieren. Wir werden eine Menge Hilfe benötigen.$B$BDie Kriegsanstrengun', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
