INSERT INTO `npc_text_localized` VALUES ('8108', 'deDE', 'Seid gegrüßt, $C. Ich bin Leoren, Stallmeister der Burg Cenarius.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
