INSERT INTO `npc_text_localized` VALUES ('8112', 'deDE', 'Wenn C\'Thun überlebt hätte, wäre unsere Freiheit bedeutungslos gewesen. Ihr seid wahrhaft $G der Held : die Heldin; der Prophezeiung.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8113', 'deDE', '', 'Jeder wird erfahren, was in diesem verfluchten Tempel vorgefallen ist, $G Held : Heldin;. Es wird in den Geschichtsbüchern niedergeschrieben werden. Die Geschichten werden zu Legenden werden und übera', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8114', 'deDE', 'Jetzt, wo der Krieg voll im Gange ist, fehlt es uns nicht an Nachschub von fähigen Kämpfern für die Allianz und Horde.$B$BWenn wir sie nur davon abhalten könnten, sich gegenseitig an die Kehle zu gehe', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
