INSERT INTO `npc_text_localized` VALUES ('8125', 'deDE', 'Neu dieses Jahr: der PX-238 Winterwundervolt. Tretet hervor, scheut Euch nicht!$B$BFüllt aber diese Formulare aus, bevor Ihr es versucht, wir beheben noch immer ein paar Macken an diesem Ding...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
